                      validation={{ required: true }}
                      value="" />
                  </div>
                </Col>
              </Row>
              <Row>
                <Col md="12">
                  <div className="mb-3">
                    <Label htmlFor="field32">{t("Special instructions Administration review")}</Label>                            {/**No Item */}
                    <AvField
                      className="form-control"
                      name="field32"
                      validation={{ required: true }}
                      type="textarea" rows={7}
                      id="field32" value="" />
                  </div>
                </Col>
              </Row>
            </Col>
          </Row >

        </LoadingOverlay >

        <CardFooter style={{ textAlign: "right" }}>

          <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { props.onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {t("Exit")}</Button>

          <Button color="negative" type="button" onClick={() => { props.onSaveProcess(3); }} style={{ margin: '5px' }}>
            <i className="mdi mdi-block-helper mdi-12px"></i>{" "}{t("Refuse")}
          </Button>

          <Button color="success" type="submit" style={{ margin: '5px' }}>
            <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {t("Advance")}
          </Button>
          {/* <Button color="success" type="button" onClick={() => { props.onSaveProcess(1); }} style={{ margin: '5px' }}>
              <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>{" "} {t("GenerateDocument")}
            </Button> */}
        </CardFooter>
      </AvForm>
    </React.Fragment >


  );

}

export default (withTranslation()(DatosGeneralesBL))
