import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation } from "react-router-dom"

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalRelevoGeneracional from "./ModalRelevoGeneracional"
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices } from "../../../../../services"

const RelevoGeneracional = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  const [dataAccionistasDataRows, setdataAccionistasDataRows] = useState([])
  const [dataReturn, setDataReturn] = useState(props.dataRelevoGenrencial);
  const [dataDelete, setDataDelete] = useState([]);
  const [jsonRelevo, setjsonRelevo] = useState(props.dataRelevoGenrencial);
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);

  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('formRelevoGeneracional');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }))
  React.useEffect(() => {
    // Read Api Service data 
    initializeData();
  }, []);
  function calcular(fecha) {
    var today = new Date();
    var yyyy = today.getFullYear();
    var today2 = new Date(fecha);
    var yyyy2 = today2.getFullYear();
    return (parseInt(yyyy)-parseInt(yyyy2));
  }
  function initializeData() {
    const api = new BackendServices()
    api.consultarDatosRelevoGeneracional(dataLocation.transactionId).then(resp => {
      if (resp.getManagementRelaysResponseDTOList.length) {
        dataReturn.dataTableRelevoGerencial = resp.getManagementRelaysResponseDTOList;
        setdataAccionistasDataRows(resp.getManagementRelaysResponseDTOList.map((data) => (
          data.status?
          <tr key={data.managementRIdentification}>
            <td>{data.name}</td>
            <td>{data.position}</td>
            <td>{data.birthDate}</td>
            <td>{calcular(data.birthDate)}</td>
            <td>{data.relationship}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { verRelevo(data) }} className="btn btn-link" ><i className="mdi mdi-eye mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { eliminarRelevo(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>:null)
        ))
      } else {
        setdataAccionistasDataRows(
          <tr key={1}>
            <td colSpan="5" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }
  function verRelevo(data) {
    setjsonRelevo(data)
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function eliminarRelevo(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      setFormValid(false);
      return;
    }
    setFormValid(true);
    props.onSubmit();
    //props.onSubmit(values);
  }

  function addRelevo(values) {
    let data = {
      transactId: dataLocation.transactionId,
      name: values.name,
      position: values.position,
      birthDate: values.birthDate,
      relationship: values.relationship
    }
    const apiBack = new BackendServices();
    apiBack.nuevoRelevoGeneracional(data).then(resp => {
      if(resp!=undefined){
        setsuccessSave_dlg(true)
        toggleShowModelAttachment();
      }else{
        toggleShowModelAttachment();
        seterror_dlg(true);
      }
    }).catch(err => {
      seterror_dlg(true);
    })
  }
  function changeAll(e) {
    dataReturn.observations = e.target.value;
    setDataReturn(dataReturn);
  }
  return (
    <React.Fragment>
      <h5>
        {t("ManagementRelays")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="formRelevoGeneracional" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Name")}</th>
                  <th>{t("Charge")}</th>
                  <th>{t("DBO")}</th>
                  <th>{t("Age")}</th>
                  <th>{t("Relation")}</th>
                  <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonRelevo({
                      transactId: 0,
                      name: null,
                      position: null,
                      birthDate: null,
                      relationship: null
                    }); toggleShowModelAttachment()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th>
                </tr>
              </thead>
              <tbody>
                {dataAccionistasDataRows}
              </tbody>
            </Table>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <div className="mb-3">
              <Label htmlFor="companyHistoryDescription">{t("Description")}</Label>
              <AvField
                className="form-control"
                name="companyHistoryDescription"
                type="textarea"
                rows="7"
                value={props.dataRelevoGenrencial.observations}
                id="companyHistoryDescription"
                onChange={(e) => { changeAll(e) }}
                validate={{
                  required: { value: true, errorMessage: t("Required Field") },
                }}
              />

            </div>
          </Col>
        </Row>
      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarRelevoGeneracional({ transactId: dataDelete.transactId, managementRIdentification: dataDelete.managementRIdentification }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalRelevoGeneracional jsonRelevo={jsonRelevo} botones={botonValidation} addRelevo={addRelevo} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );

});


RelevoGeneracional.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}


export default RelevoGeneracional;
