import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { CoreServices } from "../../../../../services";
import ApiServiceBackend from "../../../../../services/BackendServices/Services";
import Select from "react-select";

const ModalInfoAccionistas = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const [paises, setPaises] = useState([]);
  const [codigoPais, setCodigoPais] = useState(null);
  const [sumaParticipacion, setsumaParticipacion] = useState(0);
  const [sumaParticipacionMessage, setsumaParticipacionMessage] = useState(false);
  const [identificationType, setidentificationType] = useState("1");//1-Juridico, 2-Persona
  const [campoRequeridoTipo, setcampoRequeridoTipo] = useState(false);
  const [campoRequeridoNacionalidad, setcampoRequeridoNacionalidad] = useState(false);
  const [tipo, setTipo] = useState(null);
  const [nacionalidadSelect, setnacionalidadSelect] = useState(undefined);
  const [tipoPersona, settipoPersona] = useState(undefined);
  const [dataReturn, setDataReturn] = useState(props.jsonAccionistas);
  const [cambio, setcambio] = useState(1);
  const [identificationTypeList, setIdentificationTypeList] = useState([]);
  const [identificationTypeSelected, setIdentificationTypeSelected] = useState(undefined);
  const [personTypeSelect, setpersonTypeSelect] = useState(undefined);
  const [tipoIdentificacion, settipoIdentificacion] = useState(null);
  const [campoRequeridoIdentificacion, setcampoRequeridoIdentificacion] = useState(false);
  // Submitimos formulario para busqueda de clientes
  const personTypeData = [{ label: t("Legal"), value: "1" }, { label: t("Natural"), value: "2" }]
  function handleSubmit(event, errors, values) {
    if (tipo == null) {
      setcampoRequeridoTipo(true);
      return;
    } else {
      setcampoRequeridoTipo(false);
    }
    if (codigoPais == null) {
      setcampoRequeridoNacionalidad(true);
      return;
    } else {
      setcampoRequeridoNacionalidad(false);
    }
    if (tipoIdentificacion == null) {
      setcampoRequeridoIdentificacion(true);
      return;
    } else {
      setcampoRequeridoIdentificacion(false);
    }
    if (sumaParticipacionMessage) {
      return;
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.codigoPais = codigoPais;
    values.identificationType = tipoIdentificacion;
    values.personType = tipo.value;
    if (props.tipo == "guardar") {
      props.agregarAccionista(values, props.tipo);
    } else {
      values.shareholderId = props.dataAcciones.shareholderId;
      props.agregarAccionista(values, props.tipo);
    }
  }
  React.useEffect(() => {
    // Read Api Service data
    cargarPaises()
    if (props.tipo != "guardar") {
      setidentificationType(props.dataAcciones.personType)
    }
    if (props.dataGeneralIA != null) {
      setsumaParticipacion(0);
      if (props.tipo == "guardar") {
        let sumaTotal = 0;
        for (let i = 0; i < props.dataGeneralIA.length; i++) {
          sumaTotal = sumaTotal + props.dataGeneralIA[i].participation;
        }
        setsumaParticipacion(sumaTotal);
      } else {
        let sumaTotal = 0;
        for (let i = 0; i < props.dataGeneralIA.length; i++) {
          if (props.dataGeneralIA[i].shareholderId != props.dataAcciones.shareholderId) {
            sumaTotal = sumaTotal + props.dataGeneralIA[i].participation;
          }
        }
        setsumaParticipacion(sumaTotal);
      }
    }
  }, [props]);
  // React.useEffect(() => {
  //   // Read Api Service data
  //   var defaultVal = null;
  //   try {
  //     if (identificationTypeList.length > 0 && props.dataAcciones.identificationType !== null && identificationTypeSelected === undefined) {
  //       defaultVal = identificationTypeList.find(x => x.value === props.dataAcciones.identificationType);
  //       if (defaultVal !== undefined) {
  //         setIdentificationTypeSelected(defaultVal);
  //       }
  //     }
  //   }
  //   catch (err) { }
  // }, [props]);
  React.useEffect(() => {
    // Read Api Service data
    var defaultVal = null;
    setnacionalidadSelect(undefined)
    setIdentificationTypeSelected(undefined)
    setpersonTypeSelect(undefined)
    try {
      if (paises.length > 0 && props.dataAcciones.nationalityId !== null && nacionalidadSelect === undefined) {
        defaultVal = paises.find(x => x.value === props.dataAcciones.nationalityId);
        if (defaultVal !== undefined) {
          setCodigoPais(defaultVal)
          setnacionalidadSelect(defaultVal);
        }
      }
      if (personTypeData.length > 0 && props.dataAcciones.personType !== null && personTypeSelect === undefined) {
        defaultVal = personTypeData.find(x => x.value === props.dataAcciones.personType);
        if (defaultVal !== undefined) {

          setTipo(defaultVal.value)
          setpersonTypeSelect(defaultVal);
        }
      }
      if (identificationTypeList.length > 0 && props.dataAcciones.identificationType !== null && identificationTypeSelected === undefined) {
        defaultVal = identificationTypeList.find(x => x.value === props.dataAcciones.identificationType);
        if (defaultVal !== undefined) {
          setidentificationType(defaultVal.value)
          settipoIdentificacion(defaultVal.value)
          setIdentificationTypeSelected(defaultVal);
        }
      }
    }
    catch (err) { }

  }, [props, paises]);
  function changePais(event) {
    setCodigoPais(event);
  }
  function changeTipo(event) {
    setTipo(event);
    setidentificationType(event.value);
  }
  function cargarPaises() {
    const api = new CoreServices();
    api.getPaisesCatalogo()
      .then(response => {
        if (response === null) { return; }
        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        const optionGroup1 = [
          {
            label: t("Selectacountry"),
            options: json,
          },

        ];
        setPaises(json);
        setcambio(null)
        setcambio(2)
      });
    const apiServiceBackend = new ApiServiceBackend();
    apiServiceBackend.consultarCatalogoTipoIdentificacion()
      .then((data) => {
        if (data !== null && data !== undefined) {
          let json = [];
          for (let i = 0; i < data.length; i++) {
            json.push({ label: t(data[i]["description"]), value: data[i]["id"] })
          }
          /*const optionGroup1 = [
            {
              label: t("ID Type"),
              options: json,
            },
          ];*/
          setIdentificationTypeList(json)
        }
      })
      .catch((error) => {
        console.error('api error: ', error);
      });
  }
  //On change Inputs
  function handleChangeInputfrmSearch(e) {
    //selectedData[e.target.name] = e.target.value;
    //props.updateDataModel(selectedData);    

    switch (e.target.name) {
      case "identificationType": {
        setidentificationType(e.target.value);
        break;
      }
    }
  }
  function getSearchForm(tipo) {
    //segun tipo de identiicaciÃ³n
    if (tipo != undefined && tipo.value) {
      if (tipo.value == "1") {
        return (
          <Col md="8">
            <div className="mb-3">
              <Label htmlFor="firstName">{t("SocialReason")}</Label>
              <AvField
                className="form-control"
                name="firstName2"
                type="text" onChange={handleChangeInputfrmSearch}
                value={props.dataAcciones.shareholderFirstName}
                errorMessage={t("Required Field")}
                validate={{ required: { value: true } }} />
            </div>
          </Col>
        );
      } else {
        return (
          <React.Fragment>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="firstName">{t("First Name")}</Label>
                <AvField
                  className="form-control"
                  name="firstName"
                  value={props.dataAcciones.shareholderFirstName}
                  type="text" onChange={handleChangeInputfrmSearch}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }} />
              </div>
            </Col>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="secondName">{t("SecondName")}</Label>
                <AvField
                  name="secondName"
                  value={props.dataAcciones.shareholderMiddleName}
                  type="text" onChange={handleChangeInputfrmSearch}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }}
                  className="form-control" />
              </div>
            </Col>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="firstLastName">{t("FirstLastName")}</Label>
                <AvField
                  name="firstLastName"
                  value={props.dataAcciones.shareholderFirstSurname}
                  type="text" onChange={handleChangeInputfrmSearch}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }}
                  className="form-control"
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }} />
              </div>
            </Col>
            <Col md="4">
              <div className="mb-3">
                <Label htmlFor="secondLastName">{t("SecondLastName")}</Label>
                <AvField
                  name="secondLastName"
                  value={props.dataAcciones.shareholderSecondSurname}
                  type="text" onChange={handleChangeInputfrmSearch}
                  errorMessage={t("Required Field")}
                  validate={{ required: { value: true } }}
                  className="form-control" />
              </div>
            </Col>
            <Col md="4">
              <AvGroup className="mb-3">
                <Label htmlFor="startDate">{t("DBO")}</Label>
                <AvField
                  className="form-control"
                  name="startDate"
                  type="date"
                  value={props.dataAcciones.birthDate}
                  id="startDate" />
              </AvGroup>
            </Col>
          </React.Fragment>
        )
      }
    }
  }
  // switch (tipo != undefined && tipo.value) {
  //   case "1": { //Juridico

  //     default: { //Cedula o Pasaporte 

  //   }
  // }
  // }
  // validamos que el porcentaje de la participacion no sea mayor a 100
  function validationParticipation(value) {
    let sumaFinal = parseFloat(value) + parseFloat(sumaParticipacion);
    console.log(sumaFinal);
    if (parseFloat(sumaFinal) > 100) {
      setsumaParticipacionMessage(true);
    } else {
      setsumaParticipacionMessage(false)
    }
  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("Shareholder Information")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
        <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
          <Row>
            <Col xl="12">
              <Card>
                <CardBody>
                  <Row>
                    <Col xl="6">
                      <div className="mb-3">
                        <Label>{t("ID Type")}</Label>
                        <Select
                          onChange={(e) => {
                            setIdentificationTypeSelected(identificationTypeList.find(x => x.value === e.value))
                            settipoIdentificacion(e.value);
                          }}
                          options={identificationTypeList}
                          placeholder={t("SelectType")}
                          value={identificationTypeSelected}
                        />
                        {campoRequeridoIdentificacion ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>

                    <Col xl="6">
                      <div className="mb-3">
                        <Label htmlFor="idnumber">{t("ID Number")}</Label>
                        <AvField
                          className="form-control"
                          name="identificationNumber"
                          type="text"
                          id="identificationNumber"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                          value={props.dataAcciones.identificationNumber}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="identificationType">{t("PersonType")}</Label>
                        <Select
                          onChange={(e) => { setpersonTypeSelect(personTypeData.find(x => x.value === e.value)); changeTipo(e) }}
                          options={personTypeData}
                          id="sustainableCustomer"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={personTypeSelect}
                        // value={props.dataAcciones.shareholderName}
                        />
                        {campoRequeridoTipo ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}

                      </div>
                    </Col>
                    {getSearchForm(personTypeSelect)}
                  </Row>
                  <Row>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="nationality">{t("Nationality")}</Label>
                        <Select
                          onChange={(e) => {
                            setnacionalidadSelect(paises.find(x => x.value === e.value))
                            changePais(e);
                          }}
                          options={paises}
                          id="paises"
                          name="paises"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={nacionalidadSelect}
                        // value={props.dataAcciones.nationalityId}
                        // defaultValue={props.datosEmpresa.countryIdentification}
                        />
                        {campoRequeridoNacionalidad ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="name">{t("Participation")} {t("available")} {100 - sumaParticipacion}%</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="participation"
                          id="participation"
                          onChange={(e) => { validationParticipation(e.target.value) }}
                          value={props.dataAcciones.participation}
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                        {sumaParticipacionMessage ?
                          <p className="message-error-parrafo">{t("Thepercentageexceeds100%")}</p>
                          : null}
                      </div>
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="name">{t("YearsExprience")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="yearsExprience"
                          value={props.dataAcciones.yearsExperience}
                          id="yearsExprience"
                          validate={{
                            required: { value: true, errorMessage: t("Required Field") },
                            number: { value: true, errorMessage: t("InvalidField") },
                          }}
                        />
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                      {" "} {props.tipo == "guardar" ? t("Save") : t("Edit")}
                    </Button> : null}
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </div>
      </AvForm>
    </Modal>
  );
});

ModalInfoAccionistas.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func,
  onSelectClient: PropTypes.func.isRequired
};

export default (withTranslation()(ModalInfoAccionistas));
