import React, { useState, useContext, useRef, useEffect } from "react"
import PropTypes from 'prop-types';
import { translationHelpers, formatCurrency } from '../../../../helpers';
// import CreditProposalContext from './CreditProposalContext';
import { Link, useLocation } from "react-router-dom"
import SweetAlert from "react-bootstrap-sweetalert"

import {
  Table,
  Card,
  CardBody,
  Button,
  Row,
  Col,
  Label
} from "reactstrap"

// import ModalFacilityDetails from "./ModalFacilityDetails";
import { AvForm, AvGroup, AvField, AvFeedback } from "availity-reactstrap-validation"

// static data
import listaFacilidades from "../../../../api/bpm/propuestacredito/facilidades";
import { Facilidad } from "../../../../models/PropuestaCredito";
import { BackendServices, CoreServices } from "../../../../services";
import { useTranslation } from "react-i18next";
import Select from "react-select";
import ComisionSeccion from "./Comision";
import DisbursementSection from "./DisturbementSeccion";
import WarrantsSection from "./WarrantsSection";
import SuretiesSection from "./SuretiesSection";
import LoadingOverlay from "react-loading-overlay";


const ListaFacilidad = React.forwardRef((props, ref) => {
  const location = useLocation()
  const { t, i18n } = useTranslation();
  const [dataSet, setdataSet] = useState({
    "requestId": "XYZ892022",
    "cr": "",
    "amount": 0,
    "debtor": "",
    "clientTypeId": 0,
    "balance": 0,
    "proposalTypeId": "",
    "proposalTypeName": "",
    "facilityTypeId": "",
    "purpose": "",
    "sublimits": "",
    "proposalRate": 0,
    "noSubsidyRate": 0,
    "effectiveRate": 0,
    "feci": true,
    "termDays": 0,
    "termDescription": "",
    "ltv": 0,
    "finantialConditions": "",
    "environmentRiskCategory": 0,
    "covenant": "",
    "environmentRiskOpinion": "",
    "finantialCovenant": "",
    "legalDocumentation": "",
    "creditRiskOpinion": "",
    "provision": "",
    "numero": "",
    "otherConditions": "",
    "facilityId": 0,
  });

  const [datosUsuario, setdatosUsuario] = useState(null)
  const [dataValidation, setdataValidation] = useState({
    tipoPropuestaRequerido: false,
    subPropuestaRequerido: false,
    tipoFacilidadRequerido: false,
  });
  const [context, setcontext] = useState(location.data)
  const propostalSectionRef = useRef();
  const [formValid, setFormValid] = useState(false);
  const [dataLocation, setData] = useState(location.data);
  const [commissions, setCommissions] = useState([]);
  const [disbursementTerms, setDisbursementTerms] = useState([]);
  const [paymentPrograms, setPaymentPrograms] = useState([]);
  const [disbursementMethods, setDisbursementMethods] = useState([]);
  const [warrants, setWarrants] = useState([]);
  const [sureties, setSureties] = useState([]);
  const [otherconditions, setOtherconditions] = useState([]);
  const [ViewSeccion, setViewSeccion] = useState(false);
  const backendServices = new BackendServices();
  // Variables para alertas 
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [isActiveLoading, setIsActiveLoading] = useState(false);

  React.useImperativeHandle(ref, () => ({
    submit: (validate = true) => {
      const form = document.getElementById('formNewFacility');
      if (validate) {
        if (formValid) {
          form.requestSubmit();
          return true;
        }
        return false;
      }
      else {
        form.requestSubmit();
        return true;
      }
    },
    getFormValidation: () => {
      return formValid;
    }
  }));
  // services

  useEffect(() => {
    // Read Api Service  ata
    backendServices.consultarUsuarioProspecto(dataLocation.transactionId).then(resp => {
      dataSet.numero = resp.customerDocumentId
      setdataSet(dataSet);
      setdatosUsuario(resp)
    })
    // backendServices.consultarFacilidadPropCred(12, "XYZ432022").then(resp => {
    //   if (resp != undefined) {
    //     dataSet.cr = resp.cr;
    //     dataSet.amount = resp.amount;
    //     dataSet.debtor = resp.debtor;
    //     dataSet.clientTypeId = resp.clientTypeId;
    //     dataSet.balance = resp.balance;
    //     dataSet.proposalTypeId = resp.proposalTypeId;
    //     dataSet.proposalTypeName = resp.proposalTypeName;
    //     dataSet.facilityTypeId = resp.facilityTypeId;
    //     dataSet.purpose = resp.purpose;
    //     dataSet.sublimits = resp.sublimits;
    //     dataSet.proposalRate = resp.proposalRate;
    //     dataSet.noSubsidyRate = resp.noSubsidyRate;
    //     dataSet.effectiveRate = resp.effectiveRate;
    //     dataSet.feci = resp.feci;
    //     dataSet.termDays = resp.termDays;
    //     dataSet.termDescription = resp.termDescription;
    //     dataSet.ltv = resp.ltv;
    //     dataSet.finantialConditions = resp.finantialConditions;
    //     dataSet.environmentRiskCategory = resp.environmentRiskCategory;
    //     dataSet.covenant = resp.covenant;
    //     dataSet.environmentRiskOpinion = resp.environmentRiskOpinion;
    //     dataSet.finantialCovenant = resp.finantialCovenant;
    //     dataSet.legalDocumentation = resp.legalDocumentation;
    //     dataSet.creditRiskOpinion = resp.creditRiskOpinion;
    //     dataSet.provision = resp.provision;
    //     dataSet.otherConditions = resp.provision;
    //     setdataSet(dataSet);
    //   }
    // })
  }, []);
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setIsActiveLoading(true);
    values.amount = Number(values.amount);
    values.clientTypeId = Number(1);
    values.balance = Number(values.balance);
    values.proposalRate = Number(values.proposalRate);
    values.noSubsidyRate = Number(values.noSubsidyRate);
    values.effectiveRate = Number(values.effectiveRate);
    values.feci = values.feci == "true" ? true : false;
    values.termDays = Number(values.termDays);
    values.ltv = Number(values.ltv);
    values.environmentRiskCategory = Number(values.environmentRiskCategory);
    values.proposalTypeId = tipoPropuestaSelect == undefined ? " " : tipoPropuestaSelect.label;
    values.proposalTypeName = subPropuestaSelect == undefined ? " " : subPropuestaSelect.label;
    values.facilityTypeId = tipoFacilidadSelect == undefined ? " " : tipoFacilidadSelect.label;
    values.facilityId = dataSet.facilityId;
    values.requestId = props.dataGlobal.requestId;
    saveNewFacility(values)
      .then((result) => {
        // props.onSave(values);
        props.onDismiss();
      });
  }
  function nuevaFacilidad() {
    const data = {
      "facilityNumber": "0",
      "cr": "0",
      "amount": 0,
      "debtor": "  ",
      "clientTypeId": 1,
      "customer": {
        "id": "469303-1-434018"
      },
      "balance": 0,
      "purpose": ".",
      "sublimits": ".",
      "proposalRate": 0,
      "noSubsidyRate": 0,
      "effectiveRate": 0,
      "feci": false,
      "termDays": 0,
      "termDescription": "",
      "ltv": 0,
      "finantialConditions": " ",
      "environmentRiskCategory": 0,
      "covenant": " ",
      "environmentRiskOpinion": " ",
      "finantialCovenant": " ",
      "legalDocumentation": "  ",
      "otherConditions": " ",
      "creditRiskOpinion": " ",
      "provision": " ",
      "proposalTypeId": "RE",
      "proposalTypeName": "LRCC",
      "facilityTypeId": "LCN",
      "requestId": props.dataGlobal.requestId
    }


    backendServices.nuevoFacilidadPropCred(data).then(resp => {
      dataSet.facilityId = resp;
      setdataSet(dataSet);
      setViewSeccion(true)
    });
  }
  function saveNewFacility(values) {
    const datos = {
      "requestId": values.requestId,
      "facilityId": values.facilityId,
      "cr": values.cr,
      "amount": values.amount,
      "debtor": deudoresListSelect.label,
      "clientTypeId": deudoresListSelect.value,
      "balance": values.balance,
      "proposalTypeId": values.proposalTypeId,
      "proposalTypeName": values.proposalTypeName,
      "facilityTypeId": values.facilityTypeId,
      "purpose": values.purpose,
      "sublimits": values.sublimits,
      "proposalRate": values.proposalRate,
      "noSubsidyRate": values.noSubsidyRate,
      "effectiveRate": values.effectiveRate,
      "feci": values.feci,
      "termDays": values.termDays,
      "termDescription": values.termDescription,
      "ltv": values.ltv,
      "finantialConditions": values.finantialConditions,
      "environmentRiskCategory": values.environmentRiskCategory,
      "covenant": values.covenant,
      "environmentRiskOpinion": values.environmentRiskOpinion,
      "finantialCovenant": values.finantialCovenant,
      "legalDocumentation": values.legalDocumentation,
      "creditRiskOpinion": values.creditRiskOpinion,
      "otherConditions": values.otherConditions,
      "provision": values.provision
    }

    backendServices.actualizarFacilidadPropCred(datos).then(resp => {
      if (resp !== null && resp !== undefined) {
        setViewSeccion(!ViewSeccion);
        setsuccessSave_dlg(true);
      } else {
        seterror_dlg(true);
      }
      setIsActiveLoading(false)
    }).catch(err => {
      seterror_dlg(true);
      setIsActiveLoading(false)
    })
  }
  function handleCancel() {
    props.onDismiss();
  }

  function handleNewCommission(newItem) {
    const newCommissions = [...commissions, newItem];
    setCommissions(newCommissions);
  }

  function handleDeleteCommission(item) {
    const itemIndex = commissions.indexOf(item);
    if (itemIndex === -1) {
      return;
    }
    const newCommissions = [...commissions];
    newCommissions.splice(itemIndex, 1);
    setCommissions(newCommissions);
  }

  function handleNewDisbursementTerm(newItem) {
    const newDisbursementTerms = [...disbursementTerms, newItem];
    setDisbursementTerms(newDisbursementTerms);
  }

  function handleDeleteDisbursementTerm(item) {
    const itemIndex = disbursementTerms.indexOf(item);
    if (itemIndex === -1) {
      return;
    }
    const newDisbursementTerms = [...disbursementTerms];
    newDisbursementTerms.splice(itemIndex, 1);
    setDisbursementTerms(newDisbursementTerms);
  }

  function handleNewPaymentProgram(newItem) {
    const newPaymentPrograms = [...paymentPrograms, newItem];
    setPaymentPrograms(newPaymentPrograms);
  }

  function handleDeletePaymentProgram(item) {
    const itemIndex = paymentPrograms.indexOf(item);
    if (itemIndex === -1) {
      return;
    }
    const newPaymentPrograms = [...paymentPrograms];
    newPaymentPrograms.splice(itemIndex, 1);
    setPaymentPrograms(newPaymentPrograms);
  }

  function handleNewDisbursementMethod(newItem) {
    const newDisbursementMethods = [...disbursementMethods, newItem];
    setDisbursementMethods(newDisbursementMethods);
  }

  function handleDeleteDisbursementMethod(item) {
    const itemIndex = disbursementMethods.indexOf(item);
    if (itemIndex === -1) {
      return;
    }
    const newDisbursementMethods = [...disbursementMethods];
    newDisbursementMethods.splice(itemIndex, 1);
    setDisbursementMethods(newDisbursementMethods);
  }

  function handleNewWarrant(newItem) {
    const newWarrants = [...warrants, newItem];
    setWarrants(newWarrants);
  }

  function handleDeleteWarrant(item) {
    const itemIndex = warrants.indexOf(item);
    if (itemIndex === -1) {
      return;
    }
    const newWarrants = [...warrants];
    newWarrants.splice(itemIndex, 1);
    setWarrants(newWarrants);
  }

  function handleNewSurety(newItem) {
    const newSureties = [...sureties, newItem];
    setSureties(newSureties);
  }

  function handleDeleteSurety(item) {
    const itemIndex = sureties.indexOf(item);
    if (itemIndex === -1) {
      return;
    }
    const newSureties = [...sureties];
    newSureties.splice(itemIndex, 1);
    setSureties(newSureties);
  }
  // FUNCIONES PARA PROPOSTALSECTION
  const api = new BackendServices();
  const apiCoreServices = new CoreServices();
  const [dataReturn, setdataReturn] = useState({
    proposalTypeId: null,
    proposalTypeName: null,
    facilityTypeId: null,
  });
  const [tipoPropuesta, settipoPropuesta] = useState(null);
  const [tipoPropuestaSelect, settipoPropuestaSelect] = useState(undefined);
  const [propuestaSet, setpropuestaSet] = useState(null);
  const [tipoPropuestaRequerido, settipoPropuestaRequerido] = useState(false);
  const [subPropuesta, setsubPropuesta] = useState(null);
  const [subPropuestaSelect, setsubPropuestaSelect] = useState(undefined);
  const [subPropuestaSet, setsubPropuestaSet] = useState(null);
  const [subPropuestaRequerido, setsubPropuestaRequerido] = useState(false);
  const [tipoFacilidad, settipoFacilidad] = useState(null);
  const [tipoFacilidadSelect, settipoFacilidadSelect] = useState(undefined);
  const [deudoresListSelect, setdeudoresListSelect] = useState(undefined);
  const [tipoFacilidadSet, settipoFacilidadSet] = useState(null);
  const [deudoresList, setdeudoresList] = useState(null);
  const [tipo, settipo] = useState("guardar");
  const [dataRows, setdataRows] = useState(null);
  const [dataEdit, setdataEdit] = useState(null);
  const [campoRequeridoDeudores, setcampoRequeridoDeudores] = useState(false);
  const [MontoFacilidad, setMontoFacilidad] = useState(0);
  let listaIdentificacion = props.identificationList;
  const [tipoFacilidadRequerido, settipoFacilidadRequerido] = useState(false);
  React.useEffect(() => {
    // Read Api Service data

    initializeData();
  }, []);
  React.useEffect(() => {
    // Read Api Service data

    api.consultarFacilidades(props.dataGlobal.requestId).then(resp => {
      if (resp.length > 0) {
        setdataRows(resp.map((data) => (
          <tr key={data.facilityId}>
            <td>{data.facilityId}</td>
            <td>{data.debtor}</td>
            <td>{data.facilityType}</td>
            <td>{data.proposedType}</td>
            <td>{data.amount}</td>
            <td>{data.proposedRate}</td>
            {/* <td>{data.observations}%</td> */}
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => {
                setdataSet(data); settipo("editar"); setViewSeccion(!ViewSeccion);
              }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
            </td>
          </tr>)
        ));
      } else {
        setdataRows(
          <tr key={1}>
            <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    });
  }, []);
  React.useEffect(() => {
    // Read Api Service data
    settipoPropuestaRequerido(dataValidation.tipoPropuestaRequerido);
    setsubPropuestaRequerido(dataValidation.subPropuestaRequerido);
    settipoFacilidadRequerido(dataValidation.tipoFacilidadRequerido);
  }, [dataValidation]);
  React.useEffect(() => {
    // Read Api Service data 
    if (tipo == "editar") {
      var defaultVal = null;
      settipoPropuestaSelect(undefined);
      setsubPropuestaSelect(undefined);
      settipoFacilidadSelect(undefined);
      try {
        if (tipoPropuesta.length > 0 && dataSet.proposedType !== null && tipoPropuestaSelect === undefined) {
          defaultVal = tipoPropuesta.find(x => x.label === dataSet.proposedType);
          if (defaultVal !== undefined) {
            settipoPropuestaSelect(defaultVal);
          }
        }
        if (subPropuesta.length > 0 && dataSet.subproposalType !== null && subPropuestaSelect === undefined) {
          defaultVal = subPropuesta.find(x => x.label === dataSet.subproposalType);
          if (defaultVal !== undefined) {
            setsubPropuestaSelect(defaultVal);
          }
        }
        if (tipoFacilidad.length > 0 && dataSet.facilityType !== null && tipoFacilidadSelect === undefined) {
          defaultVal = tipoFacilidad.find(x => x.label === dataSet.facilityType);
          if (defaultVal !== undefined) {
            settipoFacilidadSelect(defaultVal);
          }
        }
      }
      catch (err) { }
    }
  }, [dataSet, ViewSeccion]);
  function initializeData() {
    api.consultarDeudores(dataLocation.transactionId)
      .then((data) => {
        if (data !== null && data !== undefined) {
          let json = [];
          for (let i = 0; i < data.length; i++) {
            json.push({ label: data[i]["name"] + " " + data[i]["name2"] + " " + data[i]["lastName"] + " " + data[i]["lastName2"], value: data[i]["personId"] })
          }
          /*const optionGroup1 = [
            {
              label: t("ID Type"),
              options: json,
            },
          ];*/
          setdeudoresListSelect(json[0])
          setdeudoresList(json)
        }
      })
      .catch((error) => {
        console.error('api error: ', error);
      });
    api.retrieveProposalType()
      .then((response) => {
        if (response === null) { return; }
        let json = [];
        for (let i = 0; i < response.length; i++) {
          json.push({ label: response[i]["description"], value: response[i]["id"] })
        }
        settipoPropuesta(json);
      })
      .catch((error) => {
        console.error('api error: ', error);
      });
    api.retrieveSubproposalType()
      .then((response) => {
        if (response === null) { return; }
        let json = [];
        for (let i = 0; i < response.length; i++) {
          json.push({ label: response[i]["description"], value: response[i]["id"] })
        }
        setsubPropuesta(json);
      })
      .catch((error) => {
        console.error('api error: ', error);
      });
    api.retrieveFacilityType()
      .then((response) => {
        if (response === null) { return; }
        let json = [];
        for (let i = 0; i < response.length; i++) {
          json.push({ label: response[i]["description"], value: response[i]["id"] })
        }
        settipoFacilidad(json);
      })
      .catch((error) => {
        console.error('api error: ', error);
      });

  }


  return (
    <React.Fragment>
      <AvForm id="formFacilidades" className="needs-validation" onSubmit={handleSubmit}>
        <Card>
          {!ViewSeccion ?
            <CardBody>
              <h5>{props.title}</h5>
              <p className="card-title-desc"></p>
              <Table className="table mt-1" responsive>
                <thead className="table-light">
                  <tr>
                    <th>{t("Facility")}</th>
                    <th>{t("Debtor")}</th>
                    <th>{t("Facility Type")}</th>
                    <th>{t("Proposal")}</th>
                    <th>{t("Approved Risk")}</th>
                    <th>{t("Amount") + " " + t("Rate")}</th>
                    <th><Link className="float-end" onClick={() => { nuevaFacilidad(); }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                  </tr>
                </thead>
                <tbody>
                  {dataRows}
                </tbody>
              </Table>
            </CardBody>
            : null}
          {ViewSeccion ?
            <CardBody>
              <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
                <h5>{t("Facility")}</h5>
                <p className="card-title-desc"></p>
                {/* CustomerSeccion */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("General Data")}</h5>
                    <Row>
                      <Col lg="4">
                        <AvGroup className="mb-3">
                          <Label htmlFor="facilityNumber">{t("Number")}</Label>
                          <AvField
                            className="form-control"
                            name="facilityNumber"
                            type="text"
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            value={dataSet.facilityNumber == "" || dataSet.facilityNumber == null ? dataSet.facilityId : dataSet.facilityNumber}
                            id="facilityNumber" />
                        </AvGroup>
                      </Col>
                      <Col lg="4">
                        <AvGroup className="mb-3">
                          <Label htmlFor="cr">{t("CR")}</Label>
                          <AvField
                            className="form-control"
                            name="cr"
                            value={dataSet.cr}
                            type="text"
                            id="cr" />
                        </AvGroup>
                      </Col>
                      <Col lg="4">
                        <AvGroup className="mb-3">
                          <Label htmlFor="amount">{t("Amount")}</Label>
                          <AvField
                            className="form-control"
                            name="amount"
                            type="text"
                            onChange={(e) => { setMontoFacilidad(e.target.value) }}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            value={dataSet.amount}
                            id="amount" />
                        </AvGroup>
                      </Col>
                    </Row>
                    <Row>
                      <Col md="8">
                        <Label htmlFor="amount">{t("Debtors")}</Label>
                        <Select
                          onChange={(e) => {
                            setdeudoresListSelect(deudoresList.find(x => x.value === e.value))
                          }}
                          options={deudoresList}
                          classNamePrefix="select2-selection"
                          placeholder={t("Select")}
                          value={deudoresListSelect}
                        />
                        {campoRequeridoDeudores ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </Col>
                      <Col md="4">
                        <AvGroup className="mb-3">
                          <Label htmlFor="balance">{t("Balance")}</Label>
                          <AvField
                            className="form-control"
                            name="balance"
                            value={dataSet.balance}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            type="text"
                            id="balance" />
                        </AvGroup>
                      </Col>

                    </Row>
                  </Col>
                </Row>
                {/* ProposalSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("Proposal")}</h5>
                    {/* <AvForm id="frmFacilidades" className="needs-validation" onSubmit={handleSubmit}> */}
                    <Row>
                      <Col md="4">
                        <Label htmlFor="proposalType">{t("Proposal Type")}</Label>
                        <Select
                          onChange={(e) => {
                            settipoPropuestaSelect(tipoPropuesta.find(x => x.label === e.label))
                            setpropuestaSet(e.label);
                            dataReturn.proposalTypeId = e.label;
                            setdataReturn(dataReturn);
                          }}
                          options={tipoPropuesta}
                          placeholder={t("Select")}
                          value={tipoPropuestaSelect}
                        />
                        {dataValidation.tipoPropuestaRequerido ?
                          <p className="message-error-parrafo">{t("Required")}</p>
                          : null}
                      </Col>
                      <Col md="4">
                        <Label htmlFor="subproposalType">{t("SubProposal Type")}</Label>
                        <Select
                          onChange={(e) => {
                            setsubPropuestaSelect(subPropuesta.find(x => x.label === e.label))
                            setsubPropuestaSet(e.label);
                            dataReturn.proposalTypeName = e.label;
                            setdataReturn(dataReturn);
                          }}
                          options={subPropuesta}
                          placeholder={t("Select")}
                          value={subPropuestaSelect}
                        />
                        {dataValidation.subPropuestaRequerido ?
                          <p className="message-error-parrafo">{t("Required")}</p>
                          : null}

                      </Col>
                      <Col md="4">
                        <Label htmlFor="facilityType">{t("Facility Type")}</Label>
                        <Select
                          onChange={(e) => {
                            settipoFacilidadSelect(tipoFacilidad.find(x => x.label === e.label))
                            settipoFacilidadSet(e.label);
                            dataReturn.facilityTypeId = e.label;
                            setdataReturn(dataReturn);
                          }}
                          options={tipoFacilidad}
                          placeholder={t("Select")}
                          value={tipoFacilidadSelect}
                        />
                        {dataValidation.tipoFacilidadRequerido ?
                          <p className="message-error-parrafo">{t("Required")}</p>
                          : null}
                      </Col>
                    </Row>
                    <Row>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="purpose">{t("Purpose")}</Label>
                          <AvField 
                            className="form-control"
                            name="purpose"
                            id="purpose"
                            validate={{
                              required: { value: true, errorMessage: t("Required") }
                            }}
                            type="textarea"
                            value={dataSet.purpose}
                            rows="4" />
                        </AvGroup>
                      </Col>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="sublimits">{t("Sub Limits")}</Label>
                          <AvField 
                            className="form-control"
                            name="sublimits"
                            id="sublimits"
                            value={dataSet.sublimits}
                            validate={{
                              required: { value: true, errorMessage: t("Required") }
                            }}
                            type="textarea"
                            rows="4" />
                        </AvGroup></Col>
                    </Row>
                  </Col>
                </Row>
                {/* InterestRateSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("Proposed Interest Rate")}</h5>
                    <Row>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="proposalRate">{t("Amount")}</Label>
                          <AvField
                            className="form-control"
                            name="proposalRate"
                            type="text"
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            id="proposalRate"
                            value={dataSet.proposedRate} />
                        </AvGroup>
                      </Col>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="noSubsidyRate">{t("Rate without Subsidy")}</Label>
                          <AvField
                            className="form-control"
                            name="noSubsidyRate"
                            type="text"
                            id="noSubsidyRate"
                            value={dataSet.nonSubsidyRate}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }} />
                        </AvGroup>
                      </Col>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="effectiveRate">{t("Effective Interest Rate")}</Label>
                          <AvField
                            className="form-control"
                            name="effectiveRate"
                            value={dataSet.effectiveRate}
                            type="text"
                            id="effectiveRate"
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }} />
                        </AvGroup>
                      </Col>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="feci">{t("FECI")}</Label>
                          <AvField id="feci"
                            className="form-control"
                            name="feci"
                            type="select"
                            value={dataSet.feci ? "true" : "false"}
                            required>
                            <option value="true">Si</option>
                            <option value="false">No</option>
                          </AvField>
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* CommissionSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <ComisionSeccion MontoFacilidad={MontoFacilidad} validation={dataValidation} dataSet={dataSet} items={commissions}
                      onSaveItem={handleNewCommission}
                      onDeleteItem={handleDeleteCommission} />
                  </Col>
                </Row>
                {/* TermsSection */}
                <h5>{t("Terms")}</h5>
                <Row>
                  <Col md="12">
                    <AvGroup className="mb-3">
                      <Label htmlFor="termDays">{t("Terms in Days/Month/Year")}</Label>
                      <AvField
                        className="form-control"
                        name="termDays"
                        value={dataSet.deadlinesDays}
                        type="text"
                        validate={{
                          number: { value: true, errorMessage: t("InvalidField") },
                        }}
                        id="termDays" />
                    </AvGroup>
                    <AvGroup className="mb-3">
                      <Label htmlFor="termsDescription">{t("Terms Description")}</Label>
                      <AvField
                        className="form-control"
                        name="termDescription"
                        type="textarea"
                        id="termDescription"
                        value={dataSet.descriptionDeadlines}
                        rows="4" />
                    </AvGroup>
                  </Col>
                </Row>
                {/* DisbursementSection */}
                <DisbursementSection validation={dataValidation} dataSet={dataSet} disbursementTerms={disbursementTerms}
                  onSaveDisbursementTerm={handleNewDisbursementTerm}
                  onDeleteDisbursementTerm={handleDeleteDisbursementTerm}
                  paymentPrograms={paymentPrograms}
                  onSavePaymentProgram={handleNewPaymentProgram}
                  onDeletePaymentProgram={handleDeletePaymentProgram}
                  disbursementMethods={disbursementMethods}
                  onSaveDisbursementMethod={handleNewDisbursementMethod}
                  onDeleteDisbursementMethod={handleDeleteDisbursementMethod} />
                {/* WarrantsSection */}
                <WarrantsSection validation={dataValidation} dataSet={dataSet} warrants={warrants}
                  onSaveItem={handleNewWarrant}
                  onDeleteItem={handleDeleteWarrant} />
                {/* LTV */}
                <Row>
                  <Col md="12">
                    <h5>{t("LTV")} (%)</h5>
                    <Row>
                      <Col md="4">
                        <AvGroup className="mb-3">
                          <AvField
                            className="form-control"
                            name="ltv"
                            value={dataSet.ltv}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            type="text"
                            id="ltv" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* SuretiesSection */}
                <SuretiesSection validation={dataValidation} dataSet={dataSet} sureties={sureties}
                  onSaveItem={handleNewSurety}
                  onDeleteItem={handleDeleteSurety} />
                {/* FinancialConditionsSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("FinancialConditions")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">

                          <AvField
                            className="form-control"
                            name="finantialConditions"
                            type="textarea"
                            value={dataSet.financialConditions}
                            id="finantialConditions"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* AmbientalRiskSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("AmbientalRisk")}</h5>
                    <Row>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="category">{t("Category")}</Label>
                          <AvField
                            className="form-control"
                            name="environmentRiskCategory"
                            type="text"
                            value={dataSet.environmentalRiskCategory}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            id="environmentRiskCategory" />
                        </AvGroup>
                      </Col>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="covenant">{t("Covenant")}</Label>
                          <AvField
                            className="form-control"
                            name="covenant"
                            type="text"
                            value={dataSet.covenant}
                            id="covenant" />
                        </AvGroup>
                      </Col>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <Label htmlFor="opinion">{t("Opinion")}</Label>
                          <AvField
                            className="form-control"
                            name="environmentRiskOpinion"
                            type="textarea"
                            value={dataSet.environmentalOpinion}
                            id="environmentRiskOpinion"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* FinantialCovenantSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("FinantialCovenant")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <AvField
                            className="form-control"
                            name="finantialCovenant"
                            value={dataSet.financialCovenant}
                            type="textarea"
                            id="finantialCovenant"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* LegalDocumentationSection */}
                <Row>
                  <Col>
                    <h5>{t("LegalDocumentation")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">

                          <AvField
                            className="form-control"
                            name="legalDocumentation"
                            value={dataSet.legalDocumentation}
                            type="textarea"
                            id="legalDocumentation"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* OtherConditionsSection */}
                <Row>
                  <Col>
                    <h5>{t("OtherConditions")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <AvField
                            className="form-control"
                            name="otherConditions"
                            value={dataSet.opinionRiskCredit}
                            type="textarea"
                            id="otherConditions"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* CreditRiskSection */}
                <Row>
                  <Col>
                    <h5>{t("CreditRisk")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <Label htmlFor="creditrisk">{t("Opinion")}</Label>
                          <AvField
                            className="form-control"
                            name="creditRiskOpinion"
                            type="textarea"
                            value={dataSet.environmentalOpinion}
                            id="creditRiskOpinion"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* ProvisionSection */}
                <Row>
                  <Col>
                    <h5>{t("Provision")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">

                          <AvField
                            className="form-control"
                            name="provision"
                            value={dataSet.provision}
                            type="textarea"
                            id="provision"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                <Row className="my-2">
                  <Col xl="12 text-end">
                    <Link to="#" className="btn btn-dark"
                      onClick={(e) => {
                        setViewSeccion(false);
                        api.consultarFacilidades(props.dataGlobal.requestId).then(resp => {
                          if (resp.length > 0) {
                            setdataRows(resp.map((data) => (
                              <tr key={data.facilityId}>
                                <td>{data.facilityId}</td>
                                <td>{data.debtor}</td>
                                <td>{data.facilityType}</td>
                                <td>{data.proposedType}</td>
                                <td>{data.amount}</td>
                                <td>{data.proposedRate}</td>
                                {/* <td>{data.observations}%</td> */}
                                <td style={{ textAlign: "right" }}>
                                  <Button type="button" color="link" onClick={(resp) => {
                                    setdataSet(data); settipo("editar"); setViewSeccion(true);
                                  }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                                </td>
                              </tr>)
                            ));
                          } else {
                            setdataRows(
                              <tr key={1}>
                                <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
                              </tr>);
                          }
                        });
                      }} style={{ margin: '5px' }}>
                      {t("Cancel")}
                    </Link>
                    <Button className="btn btn-primary" type="submit" color="success" style={{ margin: '5px' }}>
                      {t("Save")}
                    </Button>
                  </Col>
                </Row>
              </LoadingOverlay>
            </CardBody>
            : null}
        </Card>
      </AvForm>
      {/* {selectedFacility && (
        <ModalFacilityDetails isOpen={displayModalFacilityDetails} toggle={toggleFacilityDetailsModal} facility={selectedFacility} />
      )} */}
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false);
            api.consultarFacilidades(props.dataGlobal.requestId).then(resp => {
              if (resp.length > 0) {
                setdataRows(resp.map((data) => (
                  <tr key={data.facilityId}>
                    <td>{data.facilityId}</td>
                    <td>{data.debtor}</td>
                    <td>{data.facilityType}</td>
                    <td>{data.proposedType}</td>
                    <td>{data.amount}</td>
                    <td>{data.proposedRate}</td>
                    {/* <td>{data.observations}%</td> */}
                    <td style={{ textAlign: "right" }}>
                      <Button type="button" color="link" onClick={(resp) => {
                        setdataSet(data); settipo("editar"); setViewSeccion(!ViewSeccion);
                      }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
                    </td>
                  </tr>)
                ));
              } else {
                setdataRows(
                  <tr key={1}>
                    <td colSpan="7" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
                  </tr>);
              }
            });
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            setViewSeccion(!ViewSeccion);
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>

  );
});

ListaFacilidad.propTypes = {
  items: PropTypes.array.isRequired,
  editMode: PropTypes.bool,
  onSaveFacility: PropTypes.func
};

ListaFacilidad.defaultProps = {
  items: [],
  editMode: false
};

export default ListaFacilidad;
