import React, { Component } from "react"
import { Row, Col, Card, CardBody } from "reactstrap"
import ReactApexChart from "react-apexcharts"
import { BackendServices } from "../../services";
import * as moment from 'moment';

class SalesAnalytics extends Component {

  constructor(props) {
    super(props)
    this.backendServices = new BackendServices();
    this.inicialize();

    this.state = {
      series: [],
      options: {
        labels: [],
        plotOptions: {
          pie: {
            donut: {
              size: '75%'
            }
          }
        },
        legend: {
          show: false,
        },
        colors: ['#3b5de7', '#45cb85', '#eeb902'],
      },
    }



  }

  inicialize() {
    this.backendServices.getBpmStatistics().then(resp => {
      let processBpmId = this.groupBy(resp, "activityBpmName");
      let labels = processBpmId.map(process => process.at(0).activityBpmName);
      let series = processBpmId.map(process => process.length);
      this.setState({ ...this.state, series, options: { ...this.state.options, labels } });
    }).catch(err => { })
  }

  /**
   * groupBy, agrupa los items de un JSON
   * @param {Array} collection 
   * @param {string} property 
   * @returns {Array}
   */
  groupBy(collection, property) {
    var i = 0, val, index,
      values = [], result = [];
    for (; i < collection.length; i++) {
      val = collection[i][property];
      index = values.indexOf(val);
      if (index > -1)
        result[index].push(collection[i]);
      else {
        values.push(val);
        result.push([collection[i]]);
      }
    }
    return result;
  }

  render() {
    return (
      <React.Fragment>
        <Card>
          <CardBody>
            <h4 className="card-title mb-4">Estad√≠sticas</h4>

            <Row className="align-items-center">
              <Col>
                <ReactApexChart
                  options={this.state.options}
                  series={this.state.series}
                  type="donut"
                  height={245}
                  className="apex-charts"
                />
              </Col>
              {/* <Col sm={6}>
                <div>
                  <Row>
                    <div className="col-6">
                      <div className="py-3">
                        <p className="mb-1 text-truncate"><i
                          className="mdi mdi-circle text-primary me-1"></i>{" "}Analisis
                        </p>
                        <h5>$ 2,652</h5>
                      </div>
                    </div>
                    <div className="col-6">
                      <div className="py-3">
                        <p className="mb-1 text-truncate"><i
                          className="mdi mdi-circle text-success me-1"></i>{" "}Desembolso</p>
                        <h5>$ 2,284</h5>
                      </div>
                    </div>
                    <div className="col-6">
                      <div className="py-3">
                        <p className="mb-1 text-truncate"><i
                          className="mdi mdi-circle text-warning me-1"></i>{" "}Propuesta</p>
                        <h5>$ 1,753</h5>
                      </div>
                    </div>
                  </Row>
                </div>
              </Col> */}
            </Row>
          </CardBody>
        </Card>
      </React.Fragment>
    )
  }
}

export default SalesAnalytics
