import React, { useEffect, useState } from "react"
import { translationHelpers } from "../../../helpers"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"
import { withTranslation } from "react-i18next"
import {
  Row,
  Col,
  Button
} from "reactstrap"

import { Link } from "react-router-dom"

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import { GeneralSection } from "../../../components/PropuestaCreditoComercial"
import Analisis from "./riesgoCredito/Analisis"
import FormConclusiones from "./riesgoCredito/Conclusiones"
import AvForm from "availity-reactstrap-validation/lib/AvForm"
import { BpmServices } from "../../../services"
import SweetAlert from "react-bootstrap-sweetalert"



const RiesgoCreditoDetalle = (props) => {

  const history = useHistory();
  const location = useLocation()
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [proposal, setProposal] = useState(null);

  const [n, t, c] = translationHelpers('navigation', 'commercial_credit', 'common');

  useEffect(() => {
    fetchProposal();
  }, []);

  function fetchProposal() {
    setProposal({
      proposal: {
        id: 1010,
        revisionType: 'Bimensual',
        preapproved: false,
        minSib: 'Provision',
        relatedPart: false,
        approvalLevel: 'Nivel',
        flowType: 'Normal',
        applicationDate: '24-sep-2021',
        lastApplicationDate: '24-sep-2021',
        nextRevisionDate: '24-sep-2021',
        expirationDate: '24-sep-2021'
      },
      client: {
        number: '3223213',
      },
      economicGroup: {
        number: '32132006176',
        name: 'Grupo Tova SA',
        business: 'Actividad seleccionada'
      },
      risk: {
        countryClass: 'PanamÃ¡',
        classification: 'Medio',
        countryRisk: 'Nicaragua'
      },
      accountExecutive: {
        name: 'Elibeth'
      },
      responsibleUnit: {
        name: 'Unidad'
      }
    });
  }

  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    console.log('submit', values);
  }

  function handleReturn() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                                      Avanzamos el proceso                                      */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BpmServices();
    let taskId = "";
    api.completedStatusTask(taskId).then(resp => {
      if (resp !== undefined) {
        setsuccessSave_dlg(true);
      }
      else {
        //Mensaje ERROR
        seterror_msg(props.t("ErrorSaveMessage"))
        seterror_dlg(true)
      }
    }).catch(err => {
      console.log(err);
    })
  }

  function handleClose() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BpmServices();
    // let instanceId = location.data.instanceId
    let instanceId = 0
    api.abortProcess(instanceId).then((data) => {
      if (data !== undefined) {
        history.push(url.URL_DASHBOARD);
      }
      else {
        //Mensaje ERROR
        seterror_msg(props.t("TheProcessCouldNotFinish"))
        seterror_dlg(true)
      }
    });
  }


  return (

    <div className="page-content">

      <Breadcrumbs title={n("Commercial Credit")} breadcrumbItem={n("Credit Risk")} />

      {proposal && (
        <>
          <Row>
            <Col xl="12">
              <GeneralSection proposal={proposal} />
            </Col>
          </Row>

          <AvForm className="needs-validation" onSubmit={handleSubmit}>

            <Row>
              <Col xl="12">
                <Analisis />
              </Col>
            </Row>

            <Row>
              <Col xl="12">
                <FormConclusiones />
              </Col>
            </Row>

            <Row className="my-2">
              <Col xl="12 text-end">
                <Button id="btnReturn" color="dark" type="button" style={{ margin: '5px' }} onClick={handleClose}>
                  <i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i>
                  {" "} {c("Close")}
                </Button>
                <Button id="btnSave" color="success" type="submit" style={{ margin: '5px' }} onClick={handleReturn}>
                  <i className="mdi mdi-content-save mdi-12px"></i>
                  {" "} {c("Save")}
                </Button>
              </Col>
            </Row>

          </AvForm>
          {successSave_dlg ? (
            <SweetAlert
              success
              title={props.t("SuccessDialog")}
              onConfirm={() => {
                setsuccessSave_dlg(false)

                history.push(url.URL_DASHBOARD);
              }}
            >
              {props.t("SuccessSaveMessage")}
            </SweetAlert>
          ) : null}

          {error_dlg ? (
            <SweetAlert
              error
              title={props.t("ErrorDialog")}
              onConfirm={() => {
                seterror_dlg(false)
              }}
            >
              {error_msg}
            </SweetAlert>
          ) : null}
        </>
      )}

    </div>
  );

}


export default (withTranslation()(RiesgoCreditoDetalle));
