import PropTypes from 'prop-types';
import { formatCurrency, translationHelpers } from '../../../../helpers';
import {
  Table,
  Card,
  CardBody,
} from "reactstrap"
import React, { useEffect, useState } from "react"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import { useTranslation } from 'react-i18next';
import { useLocation } from 'react-router-dom';
const ExposicionCorporativaClientes = (props) => {
  const location = useLocation();
  const coreServices = new CoreServices();
  const { t, i18n } = useTranslation();
  const [dataRows, setdataRows] = React.useState(null);
  const [CorporateExposure, setCorporateExposure] = useState(null);
  const [customerNumberT24, setcustomerNumberT24] = React.useState(props.customerNumberT24);
  useEffect(() => {
    getCoporateExhibitionClient();
  }, []);
  function getCoporateExhibitionClient() {
    // getCorporateExhibitionByClients
    const backendServices = new BackendServices();
    backendServices.consultPrincipalDebtor(location.data.transactionId)
      .then((data) => {
        if (data !== undefined) {
          coreServices.getCorporateExhibitionByClients(data.customerNumberT24).then(resp => {
            if (resp === undefined) {
              return;
            }
            setCorporateExposure(resp.length > 0 ? resp : null);
            setdataRows(resp.map((item, index) => (
              <>
                <tr key={'row-' + index}>
                  <th scope="row" width="30%">{item.clinteId}</th>
                  <th scope="row" className="text-end"></th>
                  <th scope="row" className="text-end"></th>
                  <th scope="row" className="text-end"></th>
                </tr>
                {item.data.map((item2, index2) => (
                  <tr key={'subrow-' + index2}>
                    <td scope="row" width="30%"></td>
                    <td scope="row" className="text-start">{formatCurrency('$', item2.approvalRisk, 0)}</td>
                    <td scope="row" className="text-start">{formatCurrency('$', item2.variationRisk, 0)}</td>
                    <td scope="row" className="text-start">{formatCurrency('$', item2.proposalRisk, 0)}</td>
                  </tr>
                ))}
              </>
            )
            ));
          });
        }
      });
  }
  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <h5 className="card-title">{props.title}</h5>
          <p className="card-title-desc">
          </p>
          <div className="table-responsive">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th></th>
                  <th className="table-right">{t("ApprovedRisk")}</th>
                  <th className="table-right">{t("ProposedRisk")}</th>
                  <th className="table-right">{t("Variation")}</th>
                </tr>
              </thead>
              <tbody>
                {dataRows}
                <tr>
                  <th scope="row" width="30%">{t("Total")}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
                    let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2.approvalRisk ?? acu2) + crr2.approvalRisk)) ?? acu);
                    let data2 = crr.data?.reduce((acu2, crr2) => ((acu2.approvalRisk ?? acu2) + crr2.approvalRisk));
                    return (data1 + data2)
                  }), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
                    let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2.variationRisk ?? acu2) + crr2.variationRisk)) ?? acu);
                    let data2 = crr.data?.reduce((acu2, crr2) => ((acu2.variationRisk ?? acu2) + crr2.variationRisk));
                    return (data1 + data2)
                  }), 0)}</th>
                  <th scope="row" className="table-right">{formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
                    let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2.proposalRisk ?? acu2) + crr2.proposalRisk)) ?? acu);
                    let data2 = crr.data?.reduce((acu2, crr2) => ((acu2.proposalRisk ?? acu2) + crr2.proposalRisk));
                    return (data1 + data2)
                  }), 0)}</th>
                </tr>
              </tbody>
            </Table>
          </div>
        </CardBody>
      </Card>
    </React.Fragment>
  );
};
ExposicionCorporativaClientes.propTypes = {
  title: PropTypes.string,
  customerNumberT24: PropTypes.string,
};
export default ExposicionCorporativaClientes;
