        body: {
          noMatch: t("SorryNoMatchingRecordsWereFfound"),
          toolTip: t("Order"),
          columnHeaderTooltip: (column) => `${t("OrderBy")} ${column.label}`,
        },
        pagination: {
          next: t("Next"),
          previous: t("Prev"),
          rowsPerPage: t("RowsPerPage"),
          displayRows: t("From"),
          sizePerPage: 5,
          sizePerPageList: [{
            text: '5th', value: 5
          }, {
            text: '10th', value: 10
