import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import error from '../assets/images/error.png'
import {
    Row,
    Col,
    Button,
    Label,
    Modal,
    Card,
    CardBody,
    CardFooter,
} from "reactstrap"
// import './index.css';
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
const ModalErrrorMessage = (props) => {
    const { t, i18n } = useTranslation();
    const [activacion, setactivacion] = useState(false);
    const [datosError, setdatosError] = useState({
        code: "",
        message: "",
        functionName: "",
        serviceType: "",
        error: false
    });
    let jsonTypeService = {
        bk: "Backend Services",
        bpm: "JBPM Services",
        core: "Core Services"
    }
    let jsonErrorMessage = {
        404: "Error 404",
        500: "Error 500",
    }
    // Submitimos formulario para busqueda de clientes
    window.addEventListener('storage', function (error) {
        console.log(error);
        if (error.key == "jsonError") {
            let json = JSON.parse(error.newValue)
            console.log(json);
            setdatosError(json)
            setactivacion(!!json.error);
        }
    });
    function cambiarStatus() {
        let jsonError = {
            code: "",
            message: "",
            functionName: "",
            serviceType: "",
            error: false
        }
        localStorage.setItem("jsonError", JSON.stringify(jsonError))
        removeBodyCss()
        setactivacion(false)
    }
    function removeBodyCss() {
        document.body.classList.add("no_padding")
    }
    return (
        <Modal
            size="sm"
            isOpen={activacion}
            toggle={cambiarStatus}
            centered={true}>
            <button
                type="button"
                onClick={cambiarStatus}
                data-dismiss="modal"
                className="close"
                aria-label="Close">
                <span aria-hidden="true">&times;</span>
            </button>
            <div className="modal-body" style={{ backgroundColor: "white" }}>
                <Row>
                    <Col xl="12" style={{ textAlign: "center" }}>
                        <img src={error} width="90px" height="auto" />
                    </Col>
                </Row>
                <Row>
                    <Col xl="12" style={{ textAlign: "center" }}>
                        <b><h2 style={{ color: "red", fontWeight: "700" }}>{t("ErrorDialog")}</h2></b>
                    </Col>
                </Row >
                <Row className="mt-3">
                    <Col xl="12" style={{ textAlign: "center" }}>
                        <b><h5 style={{ color: "rgb(242 60 76)", fontWeight: "700" }}>{t("It has been a") + " " + t(jsonErrorMessage[datosError?.code])}</h5></b>
                    </Col>
                </Row>
                <Row className="mb-3">
                    <Col xl="12" style={{ textAlign: "center" }}>
                        <strong>{t(datosError?.message)}</strong>
                    </Col>
                </Row>
                <Row>
                    <Col xl="12" style={{ textAlign: "center" }}>
                        <Button onClick={cambiarStatus} color="danger" className="mt-3">{t("accept")}</Button>
                    </Col>
                </Row>
            </div>
        </Modal>
    );
};
export default ModalErrrorMessage;
