import PropTypes from 'prop-types'
import React, { useEffect } from "react"

import { Row, Col, Alert, Container } from "reactstrap"

// Redux
import { connect } from "react-redux"
import { withRouter, Link } from "react-router-dom"

// availity-reactstrap-validation
import { AvForm, AvField } from "availity-reactstrap-validation"

// actions
import { loginUser, apiError, socialLogin } from "../../store/actions"

// import images
import logo from "../../assets/images/logo3.jpg"
import logocontigo from "../../assets/images/login/Banesco_Logo.png"

//i18n
import { useTranslation } from "react-i18next"

const Login = (props) => {

  const { t, i18n } = useTranslation();
  // or const [t, i18n] = useTranslation();

  useEffect(() => {
    document.body.className = "login-background";
    // remove classname when component will unmount
    return function cleanup() {
      document.body.className = "";
    };
  });
  // handleValidSubmit
  const handleValidSubmit = (event, values) => {
    props.loginUser(values, props.history)
  }

  return (
    <React.Fragment>
      <div className="account-pages my-5 pt-sm-5">
        <Container>
          <Row className="justify-content-center">
            <Col md={8} lg={6} xl={5}>
              <div className="card overflow-hidden">
                <div className="bg-login text-center">
                  <div className="bg-login-overlay"></div>
                  <div className="position-relative">
                    <h5 className="text-white font-size-20">{t('WelcomeBack')}</h5>
                    {/*<p className="text-white-50 mb-0">Sign in to continue to Qovex.</p>*/}
                    <Link to="/" className="logo logo-admin mt-4">
                      <img src={logo} alt="" height="50" />
                    </Link>
                  </div>
                </div>
                <div className="card-body pt-5">
                  <div className="p-2">
                    <AvForm
                      className="form-horizontal"
                      onValidSubmit={(e, v) => {
                        handleValidSubmit(e, v)
                      }}
                    >
                      {props.error && typeof props.error === "string" ? (
                        <Alert color="danger">{props.error}</Alert>
                      ) : null}

                      <div className="mb-3">
                        <AvField
                          name="email"
                          label={t('UserName')}
                          value=""
                          className="form-control"
                          placeholder={t('EnterUserName')}
                          type="text"                          
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>

                      <div className="mb-3">
                        <AvField
                          name="password"
                          label={t('Password')}
                          value=""
                          type="password"
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>

                      <div className="form-check">
                        <input
                          type="checkbox"
                          className="form-check-input"
                          id="customControlInline"
                        />
                        <label
                          className="form-check-label"
                          htmlFor="customControlInline"
                        >
                          {t('RememberMe')}
                        </label>
                      </div>

                      <div className="mt-3">
                        <button
                          className="btn btn-success w-100 waves-effect waves-light"
                          type="submit"
                        >
                          {t('LogIn')}
                        </button>
                      </div>

                      <div className="mt-4 text-center">
                        {/*<Link to="/forgot-password" className="text-muted"><i className="mdi mdi-lock me-1"></i> {t('ForgotPassword')}?</Link>*/}
                      </div>
                    </AvForm>


                    <div class="logo center">
                      <a href="/" title="Inicio" rel="home">
                        <img class="sizeImgLogo" src={logocontigo} alt="Banesco"/>
                      </a>
                    </div>

                  </div>



                </div>
              </div>
              <div className="mt-5 text-center">
                {/*<p>Don't have an account ? <Link to="/register" className="fw-medium text-primary"> Signup now </Link> </p>*/}
                <p className="text-footer">© {new Date().getFullYear()} Banesco </p>
              </div>
            </Col>
          </Row>

        </Container>
      </div>
    </React.Fragment>
  )
}

const mapStateToProps = state => {
  const { error } = state.Login
  return { error }
}

export default withRouter(
  connect(mapStateToProps, { loginUser, apiError, socialLogin })(Login)
)

Login.propTypes = {
  error: PropTypes.any,
  history: PropTypes.object,
  loginUser: PropTypes.func,
  socialLogin: PropTypes.func
}
