import React, { useEffect, useState, useRef } from "react"
import { translationHelpers } from "../../../helpers"
import { useLocation, useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"
import { withTranslation } from "react-i18next"
import {
  Row,
  Col,
  Button
} from "reactstrap"


//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import { GeneralSection } from "../../../components/PropuestaCreditoComercial"
import Analisis from "./riesgoCredito/Analisis"
import FormConclusiones from "./riesgoCredito/Conclusiones"
import { BpmServices } from "../../../services"
import SweetAlert from "react-bootstrap-sweetalert"
import RiesgoCreditoContext from "./RiesgoCreditoContext"



const RiesgoCreditoDetalle = (props) => {

  const history = useHistory();
  const location = useLocation()
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [context, setContext] = useState({});

  const [n, t, c] = translationHelpers('navigation', 'commercial_credit', 'common');

  // Component Refs
  const formConclusionesRef = useRef();

  useEffect(() => {

    if (!location.data) {
      location.data = {
        "requestId": "XYZ892022",
        "facilityId": "",
        "customerId": "68",
        "transactionId": "110",
        "processId": "6",
        "activityId": "0",
        "instanceId": 9104,
        "taskId": 456,
        "taskStatus": "InProgress"
      }
    }

    // update context
    console.log('context', location.data);
    setContext({
      transactionId: location.data.transactionId,
      customerId: location.data.customerId,
      instanceId: location.data.instanceId,
      requestId: location.data.requestId,
    });

  }, []);


  function handleSubmit() {
    formConclusionesRef.current.submit();
  }

  function handleSave() {
    handleReturn();
  }

  function handleReturn() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                                      Avanzamos el proceso                                      */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BpmServices();
    let taskId = "";
    api.completedStatusTask(taskId).then(resp => {
      if (resp !== undefined) {
        setsuccessSave_dlg(true);
      }
      else {
        //Mensaje ERROR
        seterror_msg(props.t("ErrorSaveMessage"))
        seterror_dlg(true)
      }
    }).catch(err => {
      console.log(err);
    })
  }

  function handleClose() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BpmServices();
    // let instanceId = location.data.instanceId
    let instanceId = 0
    api.abortProcess(instanceId).then((data) => {
      if (data !== undefined) {
        history.push(url.URL_DASHBOARD);
      }
      else {
        //Mensaje ERROR
        seterror_msg(props.t("TheProcessCouldNotFinish"))
        seterror_dlg(true)
      }
    });
  }

  //"statuscredito"""

  return (

    <div className="page-content">

      <Breadcrumbs title={n("Commercial Credit")} breadcrumbItem={n("Credit Risk")} />

      <RiesgoCreditoContext.Provider value={context}>

        <Row>
          <Col xl="12">
            <Analisis />
          </Col>
        </Row>

        <Row>
          <Col xl="12">
            <FormConclusiones ref={formConclusionesRef} onSaveSuccess={handleSave} />
          </Col>
        </Row>

        <Row className="my-2">
          <Col xl="12 text-end">
            <Button id="btnReturn" color="dark" type="button" style={{ margin: '5px' }} onClick={handleClose}>
              <i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i>
              {" "} {c("Close")}
            </Button>
            <Button id="btnSave" color="success" type="button" style={{ margin: '5px' }} onClick={handleSubmit}>
              <i className="mdi mdi-content-save mdi-12px"></i>
              {" "} {c("Save")}
            </Button>
          </Col>
        </Row>

      {successSave_dlg ? (
        <SweetAlert
          success
          title={props.t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)

            history.push(url.URL_DASHBOARD);
          }}
        >
          {props.t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={props.t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}

      </RiesgoCreditoContext.Provider>

    </div>
  );

}


export default (withTranslation()(RiesgoCreditoDetalle));
