import React, { useState } from "react"
import PropTypes from 'prop-types';
import { useLocation,useHistory } from 'react-router-dom'
import * as OPTs from "../../../helpers/options_helper"
import * as url from "../../../helpers/url_helper"

import {
  Row,
  Col,
  Card,
  CardBody,
  Label,
  CardFooter,
  Button,
  Table,
  CardHeader,
} from "reactstrap"

import Alert from 'react-bootstrap/Alert'

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
//i18n
import { useTranslation } from "react-i18next";
//Import SweetAlert
import SweetAlert from "react-bootstrap-sweetalert";
import { Search,ToolkitProvider } from 'react-bootstrap-table2-toolkit';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import LoadingOverlay from 'react-loading-overlay';
import AttachmentFileCore from "../../../components/Common/AttachmentFileCore";
import ClientForm from "./ClientForm";
import ModalEndProcess from "../../../components/EndProcess/ModalEndProcess";

import {BackendServices,CoreServices,BpmServices,} from "../../../services";
import ApiService from "../../../services/ApiService";

const PreRequest = props => {
  
  const { t, i18n } = useTranslation();

  const history = useHistory();
  const location = useLocation();

  //Data que recibimos en el location
  const [locationData, setLocationData] = useState(undefined);     
  const [isActiveLoading, setIsActiveLoading] = useState(false);
  const [openNewClient, setopenNewClient] = useState(false);

  //Datos de la Tabla de Clientes
  const [dataClientList, setdataClientList ] = useState([]);
  //Cliente seleccionado
  const [selectedClient,setselectedClient]= useState(undefined);     

  const [identificationTypeList, setidentificationTypeList] = useState([]);  
  const [personTypeList, setpersonTypeList] = useState([]);

  //mostrar el modal para la opci√≥n de finalizar proceso
  const [displayModalEndProcess, setdisplayModalEndProcess] = useState(false);

  const [msgData, setmsgData] = useState({show:false,msg:"",isError:false});
        
  //Servicios
  const backendServices = new BackendServices();
  const bpmServices = new BpmServices();
    
  //On Mounting (componentDidMount)
  React.useEffect(() => {
    if(location.data!==null && location.data!==undefined){
      if(location.data.transactionId.length <= 0){
        location.data.transactionId=location.data.instanceId;
      }
      setLocationData(location.data);
      fetchData(location.data);    
    }    
  }, []);

    //Caraga Inicial de datos
    function fetchData(locationData) {

      /*
      //chequear si la tarea no ha sido iniciada
      if(locationData.taskStatus === "Ready"){        
        //Iniciamos la nueva tarea en BPM   
        bpmServices.startedStatusTask(locationData.taskId)
        .then((iniresult) => {                       
          if(iniresult===undefined){
            history.push(url.URL_DASHBOARD);
          }                
        })
      }
      */
        checkAndCreateProcedure(locationData);
        loadIdentificationTypes();
        loadPersonTypes();
        getProcessClients(locationData.transactionId);
    } 
    
    //CHEQUEAMOS Y GENERAMOS EL PROCESO EN BASE DE DATOS
    function checkAndCreateProcedure(locationData){
      backendServices.nuevoTramite(locationData.transactionId,locationData.instanceId,OPTs.PROCESS_BUSQUEDADESCARTE,OPTs.PROCESS_BUSQUEDADESCARTE,"admin","0")
        .then((data) => {
            if (data !== null && data !== undefined) {
                return;
            }          
      }).catch((error) => { });    
    }
    
    //cargar lista de tipo de identificacion
    function loadIdentificationTypes(){
      backendServices.consultarCatalogoTipoIdentificacion()
        .then((data) => {
            if (data !== null && data !== undefined) {
                let json = [];
                for (let i = 0; i < data.length; i++) {
                  json.push({ label: t(data[i]["description"]), value: data[i]["id"] })
                }
                setidentificationTypeList(json);
            }          
      }).catch((error) => { });    
    }
    //cargar lista de tipo de personas
    function loadPersonTypes(){
      backendServices.consultarCatalogoTipoPersona()
        .then((data) => {
            if (data !== null && data !== undefined) {
                let json = [];
                for (let i = 0; i < data.length; i++) {
                  json.push({ label: t(data[i]["label"]), value: data[i]["code"] })
                }
                setpersonTypeList(json);                
            }          
      }).catch((error) => { });    
    } 
    //se buscan todos los clientes de este proceso 
    function getProcessClients(transactionId){      
      backendServices.consultarOrquestadoListaPersonaSolicitud(transactionId)
        .then((data) => {
          if (data !== null && data !== undefined) {            
            setdataClientList(data)

            if(data.length<=0){
              onOpenNewClient();
            }
          }         
      }).catch((error) => { }); 
    }
    //Abrir formulario Persona
    function onOpenNewClient(show=true){
      setopenNewClient(show)
    }
    //Editar Persona
    function onEditClient(dataClient){
      setselectedClient(dataClient)
      onOpenNewClient();      
    }
    //Eliminar Persona
    function onDeleteClient(dataClient){
      //eliminarOrquestadoListaPersonaSolicitud
      backendServices.eliminarOrquestadoListaPersonaSolicitud(dataClient)
        .then((data) => {
          getProcessClients(dataClient.transactId);    
      }).catch((error) => { }); 
    }    
    //retorna el Tipo de Persona
    function getPersnoType(pType){
      return personTypeList.find(x => x.value === pType);
    }
    //retorna el Tipo de identificacion
    function getIdentificationType(idType){
      return identificationTypeList.find(x => x.value === idType);
    }
    //mostrar mensaje 
    function showMessage(message,isError=false){    
     
      setmsgData({show:true,msg:message,isError:isError})   
    } 

    ////////// PARTE ES LA FINAL DEL PROCESO ///////
    //Modal Para mostrar Comentario de Fin de Proceso
    function showModalEndProcess(show=true) {
      setdisplayModalEndProcess(show);
    }  
    //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
    function onSaveProcess(option){
    
    if(option === OPTs.OPT_PREFINALIZARPROCESO){
      showModalEndProcess();
      return;
    }
    
    var person = null;
    for (var i = 0; i < dataClientList.length; i++) {
      var blacklist = dataClientList[i].blacklist.find((bklist) => {
        return bklist.backList === true;
      })
      if(blacklist!==undefined && blacklist!==null){
        person = dataClientList[i];
        break;
      }
    }

    if(person!==null && option===OPTs.OPT_LISTAEXCLUSION){
      option = OPTs.OPT_ENVIARCUMPLIMIENTO;
    }
    console.log("Option",option);
    saveJBPMProcess(option);
  }

  function saveJBPMProcess(option){ 
    switch(option){
      case OPTs.OPT_FINALIZARPROCESO: {        
        bpmServices.abortProcess(locationData.instanceId)
          .then((data) => {
            if(data!==undefined){              
              history.push(url.URL_DASHBOARD);          
              }
            else{
              //Mensaje ERROR
              showMessage(t("TheProcessCouldNotFinish"),true);
            }
        });
        break;
      }
      case OPTs.OPT_SALVAPARCIAL: {
        var values = {
          "processId":OPTs.PROCESS_BUSQUEDADESCARTE.toString(),
          "activityId":OPTs.ACT_NONE.toString(),                    
          "transactionId":locationData.transactionId,
          "customerId":"",                   
          "applicationNumber":"",
          "procedureNumber":"" ,
          //"decision":"no"          
      };
      bpmServices.updatevariables(locationData.instanceId,values)
          .then((data) => {
            if(data!==undefined){              
              showMessage(t("TheProcessCouldNotFinish"));
            }
            else{
              //Mensaje ERROR
              showMessage(t("ErrorSaveMessage"),true);
            }
        });
        break;
      }
      case OPTs.OPT_ENVIARCUMPLIMIENTO: {
        var values = {
          "processId":OPTs.PROCESS_CUMPLIMIENTO.toString(),
          "activityId":OPTs.ACT_NONE.toString(),                    
          "transactionId":locationData.transactionId,
          "customerId":"",                      
          "applicationNumber":"",
          "procedureNumber":"" ,
          "decision":"si"          
      };
      bpmServices.completedStatusTask(locationData.taskId,values)
          .then((data) => {
            if(data!==undefined){  
              history.push(url.URL_DASHBOARD);              
              showMessage(t("TheProcessCouldNotFinish"));
            }
            else{
              //Mensaje ERROR
              showMessage(t("ErrorSaveMessage"),true);
            }
        });
        break;
      }
      case OPTs.OPT_LISTAEXCLUSION: {
        var values = {
          "processId":OPTs.PROCESS_LISTAEXCLUSION.toString(),
          "activityId":OPTs.ACT_NONE.toString(),                    
          "transactionId":locationData.transactionId,
          "customerId":"",                      
          "applicationNumber":"",
          "procedureNumber":"" ,
          "decision":"no"          
      };
      bpmServices.completedStatusTask(locationData.taskId,values)
          .then((data) => {
            if(data!==undefined){  
              history.push(url.URL_DASHBOARD);              
              showMessage(t("TheProcessCouldNotFinish"));
            }
            else{
              //Mensaje ERROR
              showMessage(t("ErrorSaveMessage"),true);
            }
        });
        break;
      }      
    }
  }

  //salvar comentario de end process
  function onSaveEndProcess(values){
    
    //Salvar los comentarios en Bitacora    
    //selectedData.endProcessComment = values.txtComment;
    //UpdateDataModel(selectedData);
    onSaveProcess(OPTs.OPT_FINALIZARPROCESO);
  }


  return (
     <div className="page-content">
        
        <Breadcrumbs title={t("CommercialCredit")} breadcrumbItem={t("PreRequest")} />

        <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>
        
        <Card>
          <CardHeader>
          <h4 className="card-title">{t("PreRequest")} <br/> {t("Process") + ": #" + locationData?.instanceId}</h4>
            <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
            
          </CardHeader>
            <CardBody>
            

        {openNewClient?
        <Row>
          <Col md="12">

            <ClientForm transactId={locationData.transactionId} clientSelected={selectedClient} onCancel={()=>{onOpenNewClient(false);getProcessClients(locationData.transactionId)}}></ClientForm>

          </Col>
        </Row>
        :
        <React.Fragment>
          <Row>
            <Col md="12">
              <Link className="float-end" onClick={()=>{setselectedClient(undefined); onOpenNewClient()}}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
            </Col>
              </Row>          
            <Row>
          <Col md="12">
          <div className="table-responsive">
          <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("PersonType")}</th>
                  <th>{t("FullName")}</th>
                  <th>{t("IdentificationType")}</th>
                  <th>{t("IdentificationNumber")}</th>
                  <th>{t("Roles")}</th>
                  <th>{t("WatchList")}</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
              {dataClientList.map((item, index) => (
                  <tr>
                    <td>{getPersnoType(Number(item.personType)).label}</td>
                    <td>{Number(item.personType)>1?(item.name):(item.name + " " + item.secondName + " " + item.lastName + " " + item.secondSurname)}</td>
                    <td>{getIdentificationType(item.idType).label}</td>
                    <td>{item.clientDocumentId}</td>
                    <td>
                      {item.roles!==undefined?
                      item.roles.map((rol, index) => (
                        rol.roleDescription + " - "
                      )):null}
                    </td>
                    <td>
                      {item.blacklist!==undefined?
                        item.blacklist.map((black, index) => (
                          black.backList===true?"Aplica":"No Aplica"
                      )):null}
                    </td> 
                    <td style={{ textAlign: "right" }}>
                      <Link to="#" onClick={(e) => { onEditClient(item) }}><i className="mdi mdi-border-color mdi-24px"></i></Link>
                      <Link to="#" onClick={(e) => { onDeleteClient(item) }}><i className="mdi mdi-trash-can-outline mdi-24px"></i></Link>
                    </td>                   
                  </tr>                   
                  ))}
              </tbody>
            </Table>
          </div>
           
          </Col>
        </Row>
        <Row>
            <Col md="12">            
            <Alert show={msgData.show} variant={msgData.isError?"danger":"success"} dismissible onClose={() =>{ console.log("onClose"); setmsgData({show:false,msg:"",isError:false})}}>
              {msgData.msg}
            </Alert>
            </Col>   
             </Row>  
        </React.Fragment>
        }
            </CardBody>

            {!openNewClient?
            <CardFooter style={{textAlign:"right"}}>  
                  <Button color="danger" type="button" style={{ margin: '5px' }} onClick={() => { showModalEndProcess() }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {t("Exit")}</Button>
                  <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(OPTs.OPT_LISTAEXCLUSION) }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {t("Advance")}</Button>
              </CardFooter>
            :null}
        </Card>       
      </LoadingOverlay>       

      <ModalEndProcess onSaveEndProcess={ onSaveEndProcess } isOpen={ displayModalEndProcess } toggle={ () => { showModalEndProcess(false) } } />        

      </div>      
    
  )
}

export default PreRequest
