  //https://qa.api.ob.banesco.com.pa/party/v1/parties/credits?PartyId=600088018  
  //===> Obtenemos todas las lineas y tomamos el total + 1 => Mas serial


  async newline(partyId, facilityType) {
    if (partyId.length <= 0) {
      return undefined;
    } // Garantia loans/Colateral
    //https://qa.api.ob.banesco.com.pa/acctloan/v1/loans/collaterals
    // Desembolso
    //https://qa.api.ob.banesco.com.pa/acctloan/v1/loans
    //lineas
    //https://qa.api.ob.banesco.com.pa/acctloan/v1/loans/credits/lines/limits


    var data = {
      "CreditLine": {
        "Activity": {
          "Cod": "1413" //-> Cod. Actividad Economica

        },
        "AuthType": "99",
        //-> ??
        "AutonomyCode": {
          "Cod": "9999" //-> Codigo de la Autonomia No lo tenemos

        },
        "AutonomyUser": "1",
        //-> Usuario de Autonomia que esta login
        "CreditLimit": {
          "AdviseAmt": {
            "Amt": "25,000.00",
            //-> Monto de La Facilidad
            "CurCode": "USD" //-> Codigo Moneda

          },
          "Allow": {
            "ProductData": {
              "Product": {
                "Cod": "1000" // -> Tipo de Facilidad

              }
            }
          },
          "AllowNetting": "N",
          // -> ??
          "ApprovalDt": "2022-02-02",
          // -> Fecha de Aprobacion
          "AvailDt": "2022-02-02",
          // -> Fecha de Propuesta
          "AvailMarker": "Y",
          // -> ??
          "CountryRisk": [// -> Pais de riesgo
          {
            "Country": {
              "CountryCode": "PA" // -> Codigo Pais de riesgo

            },
            "Percent": "100" // -> ??

          }],
          "CreditLimitKey": {
            "CreditLimitId": "600197109.0011000.01" // -> numero de cliente + tipo de linea + mas serial

          },
          "CurCode": "USD",
          //-> Tipo de Moneda
          "DeadLineDt": "2022-02-02",
          //Fecha del dia
          "DueDt": "2023-02-02",
          //Fecha de expiracion
          "LimitAmt": {
            "Amt": "25,000.00" //-> Monto de La Linea

          },
          "LimitAmtType": "FIXED",
          //-> FIXED/VARIABLE
          "LimitReject": "Y",
          // -> YES/NO ??
          "MaxCurAmt": {
            "Amt": "25,000.00" //-> Monto de La Linea

          },
          "ProposalDt": "2022-02-02",
          //Fecha Propuesta
          "ReviewDt": "2023-02-02",
          //Fecha Revision
          "ReviewFreq": {
            "FreqValue": "20230330M1230" // YYYY MM DD ... codigo A/M/D ... cantidad de meses, el dia especifico

          },
          "Signatories": {
            "Signatory": [{
              "PartyKey": {
                "PartyId": "45469" //Numero del Cliente T24

              }
            }]
          }
        }
      }
    };
    var result = await this.put(url.URL_CORE_LINES, data);

    if (result.Status.StatusCode !== OPTs.ResponseT24_STATUSOK) {
      return undefined;
    }

    return result;
  }

}

