import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation, useHistory } from "react-router-dom"
import * as url from "../../../../../helpers/url_helper"
import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  Table,
  CardFooter
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

import ModalGobiernoCorporativo from "./ModalGobiernoCorporativo"
import { BackendServices } from "../../../../../services"
import SweetAlert from "react-bootstrap-sweetalert"

const GobiernoCorporativo = React.forwardRef((props, ref) => {
  const history = useHistory();
  const { t, i18n } = useTranslation();
  const [botonValidation, setbotonValidation] = useState(true);
  const location = useLocation()
  const [dataLocation, setData] = useState(location.data);
  const [showModelAttachment, setShowModelAttachment] = useState(false);
  const [dataAccionistasDataRows, setdataAccionistasDataRows] = useState(null);
  const [dataReturn, setDataReturn] = useState(props.jsonCorporativo);
  const [dataDelete, setDataDelete] = useState([]);
  const [jsonCorporativo, setjsonCorporativo] = useState(props.jsonCorporativo);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [tipo, settipo] = useState("")
  const [formValid, setFormValid] = useState(false);

  React.useImperativeHandle(ref, () => (
    {
      validateForm: () => {
        const form = document.getElementById('frmGobiernoCorporativo');
        form.requestSubmit();
      },
      getFormValidation: () => {
        return formValid;
      },
    }));
  React.useEffect(() => {
    // Read Api Service data
    initializeData();
  }, []);
  function addGobierno(values, tipo2) {
    // console.log(values);
    dataReturn.name = values.name;
    dataReturn.position = values.position;
    dataReturn.birthDate = values.birthDate;
    dataReturn.transactId = dataLocation.transactionId;
    setDataReturn(dataReturn);
    let datos = {
      transactId: Number(dataLocation.transactionId),
      name: values.name,
      position: values.position,
      birthDate: values.birthDate
    }
    const apiBack = new BackendServices();
    if (tipo2 == "guardar") {
      apiBack.nuevoGobiernoCorporativo(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      datos.corporateIdentification = jsonCorporativo.corporateIdentification;
      datos.age = jsonCorporativo.age;
      apiBack.actualizarGobiernoCorporativo(datos).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModelAttachment();
        } else {
          toggleShowModelAttachment();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  function calcular(fecha) {
    var today = new Date();
    var yyyy = today.getFullYear();
    var today2 = new Date(fecha);
    var yyyy2 = today2.getFullYear();
    return (parseInt(yyyy)-parseInt(yyyy2));
  }
  function initializeData() {
    const api = new BackendServices()
    api.consultarGobiernoCorpBD(dataLocation.transactionId).then(resp => {
      if (resp.length > 0) {
        setdataAccionistasDataRows(resp.map((data) => (
          <tr key={data.personId}>
            <td>{data.name + " " + data.secondName + " " + data.lastName + " " + data.secondSurname}</td>
            <td>{data.position}</td>
            <td>{data.birthDate}</td>
            <td>{calcular(data.birthDate)}</td>
            <td style={{ textAlign: "right" }}>
              <Button type="button" color="link" onClick={(resp) => { editarDatosAccionista(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              {/* <Button type="button" color="link" onClick={(resp) => { eliminarAccionista(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button> */}
            </td>
          </tr>)
        ));
      } else {
        setdataAccionistasDataRows(
          <tr key={1}>
            <td colSpan="5" style={{ textAlign: 'center' }}><h5>{t("NoData")}</h5></td>
          </tr>);
      }
    })
  }
  function verDatosAccionista(data) {
    setjsonCorporativo(data)
    setbotonValidation(false);
    toggleShowModelAttachment()
  }
  function editarDatosAccionista(data) {
    settipo("editar")
    setjsonCorporativo(data)
    setbotonValidation(true);
    toggleShowModelAttachment()
  }
  function eliminarAccionista(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  //abrimos modal para adjunar archivos
  function toggleShowModelAttachment() {
    setShowModelAttachment(!showModelAttachment);
    removeBodyCss()
  }

  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    //props.onSubmit(values);
  }


  return (
    <React.Fragment>
      <h5>
        {t("CorporateGovernance")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmGobiernoCorporativo" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">

            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th>{t("Name")}</th>
                  <th>{t("Charge")}</th>
                  <th>{t("DBO")}</th>
                  <th>{t("Age")}</th>
                  <th></th>
                  {/* <th><Link className="float-end" onClick={() => {
                    setbotonValidation(true); setjsonCorporativo({
                      transactId: 0,
                      corporateIdentification: 0,
                      name: null,
                      position: null,
                      birthDate: null,
                      age: null,
                      status: true
                    }); settipo("guardar"); toggleShowModelAttachment();
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
                  </th> */}
                </tr>
              </thead>
              <tbody>
                {dataAccionistasDataRows}
              </tbody>
            </Table>
          </Col>
        </Row>
      </AvForm>
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            apiBack.eliminarGobiernoCoporativo({ transactId: dataDelete.transactId, corporateIdentification: dataDelete.corporateIdentification }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
      <ModalGobiernoCorporativo tipo={tipo} jsonCorporativo={jsonCorporativo} botones={botonValidation} addGobierno={addGobierno} isOpen={showModelAttachment} toggle={() => { toggleShowModelAttachment() }} />
    </React.Fragment>
  );

});

/*
GobiernoCorporativo.propTypes = {
  onSubmit: PropTypes.func.isRequired,
}
*/

export default GobiernoCorporativo;
