import PropTypes from 'prop-types';

import {
  Card,
  CardBody,
  Row,
  Col,
  Label
} from "reactstrap"

import { AvField, AvGroup, AvInput } from "availity-reactstrap-validation"
import AttachmentList from '../../../../components/Attachments/AttachmentList';


//i18n
import { useTranslation } from 'react-i18next'

const SimpleChekQuestion = props => {
  return (
    <AvGroup check className="my-2">
      <AvInput id={ props.name } type="checkbox" name={ props.name } />
      <Label check htmlFor={ props.name }> { props.question }</Label>
    </AvGroup>
  );
};

SimpleChekQuestion.propTypes = {
  name: PropTypes.string,
  question: PropTypes.string
};

const ComposedQuestion = props => {
  return (
    <AvGroup check className="my-2">
      <AvInput id={ props.name } type="checkbox" name={ props.name } />
      <Label check htmlFor={ props.name }> { props.question }</Label>
      <AvInput type="textarea" name={ props.name + 'Comments' } />
    </AvGroup>
  );
};

ComposedQuestion.propTypes = {
  name: PropTypes.string,
  question: PropTypes.string
};

const FormAspectosAmbientales = props => {

  
  const { t, i18n } = useTranslation();


  const simpleQuestions = [
    { name: 'check1', question: '¿El proyecto está ubicado o es colindante a un área natural protegida?' },
    { name: 'check2', question: '¿El proyecto involucra reasentamiento físico y/o económico de más de 100 personas?' },
    { name: 'check3', question: '¿Se necesita la presentación de permisos ambientales para la ejecución del proyecto?' },
    { name: 'check4', question: '¿El proyecto es nuevo e involucra el uso de más de 35 Hectáreas de terreno?' },
    { name: 'check5', question: '¿La actividad emplea más de 50 trabajadores y manejan más de 15 contratistas?' },
  ];

  const composedQuestions = [
    { name: 'check6', question: 'Require de licencia o permiso ambiental para el desarrollo del proyecto o actividad' },
    { name: 'check7', question: 'El proyecto se encuentra cerca de un área natural protegida' },
    { name: 'check8', question: 'El proyecto se encuentra en una zona de pueblos indígenas' },
    { name: 'check9', question: 'El proyecto cuenta con una persona que se ocupa en la GAS' },
    { name: 'check10', question: 'El proyecto cumple con toda la regulación laboral aplicable' },
  ];

  return (
    <Card>

      <CardBody>
        <h4 className="card-title">{ t("EnvironmentalAspects") }</h4>
        <p className="card-title-desc"></p>

        <Row className="my-2">
          <Col md="4">

            <AvGroup className="mb-3">
              <Label htmlFor="preclassification">{ t("PreClassificationRisk") }</Label>
              <AvField
                className="form-control"
                name="preclassification"
                type="text"
                id="preclassification"
                value="Medio" disabled={true}></AvField>
            </AvGroup>
            </Col>
            <Col md="4">
            <AvGroup className="mb-3">
              <Label htmlFor="clasificacionRiesgoSostenible">{ t("EnvironmentalRiskclassification") }</Label>
              <AvField
                className="form-control"
                name="clasificacionRiesgoSostenible"
                type="select"
                id="clasificacionRiesgoSostenible"
                value="MedioAlto">
                <option value="Bajo">Bajo</option>
                <option value="Medio">Medio</option>
                <option value="MedioAlto">Medio Alto</option>
                <option value="Alto">Alto</option>
              </AvField>
            </AvGroup>
            </Col>
            <Col md="12">
            
            { simpleQuestions.map(item => (
                <SimpleChekQuestion key={ item.name } name={ item.name } question={ item.question } />
              ))
            }

{/*
            { composedQuestions.map(item => (
              <ComposedQuestion key={ item.name } name={ item.name } question={ item.question } />
            ))}
 */}
          </Col>

        </Row>

        <Row className="my-4">
          <Col md="12">
            <AttachmentList editMode={ true } attachments={[]} />
          </Col>
        </Row>

      </CardBody>

    </Card>
  );
};


FormAspectosAmbientales.propTypes = {
};

export default FormAspectosAmbientales;
