                  />
                  {'   '}
                  {/* <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label> */}
                </AvGroup>
              </div>
            </Col>
            <Col md="2">
              <div className="mb-3">
                <Label>{t("Allow compensation")}</Label>
                <AvGroup check className="mb-3">
                  <Switch name="isProjectLocatedProtectedNaturalArea"
                    uncheckedIcon={<Offsymbol />}
                    checkedIcon={<OnSymbol />}
                    onColor="#007943"
                    id="check1"
                    className="form-label"
                    onChange={() => {
                      setswitch1(!switch1);
                    }}
                    checked={switch1}
                  />
                  {'   '}
                  {/* <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label> */}
                </AvGroup>
              </div>
            </Col>
            <Col md="2">
              <div className="mb-3">
                <Label>{t("Reject limit")}</Label>
                <AvGroup check className="mb-3">
                  <Switch name="isProjectLocatedProtectedNaturalArea"
                    uncheckedIcon={<Offsymbol />}
                    checkedIcon={<OnSymbol />}
                    onColor="#007943"
                    id="check1"
                    className="form-label"
                    onChange={() => {
                      setswitch2(!switch2);
                    }}
                    checked={switch2}
                  />
                  {'   '}
                  {/* <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label> */}
                </AvGroup>
              </div>
            </Col>
            <Col md="2">
              <div className="mb-3">
                <Label>{t("Accounts")}</Label>
                <AvGroup check className="mb-3">
                  <Switch name="isProjectLocatedProtectedNaturalArea"
                    uncheckedIcon={<Offsymbol />}
                    checkedIcon={<OnSymbol />}
                    onColor="#007943"
                    id="check1"
                    className="form-label"
                    onChange={() => {
                      setswitch3(!switch3);
                    }}
                    checked={switch3}
                  />
                  {'   '}
                  {/* <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label> */}
                </AvGroup>
              </div>
            </Col>
            <Col md="6" className="table-responsive styled-table-div">
              <span>{t("Review Frequency")}</span>
              <Table className="table table-striped table-hover styled-table table" >
                <thead>
                  <tr>
                    <th>{t("Date")}</th>
                    <th>{t("Type")}</th>
                    <th>{t("Quantity")}</th>
                    <th>{t("Day")}</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>

                      <Flatpickr
                        id="freqDate"
                        name="freqDate"
                        className="form-control d-block"
                        placeholder={OPTs.FORMAT_DATE_SHOW}
                        options={{
                          dateFormat: OPTs.FORMAT_DATE,
                          //maxDate: new Date().setFullYear(new Date().getFullYear() - 18),
                          defaultDate: moment().format("DD-MM-YYYY")
                        }}
                        onChange={(selectedDates, dateStr, instance) => { setfechaSet6(moment(dateStr, "DD/MM/YYYY").format("YYYY-MM-DD")) }}
                      />
                    </td>
                    <td>
                      <Select noOptionsMessage={() => ""}
                        showSearch
                        style={{ width: "100%" }}
                        placeholder={t("Search")}
                        optionFilterProp="children"
                        // defaultValue={props.dataCapex1.back}
                        onChange={(e) => { setTipoFrecuencia(e) }}
                        filterOption={(input, option) =>
                          option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                      >
                        <Option value={"A"}>A</Option>
                        <Option value={"M"}>M</Option>
                        <Option value={"D"}>D</Option>
                      </Select>
                    </td>
                    <td>
                      <AvField
                        className="form-control"
                        type="number"
                        name="freqAmount"
                        id="freqAmount"
                      />
                    </td>
                    <td>
                      <AvField
                        className="form-control"
                        type="number"
                        name="freqDay"
                        id="freqDay"
                      />
                    </td>
                  </tr>
                </tbody>
              </Table>
            </Col>
            <Col md="6">
              <Row>
                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="Totmaximo">{t("Total Maximum")}</Label>
                    <AvField
                      className="form-control"
                      type="text"
                      onKeyPress={(e) => { return check(e) }}
                      name="maxTotal"
                      pattern="^[0-9,.]*$"
                      onKeyUp={(e) => { let x = currencyData.getRealValue(e.target.value); e.target.value = currencyData.format(x); }}

                      id="maxTotal"
                    />
                  </div>
                </Col>
                <Col md="6">
                  <div className="mb-3">
                    <Label htmlFor="Autonomia">
                      {t("Autonomy")}
                    </Label>
                    <AvField
                      className="form-control"
                      type="text"
                      name="autonomy"
                      id="autonomy"
                      title={t("")}
                    />
                  </div>
                </Col>

              </Row>
              <Row>
                <Col md="12">
                  <div className="mb-3">
                    <Label htmlFor="additionalInfo">
                      {t("Additional Information")}
                    </Label>
                    <AvField
                      className="form-control"
                      type="textarea"
                      rows="1"
                      name="additionalInfo"
                      id="additionalInfo"
                    />
                  </div>
                </Col>
              </Row>
            </Col>
          </Row>
          <Row>
            <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
              <Link
                to={{
                  pathname: '/creditocomercial/garantias/',
                  data: { facilityId: dataLocation?.facilityId ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
                }}
              >
                <Button
                  id="btnNew"
                  color="danger"
                  type="button"
                  style={{ margin: "5px" }}
                  onClick={props.toggle}
                  data-dismiss="modal"
                >
                  <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                  {t("Cancel")}
                </Button>
              </Link>
              <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
                <i className="mdi mdi-content-save mdi-12px"></i>
                {" "} {typeGarantia == "nuevo" ? t("Save") : t("Edit")}</Button>
            </Col>
          </Row>
        </AvForm>
      </LoadingOverlay>
      {
        successSave_dlg2 ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              setsuccessSave_dlg2(false)
              props?.cerrarModalLinea()
              // window.location.reload();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }
      {
        error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );
};

export default withTranslation()(PantallaBusqueda);
