import ModalEndProcess from "../../../../components/EndProcess/ModalEndProcess"
import { BackendServices, CoreServices, BpmServices, } from "../../../../services";
import ModalPrevicualizarIGR from "./previsualizarIGR";
import SweetAlert from "react-bootstrap-sweetalert"
import LoadingOverlay from "react-loading-overlay";

import { LogProcessModel } from '../../../../models/Common/LogProcessModel';
import LogProcess from "../../../../components/LogProcess/index";
import Currency from "../../../../helpers/currency"

const FormularioIgr = props => {
  let modelo = {
    datosGenerales: {
      codigoTipoIdentificacion: null,
      numeroCliente: null,
      primerNombre: null,
      segundoNombre: null,
      primerApellido: null,
      segundoApellido: null,
      numeroIdentificacion: null,
      transactId: 0,
      economicGroup: {
        code: "",
        name: ""
      },
      economicActivity: {
        code: "",
        name: ""
      },
      subeconomicActivity: {
        code: "",
        name: ""
      },
      bank: {
        code: "",
        name: ""
      },
    },
    datosEmpresa: {
      transactId: "",
      referencePoint: "",
      residency: "",
      houseNumber: "",
      phoneNumber: "",
      mobileNumber: "",
      workNumber: "",
      email: "",
      exclusion: false,
      sustainable: false,
      apto: "",
      country: {
        code: "",
        name: ""
      },
      province: {
        code: "",
        name: ""
      },
      district: {
        code: "",
        name: ""
      },
      township: {
        code: "",
        name: ""
      },
      city: {
        code: "",
        name: ""
      },
      sector: {
        code: "",
        name: ""
      },
      subSector: {
        code: "",
        name: ""
      },
    },
    historiaEmpresa: {
      transactId: 0,
      description: " ",
      employeesNumber: 0,
      details: " "
    },
    dataAccionistas: {
      transactId: 0,
      informacionAccionistaDetails: " ",
      datosTablaAccionistas: null,
      observations: " ",
      description: " ",
    },
    dataGobiernoCorporativo: {
      transactId: 0,
      corporateIdentification: 0,
      name: " ",
      position: " ",
      birthDate: " ",
      age: " ",
      status: true,
      dataTableGobiernoCorporativo: null
