import axios from "axios"
import * as url from "../helpers/url_helper"
import * as opt from "../helpers/options_helper"
import qs from 'qs';

import LocalStorageHelper from "../helpers/LocalStorageHelper";

//const usr = "admin"
//const psw = "BanescoBPM2021."
    
    const localStorageHelper = new LocalStorageHelper();
    //apply base url for axios
    const API_URL = ""
    
    const axiosApi = axios.create({
      baseURL: API_URL,
      headers: {
        "Content-Type": "application/json"
      },
    })
    
    axiosApi.interceptors.request.use(
      (config) => {  
        localStorageHelper.save(opt.VARNAME_USRCREDENTIAL,{usr:usr,psw:psw});      
        config.headers["Authorization"] = "Basic " + btoa(usr + ":" + psw);
        return config;
      },
      (error) => {
        console.log("request error: ", error)
        return Promise.reject(error);
      }
    );
    
export default class ApiServiceCore {

    async get(url, config = {}) {
      return await axiosApi
      .get(url, { ...config })
      .then(response => response.data)
    }

    async post(url, data,config = {}) {
      return axiosApi
        .post(url, { ...data },{ ...config })
        .then(response => response.data)
    }

    async put(url, data, config = {}) {
      return axiosApi
        .put(url, { ...data }, { ...config })
        .then(response => response.data)
    }

    async del(url,data, config = {}) {
      return await axiosApi
        .delete(url,{ data: data}, { ...config })
        .then(response => response.data)
    }
}
