import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types'
import { Link, useLocation, useHistory } from "react-router-dom"
import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Input,
  CardHeader,
  CardTitle,
  InputGroup,
  Table,
  CardFooter
} from "reactstrap"
import { AvForm, AvField } from "availity-reactstrap-validation"
import ModalDeudasCortoPlazo from "./ModalDeudasCortoPlazo"
import ModalDeudasLargoPlazo from "./ModalDeudasLargoPlazo"
import ModalSow from "./ModalSow"
//Import Flatepicker
import moment from "moment";

import "flatpickr/dist/themes/material_blue.css";
import Flatpickr from "react-flatpickr";
import SweetAlert from "react-bootstrap-sweetalert"
import { BackendServices,CoreServices } from "../../../../../services"
import * as url from "../../../../../helpers/url_helper"
import Currency from "../../../../../helpers/currency"
import ShortLongTermDebts from "../../../../../components/Common/ShortLongTermDebts";

const estiloTotales = { backgroundColor: "lightgrey", color: "black" }
const RelacionesBancarias = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const location = useLocation()
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmRelacionesBancaria');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }, dataReturn
  }));
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [dataReturn, setdataReturn] = useState(props.dataRelacionesBancarias);
  const [info_dlg, setinfo_dlg] = useState(false)
  const [info_msg, setinfo_msg] = useState("")
  const [dynamic_title, setdynamic_title] = useState("")
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [success_dlg, setsuccess_dlg] = useState(false)
  const [dynamic_description, setdynamic_description] = useState("")
  /* ---------------------------------------------------------------------------------------------- */
  /*                          Variables de estados generales del componente                         */
  /* ---------------------------------------------------------------------------------------------- */
  const [formValid, setFormValid] = useState(false);
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [showModalDeudasCortoPlazo, setShowModalDeudasCortoPlazo] = useState(false);
  const [showModalDeudasLargoPlazo, setShowModalDeudasLargoPlazo] = useState(false);
  const [showModalSow, setShowModalSow] = useState(false);
  const [shorttermdebtsRows, setshorttermdebtsRows] = useState(null);
  const [longtermdebtsRows, setlongtermdebtsRows] = useState(null);
  const [sowactualRows, setsowactualRows] = useState(null);
  const [sowproposedRows, setsowproposedRows] = useState(null);
  const [tipo, setTipo] = useState("");
  const currencyData = new Currency();
  const [dataSet, setdataSet] = useState({
    transactId: 0,
    otherBanks: 0,
    banesco: 0,
    total: 0,
    sow: 0
  });
  const [locationData, setLocationData] = useState(null);
  const history = useHistory();
  const [datosUsuario, setdatosUsuario] = useState(null);
  const[coreServices,setcoreServices]=useState(new CoreServices())
  const [debtsT24, setdebtsT24] = useState(null);

  React.useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result;
      }
    }
    setdataReturn(props.dataRelacionesBancarias);
    initializeData(dataSession);
  }, [props.activeTab == 14]);



  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    setFormValid(true)
    // props.onSubmit(values);
  }
  function initializeData(dataSession) {

    loadSowNow(dataSession);
    uploadSowProposed(dataSession);
    loadTablesShortTerm(dataSession);
    loadLongTermTables(dataSession);

    const api = new BackendServices()
    api.consultPrincipalDebtor(dataSession.transactionId).then(resp => {
      setdatosUsuario(resp)
      loadDebtsT24(resp.clientDocId);
    })

  }
  function loadSowNow(dataLocation) {
    const api = new BackendServices()
    // consultarSowActualIGR
    api.consultSowActualIGR(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablSowActual = resp.currentSOWDTOList;
      setdataReturn(dataReturn)
      setsowactualRows(resp.currentSOWDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td data-label={t("OtherBank")}>${currencyData.format(data.otherBanks ?? 0)}</td>
            <td data-label={t("Banesco")}>${currencyData.format(data.banesco ?? 0)}</td>
            <td data-label={t("Total")}>${currencyData.format(data.total ?? 0)}</td>
            <td data-label={t("Sow")}>${currencyData.format(data.sow ?? 0)}</td>
            <td data-label={t("Actions")} style={{ textAlign: "right", display: "flex" }}>
              <Button type="button" color="link" onClick={(resp) => { editSowCurrent(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { deleteSowCurrent(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  function uploadSowProposed(dataLocation) {
    const api = new BackendServices()
    // consultarSowPropuestoIGR
    api.consultSowProposedIGR(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablSowPropuesto = resp.proposedSOWDTOList;
      setdataReturn(dataReturn)
      setsowproposedRows(resp.proposedSOWDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td data-label={t("OtherBank")}>${currencyData.format(data.otherBanks ?? 0)}</td>
            <td data-label={t("Banesco")}>${currencyData.format(data.banesco ?? 0)}</td>
            <td data-label={t("Total")}>${currencyData.format(data.total ?? 0)}</td>
            <td data-label={t("Sow")}>${currencyData.format(data.sow ?? 0)}</td>
            <td data-label={t("Actions")} style={{ textAlign: "right", display: "flex" }}>
              <Button type="button" color="link" onClick={(resp) => { editSowProposed(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { removeSowProposed(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  function loadTablesShortTerm(dataLocation) {
    const api = new BackendServices()
    // consultarRelacionesBancariasDeudasCP
    api.consultBankingRelationsDebtsCP(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablDeudaCorto = resp.getBankingRelationCPDTOList;
      dataReturn.sumatoriaDeudaCorto = { monto: 0, saldo1: 0, saldo2: 0, saldo3: 0 }
      setdataReturn(dataReturn);
      for (let i = 0; i < resp.getBankingRelationCPDTOList.length; i++) {
        if (resp.getBankingRelationCPDTOList[i].status) {
          dataReturn.sumatoriaDeudaCorto.monto = parseFloat(dataReturn.sumatoriaDeudaCorto.monto) + parseFloat(resp.getBankingRelationCPDTOList[i].amount);
          dataReturn.sumatoriaDeudaCorto.saldo1 = parseFloat(dataReturn.sumatoriaDeudaCorto.saldo1) + parseFloat(resp.getBankingRelationCPDTOList[i].debitBalance1);
          dataReturn.sumatoriaDeudaCorto.saldo2 = parseFloat(dataReturn.sumatoriaDeudaCorto.saldo2) + parseFloat(resp.getBankingRelationCPDTOList[i].debitBalance2);
          dataReturn.sumatoriaDeudaCorto.saldo3 = parseFloat(dataReturn.sumatoriaDeudaCorto.saldo3) + parseFloat(resp.getBankingRelationCPDTOList[i].debitBalance3);
        }
      }
      setdataReturn(dataReturn)
      setshorttermdebtsRows(resp.getBankingRelationCPDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td data-label={t("Bank")}>{data.bank}</td>
            <td data-label={t("FacilityType")}>{data?.facilityType ?? data?.facilityTypeId}</td>
            <td data-label={t("Amount")}>${currencyData.format(data.amount ?? 0)}</td>
            <td data-label={t("GrantDate")}>{formatDate(data.date)}</td>
            <td data-label={t("DueDate")}>{formatDate(data.expirationDate)}</td>
            <td data-label={t("CurrentYear")}>${currencyData.format(data.debitBalance1 ?? 0)}</td>
            <td data-label={"1 " + t("YearAgo")}>${currencyData.format(data.debitBalance2 ?? 0)}</td>
            <td data-label={"2 " + t("YearAgo")}>${currencyData.format(data.debitBalance3 ?? 0)}</td>
            <td data-label={t("Rate")}>{data.rate}</td>
            <td data-label={t("Bond")}>{data.bail}</td>
            <td data-label={t("Term")}>{data.fee}</td>
            <td data-label={t("DestinationOfFunds")}>{data.fundDestiny}</td>
            <td data-label={t("Actions")} style={{ textAlign: "right", display: "flex" }}>
              <Button type="button" color="link" onClick={(resp) => { editShortTerm(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
              <Button type="button" color="link" onClick={(resp) => { removeShortTerm(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr> : null
      )
      ));
    });
  }
  // function cerrarModal() {
  //   setShowModelAttachment(false);
  //   removeBodyCss()
  // }
  // function abrirModal() {
  //   setShowModelAttachment(true);
  //   removeBodyCss()
  // }
  function loadLongTermTables(dataLocation) {
    const api = new BackendServices()
    // consultarRelacionesBancariasDeudasLP
    api.consultBankRelationsDebtsLP(dataLocation.transactionId).then(resp => {
      dataReturn.dataTablDeudaLargo = resp.bankingRelationLPDTOList;
      dataReturn.sumatoriaDeudaLargo = { monto: 0, saldo1: 0, saldo2: 0, saldo3: 0 }
      setdataReturn(dataReturn);
      for (let i = 0; i < resp.bankingRelationLPDTOList.length; i++) {
        if (resp.bankingRelationLPDTOList[i].status) {
          dataReturn.sumatoriaDeudaLargo.monto = parseFloat(dataReturn.sumatoriaDeudaLargo.monto) + parseFloat(resp.bankingRelationLPDTOList[i].amount);
          dataReturn.sumatoriaDeudaLargo.saldo1 = parseFloat(dataReturn.sumatoriaDeudaLargo.saldo1) + parseFloat(resp.bankingRelationLPDTOList[i].debitBalance1);
          dataReturn.sumatoriaDeudaLargo.saldo2 = parseFloat(dataReturn.sumatoriaDeudaLargo.saldo2) + parseFloat(resp.bankingRelationLPDTOList[i].debitBalance2);
          dataReturn.sumatoriaDeudaLargo.saldo3 = parseFloat(dataReturn.sumatoriaDeudaLargo.saldo3) + parseFloat(resp.bankingRelationLPDTOList[i].debitBalance3);
        }
      }
      setdataReturn(dataReturn)
      setlongtermdebtsRows(resp.bankingRelationLPDTOList.map((data, index) => (
        data.status ?
          <tr key={index}>
            <td>{data.bank}</td>
            <td>{data?.facilityType ?? data?.facilityTypeId}</td>
            <td>${currencyData.format(data.amount ?? 0)}</td>
            <td>{formatDate(data.date)}</td>
            <td>{formatDate(data.expirationDate)}</td>
            <td>${currencyData.format(data.debitBalance1 ?? 0)}</td>
            <td>${currencyData.format(data.debitBalance2 ?? 0)}</td>
            <td>${currencyData.format(data.debitBalance3 ?? 0)}</td>
            <td>{data.rate}</td>
            <td>{data.bail}</td>
            <td>{data.fee}</td>
            <td>{data.fundDestiny}</td>
            <td style={{ textAlign: "right" }}>
