          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "CP") {
      let datos = {
        "facilityType": data?.facilityType ?? data?.facilityTypeId ?? " ",
        "amount": Number(currencyData.getRealValue(data?.amount)) ?? 0,
        "date": data?.date ?? "2021-11-11",
