          initializeData(locationData);
          toggleShowModalSow(false);
        } else {
          setconfirm_alert(false)
          seterror_dlg(true)
        }
      }).catch(err => {
        setconfirm_alert(false)
        seterror_dlg(true)
      });
    }
    if (tipo == "ECP") {
      //console.log("ECP", data);
      let datos = {
        "facilityType": data?.facilityType ?? "",
