                setTipo("CL"); setbotonValidation(true); setdataSet({
                  transactId: 0,
                  bank: null,
                  facilityType: null,
                  amount: 0,
                  date: null,
                  expirationDate: null,
                  debitBalance1: 0,
                  debitBalance2: 0,
                  debitBalance3: 0,
                  rate: 0,
