import PropTypes from 'prop-types'
import { translationHelpers } from '../../../../helpers';


import {
  Modal,
  ModalHeader,
  ModalBody,
  Label,
  Row,
  Col,
  Button
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { useTranslation } from 'react-i18next';
import React, { useState } from 'react';


const ModalNuevaComision = (props) => {
  const { t, i18n } = useTranslation();
  const [sumaParticipacion, setsumaParticipacion] = useState(0);
  const [sumaParticipacionMessage, setsumaParticipacionMessage] = useState(false);


  function handleSubmit(event, errors, value) {
    if (sumaParticipacionMessage) {
      return;
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    props.guardarDatos(value, props.tipo);
    props.toggle();
  }

  function handleCancel() {
    props.toggle();
  }
  React.useEffect(() => {
    // Read Api Service data
    if (props.dataGeneralComisiones != null) {
      setsumaParticipacion(0);
      if (props.tipo == "COMISION") {
        let sumaTotal = 0;
        for (let i = 0; i < props.dataGeneralComisiones.length; i++) {
          sumaTotal = parseFloat(sumaTotal) + parseFloat(props.dataGeneralComisiones[i].amount);
        }
        setsumaParticipacion(sumaTotal);
      } else {
        let sumaTotal = 0;
        for (let i = 0; i < props.dataGeneralComisiones.length; i++) {
          if (props.dataGeneralComisiones[i].commissionId != props.dataSet.commissionId) {
            sumaTotal = sumaTotal + props.dataGeneralComisiones[i].amount;
          }
        }
        setsumaParticipacion(sumaTotal);
      }
    }
  }, [props]);
  function validationParticipation(value) {
    let sumaFinal = parseFloat(value) + parseFloat(sumaParticipacion);
    if (parseFloat(sumaFinal) > parseFloat(props.MontoFacilidad)) {
      setsumaParticipacionMessage(true);
    } else {
      setsumaParticipacionMessage(false)
    }
  }
  function validarMonto(value) {
    if (parseFloat(value) > parseFloat(props.MontoFacilidad)) {
      setsumaParticipacionMessage(true);
    } else {
      setsumaParticipacionMessage(false);
    }
  }
  return (
    <Modal isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}
      size="md">
      <ModalHeader toggle={props.toggle} color="primary">{t("Commission")}</ModalHeader>
      <ModalBody>
        <AvForm id="formNewCommission" className="needs-validation" onSubmit={handleSubmit}>
          <AvGroup className="mb-3">
            <Label htmlFor="commissionType">{t("Commission Type")}</Label>
            <AvField
              className="form-control"
              name="comisionType"
              type="text"
              value={props.dataSet.comisionType?.code}
              id="comisionType"
              validate={{
                required: { value: true, errorMessage: t("Required") },
              }}
              required />
          </AvGroup>
          <AvGroup className="mb-3">
            <Label htmlFor="amount">{t("Amount")}</Label>
            <AvField
              className="form-control"
              name="amount"
              type="text"
              onChange={(e) => { validationParticipation(e.target.value) }}
              value={props.dataSet.amount}
              id="amount"
              validate={{
                required: { value: true, errorMessage: t("Required") },
                number: { value: true, errorMessage: t("InvalidField") },
              }}
              required />
            {sumaParticipacionMessage ?
              <p className="message-error-parrafo">{t("Theamountexceedstheamountofthefacility")}</p>
              : null}
          </AvGroup>
          {/* <AvGroup className="mb-3">
            <Label htmlFor="observations">{t("Percent")} {t("available")} {100 - sumaParticipacion}%</Label>
            <AvField
              className="form-control"
              name="observations"
              type="text"
              onChange={(e) => { validationParticipation(e.target.value) }}
              value={props.dataSet.observations}
              id="observations"
              validate={{
                number: { value: true, errorMessage: t("InvalidField") },
              }} />
            {sumaParticipacionMessage ?
              <p className="message-error-parrafo">{t("Thepercentageexceeds100%")}</p>
              : null}
          </AvGroup> */}
          {
            parseFloat(props.MontoFacilidad)>0?
          <Row className="my-2">
            <Col xl="12 text-end">
              <Button id="btnCancel" color="dark" type="button" style={{ margin: '5px' }} onClick={handleCancel}>
                {t("Cancel")}
              </Button>
              {props.botones ?
                <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                  {" "} {props.tipo == "COMISION" ? t("Save") : t("Edit")}
                </Button> : null}
            </Col>
          </Row>
          :<p className="message-error-parrafo">{t("In order to save a commission you must add the amount of the facility")}</p>}
        </AvForm>
      </ModalBody>
    </Modal>
  )
};

ModalNuevaComision.propTypes = {
  toggle: PropTypes.func,
  isOpen: PropTypes.bool
};


export default ModalNuevaComision;
