        log.observations = observations !== null ? t("ActivityHasBeenFinishedSuccessfully") : observations;
        log.requestId = APPLICATION_STATUS?.id ?? 0;
        log.statusDescription = APPLICATION_STATUS?.name ?? "";
        saveLogProcess(log);
    }


