  return (
   
    <React.Fragment>      
    
    <SearchClientCore onClientSelect={(data)=> {loadClientSelectedData(data)} }/>

    <h5>{t("Client")}</h5>
    <p className="card-title-desc"></p>

    <AvForm id="frmClient" className="needs-validation" onSubmit={handleSubmitFormClient}>
        <Card>
                <CardBody>
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="personType">{t("PersonType")}</Label>
                        <Select
                          onChange={(e) => { setpersonTypeSelected(personTypeList.find(x => x.value === e.value)); handleChangeInputFormClient(e) }}
                          options={personTypeList}
                          classNamePrefix="select2-selection"
                          value={personTypeSelected}
                          name="personType"
                        />
                      </div>
                    </Col>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="customerNumberT24">{t("ClientNumber")}</Label>
                        <AvField onChange={handleChangeInputFormClient} className="form-control"
                          name="customerNumberT24" type="number"
                          //validate={{required: { value: true, errorMessage: t("Required Field") }}}
                          value={selectClient !== undefined?selectClient.customerNumberT24:''}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    {getClientDataFormByPersonType(personTypeSelected)}                      
                  </Row>
                  <Row>
                    <Col xl="6">
                      <div className="mb-3">
                        <Label>{t("IdType")}</Label>
                        <Select
                          onChange={(e) => { setidentificationTypeSelected(identificationTypeList.find(x => x.value === e.value)); handleChangeInputFormClient(e) }}
                          options={identificationTypeList}
                          classNamePrefix="select2-selection"
                          value={identificationTypeSelected}
                          name="idType"
                        />
                      </div>
                    </Col>

                    <Col xl="6">
                      <div className="mb-3">
                        <Label htmlFor="idnumber">{t("IdNumber")}</Label>
                        <AvField onChange={handleChangeInputFormClient} className="form-control"
                          name="clientDocumentId" type="text"
                          validate={{required: { value: true, errorMessage: t("Required Field") }}}
                          value={selectClient !== undefined?selectClient.clientDocumentId:''}
                        />
                      </div>
                    </Col>
                  </Row>                  
                  <Row>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="nationality">{t("Nationality")}</Label>
                        <Select
                          onChange={(e) => { setcountrySelected(countryList.find(x => x.value === e.value)); handleChangeInputFormClient(e) }}
                          options={countryList}
                          classNamePrefix="select2-selection"
                          value={countrySelected}
                          name="nationality"
                        />
                      </div>
                    </Col> 
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="phone">{t("PhoneNumber")}</Label>
                        <AvField onChange={handleChangeInputFormClient} className="form-control"
                          name="phone" type="number"
                          //validate={{required: { value: true, errorMessage: t("Required Field") }}}
                          value={selectClient !== undefined?selectClient.phone:''}
                        />
                      </div>
                    </Col>
                    <Col md="6">
                      <div className="mb-3">
                        <Label htmlFor="email">{t("Email")}</Label>
                        <AvField onChange={handleChangeInputFormClient} className="form-control"
                          name="email" type="email"
                          //validate={{required: { value: true, errorMessage: t("Required Field") }}}
                          value={selectClient !== undefined?selectClient.email:''}
                        />
                      </div>
                    </Col>
                  </Row>
                  <Row>
                  <Label className="form-check-label">{t("Roles")}</Label>
                  <p>&ensp;</p>
                  {roleList.map((item, index) => (
                  <Col md="4">
                    <AvGroup check className="mb-3">
                      <Switch name={item.value}
                        uncheckedIcon={<Offsymbol />}
                        checkedIcon={<OnSymbol />}
                        onColor="#007943"
                        disabled={false}
                        className="form-label"
                        //checked={data.dataAspectosAmbientales.natureLocationProject}
                        onChange={handleChangeInputFormClient}
                      />
                      {'   '}
                      <Label className="form-check-label" htmlFor={item.value}> {item.label}</Label>
                    </AvGroup>
                  </Col>                   
                  ))}
                  </Row>      
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button color="dark" type="button" style={{ margin: '5px' }} onClick={props.onCancel}>
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  <Button color="success" type="submit" style={{ margin: '5px' }}><i className="mdi mdi-content-save mdi-12px"></i>
                    {" "}{t("Save")}
                  </Button>
                </CardFooter>
              </Card>
      </AvForm>
  </React.Fragment>

       );
};

ClientForm.propTypes = {
  onCancel: PropTypes.func,
  clientSelected:PropTypes.any
}

export default ClientForm;
