import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import Select from "react-select";
import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import { CoreServices } from "../../../../../services";

const ModalRelevoGeneracional = (props) => {
  const { t, i18n } = useTranslation();
  const [campoRequeridoCargo, setcampoRequeridoCargo] = useState(false);
  const [cargo, setCargo] = useState(null);
  const [CargosSelect, setCargosSelect] = useState(undefined);
  const [Cargoset, setCargoset] = useState(null);
  const [Cargos, setCargos] = useState(null);
  const [Relacion, setRelacion] = useState([]);
  const [codigoRelacion, setcodigoRelacion] = useState(null);
  const [relacionSelect, setrelacionSelect] = useState(undefined);
  const [campoRequeridoRelacion, setcampoRequeridoRelacion] = useState(undefined);
  const [cargoSelect, setcargoSelect] = useState(undefined);
  // Submitimos formulario para busqueda de clientes
  const cargos = [{ label: "Gerente", value: "1" }, { label: "Jefe RH", value: "2" }, { label: "Operario", value: "3" }]
  function handleSubmit(event, errors, values) {
    if (CargosSelect == undefined) {
      setcampoRequeridoCargo(true);
      return;
    } else {
      setcampoRequeridoCargo(false);
    }
    if (relacionSelect == undefined) {
      setcampoRequeridoRelacion(true);
      return;
    } else {
      setcampoRequeridoRelacion(false);
    }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.position = Cargoset;
    values.relationship = codigoRelacion;
    props.addRelevo(values);

  }
  function cambio(e) {
    setCargo(e.value)
  }
  React.useEffect(() => {
    // Read Api Service data
    cargarCargos()
    cargarRelacion() 
    setcampoRequeridoCargo(false);
  }, []);
  React.useEffect(() => {
    // Read Api Service data
    var defaultVal = null;
    console.log(props.jsonRelevo);
    setrelacionSelect(undefined)
    setCargosSelect(undefined)
    try {
      if (Relacion.length > 0 && props.jsonRelevo.relationship !== null && relacionSelect === undefined) {
        defaultVal = Relacion.find(x => x.label === (props.jsonRelevo.relationship));
        if (defaultVal !== undefined) {
          setrelacionSelect(defaultVal);
        }
      }
      if (Cargos.length > 0 && props.jsonRelevo.position !== null && CargosSelect === undefined) {
        defaultVal = Cargos.find(x => x.label === props.jsonRelevo.position);
        if (defaultVal !== undefined) {
          setCargosSelect(defaultVal);
        }
      }
      // if (cargos.length > 0 && props.jsonRelevo.relationship !== null && cargoSelect === undefined) {
      //   defaultVal = cargos.find(x => x.value === props.jsonRelevo.relationship);
      //   if (defaultVal !== undefined) {
      //     setcargoSelect(defaultVal);
      //   }
      // }
    }
    catch (err) { }
  }, [props, Relacion,Cargos]);
  function cargarCargos() {
    const api = new CoreServices();
    api.getCargosCatalogo()
      .then(response => {
        if (response === null) { return; }
        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        setCargos(json);
      });
  }
  function cargarRelacion() {
    const api = new CoreServices();
    api.getCargosCatalogo()
      .then(response => {
        if (response === null) { return; }
        let json = [];
        for (let i = 0; i < response.Records.length; i++) {
          json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
        }
        const optionGroup1 = [
          {
            label: t("Relation"),
            options: json,
          },

        ];
        setRelacion(json);
      });
  }
  function changeAll(e, tipo) {

  }
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{t("ManagementRelays")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          data-dismiss="modal"
          className="close"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body" style={{ backgroundColor: "#f3f5f7" }}>
        <Row>
          <Col xl="12">
            <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
              <Card>
                <CardBody>
                  <Row>
                    <Col md="8">
                      <div className="mb-3">
                        <Label htmlFor="name">{t("FullName")}</Label>
                        <AvField
                          className="form-control"
                          type="text"
                          name="name"
                          id="name"
                          value={props.jsonRelevo.name}
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                        />
                      </div>
                    </Col>
                    <Col md="4">
                      <div className="mb-3">
                        <Label htmlFor="position">{t("Charge")}</Label>
                        <Select
                          onChange={(e) => {
                            setCargosSelect(Cargos.find(x => x.label === e.label));
                            setCargoset(e.label);
                          }}
                          options={Cargos}
                          id="paises"
                          classNamePrefix="select2-selection"
                          placeholder={t("toselect")}
                          value={CargosSelect}
                        />
                        {campoRequeridoCargo ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="3">
                      <AvGroup className="mb-3">
                        <Label htmlFor="birthDate">{t("DBO")}</Label>
                        <AvField
                          className="form-control"
                          name="birthDate"
                          type="date"
                          value={props.jsonRelevo.birthDate}
                          errorMessage={t("Required Field")}
                          validate={{ required: { value: true } }}
                          id="birthDate" />
                      </AvGroup>
                    </Col>
                    <Col md="3">
                      <div className="mb-3">
                        <Label htmlFor="relationship">{t("Relation")}</Label>
                        <Select
                          onChange={(e) => { setrelacionSelect(Relacion.find(x => x.value === e.value)); setcodigoRelacion(e.label) }}
                          options={Relacion}
                          id="relationship"
                          classNamePrefix="select2-selection"
                          placeholder={t("Relation")}
                          value={relacionSelect}
                        />
                        {campoRequeridoRelacion ?
                          <p className="message-error-parrafo">{t("Required Field")}</p>
                          : null}
                      </div>
                    </Col>
                  </Row>
                </CardBody>
                <CardFooter style={{ textAlign: "right" }}>
                  <Button id="btnNew" color="dark" type="button" style={{ margin: '5px' }} onClick={props.toggle} data-dismiss="modal">
                    <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}{t("Cancel")}
                  </Button>
                  {props.botones ?
                    <Button id="btnSearch" color="success" type="submit" style={{ margin: '5px' }}>
                      <i className="mdi mdi-content-save mdi-12px"></i>
                      {" "}{t("Save")}
                    </Button>
                    : null}
                </CardFooter>
              </Card>
            </AvForm>
          </Col>
        </Row>
      </div>
    </Modal>
  );
};

ModalRelevoGeneracional.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func,
  onSelectClient: PropTypes.func.isRequired
};

export default ModalRelevoGeneracional;
