import PropTypes from 'prop-types';
import { translationHelpers } from '../../helpers';

import {
  Row,
  Col
} from "reactstrap"

import Balance from './financialSummary/Balance';
import Debts from './financialSummary/Debts';
import FinancialStatus from "./financialSummary/FinancialStatus";
import History from './financialSummary/History';

const FinancialSummary = (props) => {

  const [t, c] = translationHelpers('commercial_credit', 'common');

  return (
    <>
    <h4 className="card-title clearfix">{ t("Financial Summary") }</h4>
    <Row>
      <Col xl="12">
        <FinancialStatus client={ props.client } data={ props.financialStatus } />
      </Col>
    </Row>
    <Row>
      <Col xl="6">
        <Balance title={ props.balances.active.title }
          headers={ props.balances.active.headers }
          items={ props.balances.active.items } />
      </Col>
      <Col xl="6">
      <Balance title={ props.balances.passive.title }
          headers={ props.balances.passive.headers }
          items={ props.balances.passive.items } />
      </Col>
    </Row>
    <Row>
      <Col xl="6">
      <Balance title={ props.balances.application.title }
          headers={ props.balances.application.headers }
          items={ props.balances.application.items } />
      </Col>
      <Col xl="6">
      <Balance title={ props.balances.indicators.title }
          headers={ props.balances.indicators.headers }
          items={ props.balances.indicators.items } />
      </Col>
    </Row>

    { props.debts.short && (
      <Row>
        <Col xl="12">
          <Debts title={ t("Short Term Debts") } items={ props.debts.short.items }
            editable={ props.editMode } addDebt={ props.addDebt } />
        </Col>
      </Row>
      )}
      { props.debts.long && (
      <Row>
        <Col xl="12">
        <Debts title={ t("Long Term Debts") } items={ props.debts.long.items }
          editable={ props.editMode } addDebt={ props.addDebt } />
        </Col>
      </Row>
    )}

    { props.history && (
      <Row>
        <Col xl="12">
          <History headers={ props.history.headers } data={ props.history.data }
            editMode={ props.editMode } addHistoryDetail={ props.addHistoryDetail } />
        </Col>
      </Row>
    )}

    </>
  );
};

FinancialSummary.propTypes = {
  editMode: PropTypes.bool,
  client: PropTypes.object.isRequired,
  financialStatus: PropTypes.object.isRequired,
  balances: PropTypes.object,
  addDebt: PropTypes.func,
  addHistoryDetail: PropTypes.func,
};

FinancialSummary.defaultProps = {
  editMode: false,
};


export default FinancialSummary;
