import React, { useState } from "react"
import { withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import * as OPTs from "../../../../helpers/options_helper"
import * as url from "../../../../helpers/url_helper"

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import Services from "../../../../services/BpmServices/Services";
import { useLocation, useHistory } from 'react-router-dom'
const ModalAdvanceOptions = (props) => {
  const history = useHistory();

  /* ---------------------------------------------------------------------------------------------- */
  /*                       Submid del modal para pasar los parametros a la api                       */
  /* ---------------------------------------------------------------------------------------------- */
  function handleSubmitAdvanceOption(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }

    props.onSaveAdvanceProcess(values);
    
    /* ---------------------------------------------------------------------------------------------- */
    /*                             Aqui se manda a guardar el cumplimiento                            */
    /* ---------------------------------------------------------------------------------------------- */
    // const api = new Services();
    // api.guardarCumplimiento(values).then(resp => {
    //   /* ---------------------------------------------------------------------------------------------- */
    //   /*               aqui esta por verse si se envia a la llamar una funcion de index.js              */
    //   /* ---------------------------------------------------------------------------------------------- */
    //   // props.onSaveProcess(OPTs.OPT_ENVIARBUSQUEDADESCARTE, values);
    //   console.log(resp);
    // }).catch(err => {
    //   console.log(err);
    // })
    
  }
  
  return (
    <Modal
      size="xl"
      isOpen={props.isOpen}
      toggle={props.toggle}
      centered={true}>
      <div className="modal-header">
        <h5 className="modal-title mt-0">{props.t("Advance")}</h5>
        <button
          type="button"
          onClick={props.toggle}
          className="close"
          data-dismiss="modal"
          aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>

      <AvForm id="frmSearchAdvanceOption" className="needs-validation" onSubmit={handleSubmitAdvanceOption}>
        <div className="modal-body">
          <Row>
            <Col md="12">
              <div className="mb-3">
                <Label htmlFor="companyHistoryDescription">{props.t("Description")}</Label>
                <AvField
                  className="form-control"
                  name="txtComment"
                  type="textarea"
                  id="modalAdvanceOptionDescription"
                  rows={7}
                  validate={{
                    required: { value: true, errorMessage: props.t("Required Field") }
                  }}
                />
              </div>
            </Col>
          </Row>


        </div>
        <div className="modal-footer">

          {/*<Button color="negative" type="submit" style={{ margin: '5px' }} onClick={()=>clickBtn(OPTs.OPT_FINALIZARPROCESO)}>
            <i className="mdi mdi mdi-cancel mid-12px"></i> {props.t("FinishProcess")}
                </Button> */}

          <Button color="negative" type="button" style={{ margin: '5px' }} onClick={() => {props.toggle()}}>
            <i className="mdi mdi mdi-cancel mid-12px"></i> {props.t("Cancel")}
          </Button>

          <Button color="success" type="submit" style={{ margin: '5px' }}>
            <i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Continue")}
          </Button>
        </div>
      </AvForm>

    </Modal>
  );
};

ModalAdvanceOptions.propTypes = {
  isOpen: PropTypes.bool,
  toggle: PropTypes.func,
  onSaveAdvanceProcess: PropTypes.func,
};

export default (withTranslation()(ModalAdvanceOptions));
