          apiBack.eliminateCashFlowServiceDebts({
            transactId: dataDelete.transactId,
            cashFlowDebtServiceId: dataDelete.cashFlowDebtServiceId
          }).then(resp => {
            if (resp.statusCode == "500") {
              setconfirm_alert(false);
              seterror_dlg(true);
            } else {
              setconfirm_alert(false);
              initializeData(locationData);
            }
          }).catch(() => {
            setconfirm_alert(false);
            seterror_dlg(true);
          });
        }

        if (tipo == "DINGREFACTURA") {
          apiBack.eliminateCashFlowRevenueInvoicing({
            transactId: dataDelete.transactId,
            cashFlowInvoiceId: dataDelete.cashFlowInvoiceId
          }).then(resp => {
            if (resp.statusCode == "500") {
              setconfirm_alert(false);
              seterror_dlg(true);
            } else {
              setconfirm_alert(false);
              initializeData(locationData);
            }
          }).catch(() => {
            setconfirm_alert(false);
            seterror_dlg(true);
          });
        }

        if (tipo == "DCOBRANZA") {
          apiBack.eliminateCashFlowCollections({
            transactId: dataDelete.transactId,
            cashFlowCollectiond: dataDelete.cashFlowCollectiond
          }).then(resp => {
            if (resp.statusCode == "500") {
              setconfirm_alert(false);
              seterror_dlg(true);
            } else {
              setconfirm_alert(false);
              initializeData(locationData);
            }
          }).catch(() => {
            setconfirm_alert(false);
            seterror_dlg(true);
          });
        }

        if (tipo == "DEGRESO") {
          apiBack.eliminateCashFlowExpenses({
            transactId: dataDelete.transactId,
            cashFlowOutcomeId: dataDelete.cashFlowOutcomeId
          }).then(resp => {
            if (resp.statusCode == "500") {
              setconfirm_alert(false);
              seterror_dlg(true);
            } else {
              setconfirm_alert(false);
              initializeData(locationData);
            }
          }).catch(() => {
            setconfirm_alert(false);
            seterror_dlg(true);
          });
        }

        if (tipo == "DCARGATRABAJO") {
          apiBack.deleteCashFlowWorkLoad({
            transactId: dataDelete.transactId,
            cashFlowWorkloadId: dataDelete.cashFlowWorkloadId
          }).then(resp => {
            if (resp.statusCode == "500") {
              setconfirm_alert(false);
              seterror_dlg(true);
            } else {
              setconfirm_alert(false);
              initializeData(locationData);
            }
          }).catch(() => {
            setconfirm_alert(false);
            seterror_dlg(true);
          });
        }
      },
      onCancel: () => setconfirm_alert(false),
