import { useState } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Row,
    Col,
    Form,
    Container,
} from "react-bootstrap";
import Select from 'react-select'

const FormCommonFields = (props) => {
    // props
    const { onChange, form } = props

    // hooks
    const { t, i18n } = useTranslation();
    const [typePerson, setTypePerson] = useState({
        value: '1', label: 'Natural'
    })
    const [personTypeSelected, setPersonTypeSelected] = useState("")

    const options = [
        { value: '1', label: 'Natural' },
        { value: '2', label: 'Jurídica' }
    ]
    const optionsIdType = [
        { value: '1', label: 'RUC' },
        { value: '2', label: 'RIF' },
        { value: '3', label: 'Cédula' },
    ]

    const handleChangeTypePerson = (personSelected) => {
        setPersonTypeSelected(personSelected.value)
        console.log("Handle type person", personSelected.value);
    }

    return (
        <Container>
            <Row>
                <Col md={6} lg={4}>
                    <div className="mb-3">
                        <Form.Label htmlFor="personType">{t("PersonType")}</Form.Label>
                        <Select
                            defaultValue={typePerson}
                            options={options}
                            name="personType"
                            onChange={handleChangeTypePerson}
                        />
                    </div>
                </Col>
                <Col md={6} lg={4}>
                    <div className="mb-3">
                        <Form.Label htmlFor="idType">{t("IdType")}</Form.Label>
                        <Select
                            id="idType"
                            name="idType"
                            value={form.name}
                            options={optionsIdType}
                            onChange={onChange}
                        />
                    </div>
                </Col>
                <Col md={12} lg={4}>
                    <div className="mb-3">
                        <Form.Label htmlFor="idnumber">{t("IdNumber")}</Form.Label>
                        <Form.Control
                            id="idnumber"
                            name="idnumber"
                            value={form.name}
                            options={optionsIdType}
                            onChange={onChange}
                        >
                        </Form.Control>
                    </div>
                </Col>
            </Row>
        </Container>
    )
}

export default FormCommonFields
