import React, { useEffect, useState } from "react"
import { withTranslation } from "react-i18next"
import PropTypes from 'prop-types';

import {
  Row,
  Col,
  Button,
  Label,
  Input,
  Modal,
  Card,
  CardBody,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { translationHelpers } from "../../../../helpers";
import { BackendServices, BpmServices } from "../../../../services";
import { useHistory, useLocation } from "react-router-dom";
import * as url from "../../../../helpers/url_helper"

const LevelAutonomy = (props) => {
  const [t, c] = translationHelpers('translation', 'common');
  const [levelAutonomy, setLevelAutonomy] = useState(0);
  const [levelAutonomyPosition, setLevelAutonomyPosition] = useState('');
  const bpmServices = new BpmServices();
  const backendServices = new BackendServices();

  const location = useLocation();
  const history = useHistory();
  const [locationData, setLocationData] = useState(null);

  useEffect(() => {

    let dataSession;
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        dataSession = location.data;
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        dataSession = result
      }
    }

    getFacilityHistory({ dataSession })

  }, []);


  useEffect(() => {
    let amount = levelAutonomy + (props.levelAutonomy ?? 0)
    amount > 0 && bpmServices.saveAutonomy(amount).then(resp => {
      setLevelAutonomyPosition(resp?.body?.result['dmn-evaluation-result']['dmn-context']?.autonomiaduales?.banca)
    }).catch(err => {
      console.log(err)
    });
  }, [levelAutonomy, props.levelAutonomy])

  async function getFacilityHistory({ dataSession = locationData }) {
    await backendServices.consultGeneralDataPropCred(dataSession?.transactionId ?? 0).then(async propuesta => {
      backendServices.consultarFacilidadesT24(propuesta[0]?.requestId).then(async resp => {
        if (resp.length > 0 && resp != undefined) {
          setLevelAutonomy(levelAutonomy => levelAutonomy + +resp.reduce((acc, crr) => acc + crr.amount, 0))
        }
      })
      backendServices.consultarFacilidades(propuesta[0].requestId).then(resp => {
        resp = resp?.filter(data => data.debtor != "  ");
        if (resp.length > 0) {
          setLevelAutonomy(levelAutonomy => levelAutonomy + +resp.reduce((acc, crr) => acc + crr.amount, 0))
        }
      });
    })
  }

  return (
    <h5 className="card-title mt-2" style={{ textAlign: "right", fontSize: '21px!important' }}>{t("Level of Autonomy")}: {levelAutonomyPosition}</h5>
  );
};

export default (withTranslation()(LevelAutonomy));
