                typeofId = "RUC";
                break;
            }
            case "PAS": {
                typeofId = "Pasaporte";
                break;
            }
        }


        var tmpidentification = "";
        identification.split('-').map((str) => {
            if (str.startsWith("0")) {
                tmpidentification += str.replace(/^0+/, '') + "-";
            }
            else {
                tmpidentification += str + "-";
            }
        })
