import React, { useEffect, useState, useRef } from "react"
import { translationHelpers } from "../../../helpers"
import { useLocation,useHistory } from 'react-router-dom'
import * as url from "../../../helpers/url_helper"
import RiesgoAmbientalContext from './RiesgoAmbientalContext'

import {
  Row,
  Col,
  Button
} from "reactstrap"


//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"
import FormAspectosAmbientales from "./riesgoAmbiental/FormAspectosAmbientales"
import FormDetalles from "./riesgoAmbiental/FormDetalles"


const RiesgoAmbientalDetalle = (props) => {

  const history = useHistory();
  const location = useLocation();

  const [proposal, setProposal] = useState(null);
  const [context, setContext] = useState({});

  const [n, t, c] = translationHelpers('navigation', 'commercial_credit', 'common');

  useEffect(() => {

    if (!location.data) {
      location.data = {
        "requestId": "XYZ892022",
        "facilityId": "",
        "customerId": "68",
        "transactionId": "101",
        "processId": "6",
        "activityId": "0",
        "instanceId": 9104,
        "taskId": 456,
        "taskStatus": "InProgress"
      }
    }

    // update context
    console.log('setContext', location.data);
    setContext({
      transactionId: location.data.transactionId,
      customerId: location.data.customerId,
      instanceId: location.data.instanceId,
      requestId: location.data.requestId,
    });

    fetchProposal();
  }, []);

  // Component Refs
  const aspectosAmbientalesRef = useRef();
  const formDetallesRef = useRef();

  function fetchProposal() {
    setProposal({
      proposal: {
        id: 1010,
        revisionType: 'Bimensual',
        preapproved: false,
        minSib: 'Provision',
        relatedPart: false,
        approvalLevel: 'Nivel',
        flowType: 'Normal',
        applicationDate: '24-sep-2021',
        lastApplicationDate: '24-sep-2021',
        nextRevisionDate: '24-sep-2021',
        expirationDate: '24-sep-2021'
      },
      client: {
        number: '3223213',
      },
      economicGroup: {
        number: '32132006176',
        name: 'Grupo Tova SA',
        business: 'Actividad seleccionada'
      },
      risk: {
        countryClass: 'Panamá',
        classification: 'Medio',
        countryRisk: 'Nicaragua'
      },
      accountExecutive: {
        name: 'Elibeth'
      },
      responsibleUnit: {
        name: 'Unidad'
      }
    });
  }

  function handleSave() {
    /* ---------------------------------------------------------------------------------------------- */
    /*                   Aqui se debe de realizar la decision o continuar el proceso                  */
    /* ---------------------------------------------------------------------------------------------- */

    console.log('handleSave()');
    aspectosAmbientalesRef.current.submit();
    formDetallesRef.current.submit();
    // history.push(url.URL_DASHBOARD);
  }

  function handleClose() {
    /* ---------------------------------------------------------------------------------------------- */
    /*           Verificar si toca culminar el proceso o solo salir a la bandeja de entrada           */
    /* ---------------------------------------------------------------------------------------------- */
    history.push(url.URL_DASHBOARD);
  }

  return (

    <RiesgoAmbientalContext.Provider value={context}>

      <div className="page-content">

        <Breadcrumbs title={ n("Commercial Credit") } breadcrumbItem={ n("Environmental Risk") } />

        { proposal && (
          <>

            <Row>
              <Col xl="12">
                <FormAspectosAmbientales ref={aspectosAmbientalesRef} />
              </Col>
            </Row>

            <Row>
              <Col xl="12">
                <FormDetalles ref={formDetallesRef} />
              </Col>
            </Row>

            <Row className="my-2">
              <Col xl="12 text-end">
              <Button id="btnClose" color="dark" type="button" style={{ margin: '5px' }} onClick={ handleClose }>
              <i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i>
            {" "} { c("Close") }
                </Button>
                <Button id="btnSubmit" color="success" type="button" style={{ margin: '5px' }} onClick={ handleSave }>
                <i className="mdi mdi-content-save mdi-12px"></i>
            {" "}{ c("Save") }
                </Button>

              </Col>
            </Row>

          </>
        )}

      </div>

    </RiesgoAmbientalContext.Provider>
  );

}


export default RiesgoAmbientalDetalle;
