//Gestionar Log de los Procesos
import React, { useEffect, useState } from "react"
import PropTypes from 'prop-types';
import { Link, useLocation } from "react-router-dom"
import functionshelper from "../../helpers/functions_helper"
import moment from "moment";

import {
  Table,
  Card,CardHeader,
  CardBody,
  UncontrolledAlert,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
  Button,
  Row,
  Col,
} from "reactstrap"

import Alert from 'react-bootstrap/Alert'
//i18n
import { withTranslation } from "react-i18next"
//Import SweetAlert
import SweetAlert from "react-bootstrap-sweetalert"

import ModalNewLog from "./ModalNewLog"
import { BackendServices } from "../../services/index";

import LocalStorageHelper from "../../helpers/LocalStorageHelper";
import * as opt from "../../helpers/options_helper"

import BootstrapTable from 'react-bootstrap-table-next';

const LogProcess = props => {
  
  const [logProcessModel, setlogProcessModel] = useState(props.logProcessModel);
  
  const [dataList, setDataList] = useState([]);
  const [displayNewLogModal, setDisplayNewLogModal] = useState(false);

  const [mainDebtor, setmainDebtor] = useState(null);
  const [backendServices, setbackendServices] = useState(new BackendServices());
    
  const [msgData, setmsgData] = useState({ show: false, msg: "", isError: false });
    
  const [localStorageHelper, setlocalStorageHelper] = useState(new LocalStorageHelper());

  useEffect(() => {
    if (logProcessModel !== undefined && logProcessModel !== null) {
      loadMainDebtor(logProcessModel.transactId);
      searchData(logProcessModel.transactId)
    } 
  }, []);

  //nuevo log
  function toggleDisplayNewLogModal() {
    setDisplayNewLogModal(!displayNewLogModal);
    removeBodyCss()
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  function loadMainDebtor(transactionId) {
    // consultarDeudorPrincipal
    backendServices.consultPrincipalDebtor(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setmainDebtor(data);
        }
      });
  }
  
  //buscamos las Propuestas de Credito
  function searchData(transactId) {
      backendServices.getHistorical(transactId)
        .then(resp => {
          const dataRows = resp.map((dt, idx) => (
            <tr key={idx}>
              <td scope="row">{moment(dt.date).format("YYYY-MM-DD HH:mm:ss")}</td>
              <td>{dt.observations}</td>
              <td>{dt.processBpmName /*functionshelper.getProcessName(dt.processId)*/}</td>
              <td>{dt.activityBpmName /*functionshelper.getProcessName(dt.processId)*/}</td>
              <td>{dt.statusDescription /*functionshelper.getProcessName(dt.processId)*/}</td>
              <td>{dt.responsible}</td>
            </tr>)
          );
          setDataList(dataRows);
        }).catch(err => {console.log(err);});
  }

  function loadTableData(){
    let options = {
      textLabels: {
        body: {
          noMatch: props.t("SorryNoMatchingRecordsWereFfound"),
          toolTip: props.t("Order"),
          columnHeaderTooltip: (column) => `${props.t("OrderBy")} ${column.label}`,
        },
        pagination: {
          next: props.t("Next"),
          previous: props.t("Prev"),
          rowsPerPage: props.t("RowsPerPage"),
          displayRows: props.t("From"),
          sizePerPage: 5,
          sizePerPageList: [{
            text: '5th', value: 5
          }, {
            text: '10th', value: 10
          }, {
            text: 'All', value: 20
          }]
        },
        toolbar: {
          search: props.t("Search"),
          downloadCsv: props.t("DownloadCSV"),
          print: props.t("Print"),
          viewColumns: props.t("ViewColumns"),
          filterTable: props.t("FilterTable"),
        },
        filter: {
          all: props.t("All"),
          title: props.t("Filter"),
          reset: props.t("Restore"),
        },
        viewColumns: {
          title: props.t("ShowColumns"),
          titleAria: props.t("ShowHideColumns"),
        },
        selectedRows: {
          text: props.t("SelectedRows"),
          delete: props.t("Delete"),
          deleteAria: props.t("DeleteSelectedRows"),
        },
      },
    };

    //Header de la Tabla de Bandeja de Entrada
    const columns = [
      { text: <strong>{props.t("Date")}</strong>, dataField: 'date', sort: true },
      { text: <strong>{props.t("Comment")}</strong>, dataField: 'instanceId', sort: true },
      { text: <strong>{props.t("Process")}</strong>, dataField: 'transactionId', sort: true },
      { text: <strong>{props.t("Activity")}</strong>, dataField: 'applicationNumber', sort: true },
      { text: <strong>{props.t("Status")}</strong>, dataField: 'personName', sort: true },
      { text: <strong>{props.t("User")}</strong>, dataField: 'activity', sort: true },
      { text: <strong>{props.t("Status")}</strong>, dataField: 'status', sort: true },
      { text: "", dataField: 'action' },
    ];

    setDataHeader(columns);
    setDataOptions(options);
  }

  function onSaveLogProcess(observations) {
    
    var credentials = localStorageHelper.get(opt.VARNAME_USRCREDENTIAL);
    
    logProcessModel.responsible = credentials?.usr??"admin"
    logProcessModel.clientId = mainDebtor?.personId??logProcessModel.clientId;
    logProcessModel.observations= observations;    
    console.log("onSaveLogProcess",logProcessModel)
    backendServices.saveHistorical(logProcessModel)
        .then(resp => {
          if (resp !== undefined) {
            searchData(logProcessModel.transactId);
          }
          else {
            //Mensaje ERROR
            showMessage(props.t("ErrorSaveMessage"), true);
          }
        }).catch(err => {
          console.log(err);
        });
  }

  //mostrar mensaje 
  function showMessage(message, isError = false) {
    setmsgData({ show: true, msg: message, isError: isError })
  }
  

  return (
    <React.Fragment>
      <Card>

      <CardHeader>
              <Row>
                <Col md={6}>
                  <h4 className="card-title">{props.t("Binnacle")}</h4>
                </Col>
                <Col md={6} style={{ textAlign: "right"}}>
                <Button className="btn" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleDisplayNewLogModal() }} title={props.t("Add")}><i className="fas fa-lg fa-plus-circle"></i> {(" ")}</Button>
         
                </Col>
               </Row>                     
            </CardHeader>
          <CardBody>
              <Row>
                  <Col md="12">
                    <Alert show={msgData.show} variant={msgData.isError ? "danger" : "success"} dismissible onClose={() => { setmsgData({ show: false, msg: "", isError: false }) }}>
                      {msgData.msg}
                    </Alert>
                  </Col>
                </Row>
        <Row>
          <Col md={12}>
            <div className="table-responsive styled-table-div">
              <Table className="table table-striped table-hover styled-table">
                <thead className="">
                  <tr>
                    <th>{props.t("Date")}</th>
                    <th>{props.t("Comment")}</th>
                    <th>{props.t("Process")}</th>
                    <th>{props.t("Activity")}</th>
                    <th>{props.t("Status")}</th>
                    <th>{props.t("User")}</th>
                  </tr>
                </thead>
                <tbody>
                  {dataList!==undefined && dataList.length>0?
                    dataList:
                  <td colspan="7"><div className="alert alert-info" style={{ textAlign: "center" }}>{props.t("NoData")}</div></td> }
                </tbody>
              </Table>
            </div>
          </Col>
        </Row>
        
      </CardBody>
      </Card>
      <ModalNewLog onSaveLogProcess={(observations) => { onSaveLogProcess(observations) }} isOpen={displayNewLogModal} toggle={() => { toggleDisplayNewLogModal() }} />
      

    </React.Fragment>
  );
}
LogProcess.propTypes = {
  logProcessModel: PropTypes.any, //LogProcessModel
};
export default (withTranslation()(LogProcess))
