import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { useTranslation } from "react-i18next";
import { Tabs, Tab } from 'react-bootstrap';

import { Row, Col, Card, CardBody, Button, Label } from "reactstrap";
import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation";
import ApiServicesCommon from "../../../../../services/Common/ApiServicesCommon";
import { BackendServices, CoreServices } from "../../../../../services";
// import Select from "react-select";
import Switch from "react-switch";
import Breadcrumb from "../../../../../components/Common/Breadcrumb";
import { withTranslation } from "react-i18next";
import { Select } from 'antd';
import { Link, useLocation } from "react-router-dom";
import SweetAlert from "react-bootstrap-sweetalert";
import Currency from "../../../../../helpers/currency";

const Offsymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      No
    </div>
  );
};

const OnSymbol = () => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100%",
        fontSize: 12,
        color: "#fff",
        paddingRight: 2,
      }}
    >
      {" "}
      Si
    </div>
  );
};

const PantallaBusqueda = (props) => {
  const currencyData = new Currency();
  const { Option } = Select;
  const [switch1, setswitch1] = useState(false);
  const [activeTab, setactiveTab] = useState(1);
  const location = useLocation();
  const [dataLocation, setdataLocation] = useState(undefined);
  const [showResults, setShowResults] = useState(false);
  const [dataList, setDataList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [displayClienteModal, setDisplayClienteModal] = useState(false);
  const { t, i18n } = useTranslation();
  const [dataGlobal, setdataGlobal] = useState({});
  const [typeGarantia, settypeGarantia] = useState("nuevo");
  const [dataFacilidad, setdataFacilidad] = useState(null);
  const [idFacilidad, setidFacilidad] = useState(null);
  const [dataFacilidadSet, setdataFacilidadSet] = useState(null);
  const [monedas, setmonedas] = useState(null);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [monedasSet, setmonedasSet] = useState(null);
  const [successSave_dlg2, setsuccessSave_dlg2] = useState(false);
  const [paises, setpaises] = useState(null);
  const [usuarioProspecto, setusuarioProspecto] = useState(null);
  const [paisesSet, setpaisesSet] = useState(null);
  const [provincias, setprovincias] = useState(null);
  const [provinciasSet, setprovinciasSet] = useState(null);
  const [ActividadEmpresa, setActividadEmpresa] = useState(null);
  const [ActividadEmpresaSet, setActividadEmpresaSet] = useState(null);
  const [requestDate, setRequestDate] = useState('');
  const [lastRequestDate, setLastRequestDate] = useState('');
  const [proposedExpirationDate, setProposedExpirationDate] = useState('');
  const [nextRevisionDate, setNextRevisionDate] = useState('');
  const [dataRequest, setdataRequest] = useState('');
  const [dataGeneral, setdataGeneral] = useState({
    "transactId": 2,
    "lineId": "LIN -2",
    "revisionFrequency": "FRECUENCIA REVISION",
    "dueDate": "2022-03-09",
    "limitDate": "2022-03-09",
    "amount": 450.000,
    "maxTotal": 450.000,
    "modifiedAmount": 450.000,
    "status": true
  });
  const backendServices = new BackendServices();
  const coreServices = new CoreServices();
  useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        location.data.transactionId = 0;
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setdataLocation(location.data);
        loadData(location.data)
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setdataLocation(result);
        loadData(result)
      }
    }

  }, []);
  function handleSubmit(event, errors, values) {
    // if (monedasSet == null) {
    //   return;
    // }
    // if (paisesSet == null) {
    //   return;
    // }
    // if (provinciasSet == null) {
    //   return;
    // }
    // if (ActividadEmpresaSet == null) {
    //   return;
    // }
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
    values.riskCountry = paisesSet;
    values.province = provinciasSet;
    values.economicActivity = ActividadEmpresaSet;
    values.currency = monedasSet;
    values.transactId = Number(dataLocation.transactionId ?? 0);
    values.accounts = switch1 ? "Si" : "No";
    values.amount = Number(currencyData.getRealValue(values?.amount ?? 0));
    values.maxTotal = Number(currencyData.getRealValue(values?.maxTotal ?? 0));
    values.modifiedAmount = Number(currencyData.getRealValue(values?.modifiedAmount ?? 0));
    values.facilityId = Number(dataLocation.facilityId ?? 0);
    let datosJson = {
      transactId: values?.transactId ?? " ",
      lineId: values?.lineId ?? " ",
      currency: values?.currency ?? " ",
      riskCountry: values?.riskCountry ?? " ",
      province: values?.province ?? " ",
      proposalDate: values?.proposalDate ?? " ",
      approvalDate: values?.approvalDate ?? " ",
      revisionFrequency: values?.revisionFrequency ?? " ",
      dueDate: values?.dueDate ?? " ",
      limitDate: values?.limitDate ?? " ",
      amount: Number(values?.amount) ?? 0,
      maxTotal: Number(values?.maxTotal) ?? 0,
      modifiedAmount: Number(values?.modifiedAmount) ?? 0,
      additionalInfo: values?.additionalInfo ?? " ",
      economicActivity: values?.economicActivity ?? " ",
      accounts: values?.accounts ?? " ",
      autonomy: values?.autonomy ?? " "
    }
    backendServices.saveCreditLine(datosJson).then(resp => {
      if (resp !== null && resp !== undefined) {
        setsuccessSave_dlg2(true)
      } else {
        seterror_dlg(true);
      }
    }).catch(err => {
      seterror_dlg(true);
    })
  }
  async function loadData(data) {
    await coreServices.getMonedaCatalogo().then(resp => {
      setmonedas(resp.Records)
    });
    await coreServices.getPaisesCatalogo().then(resp => {
      setpaises(resp.Records)
    });
    await backendServices.consultPrincipalDebtor(data.transactionId)
      .then((data) => {
        if (data !== undefined) {
          setusuarioProspecto(data);
        }
      });
    await coreServices.getActividadEconomicaCatalogo().then(response => {
      if (response === null) { return; }
      let json = [];
      for (let i = 0; i < response.Records.length; i++) {
        json.push({ label: response.Records[i]["Description"], value: response.Records[i]["Code"] })
      }
      setActividadEmpresa(json);
    })
    await backendServices.consultGeneralDataIGR(data?.transactionId ?? 0).then(resp => {
      setActividadEmpresaSet(resp?.economicActivity?.name ?? "")
    })
    await backendServices.consultGeneralDataPropCred(data?.transactionId ?? 0).then(async resp => {
      setRequestDate(resp[0]?.requestDate ?? '');
      setLastRequestDate(resp[0]?.lastRequestDate ?? '');
      setNextRevisionDate(resp[0]?.nextRevisionDate ?? '');
      setProposedExpirationDate(resp[0]?.proposedExpirationDate ?? '');
      setpaisesSet(resp[0]?.countryRisk ?? '');
      await coreServices.getPaisesCatalogo().then(pais => {
        coreServices.getProvinciasCatalogo(pais.Records.find(x => x.Description === resp[0]?.countryRisk ?? '')?.Code).then(resp => {
          setprovincias(resp.Records)
        });
      });
      // changeCountry(resp[0]?.countryRisk ?? '')
    })
  }
  function changeCountry(code) {
    coreServices.getProvinciasCatalogo(paises.find(x => x.Description === code)?.Code).then(resp => {
      setprovincias(resp.Records)
    });
  }
  function handleSelect(key) {
    setidFacilidad(dataFacilidadSet[key].facilityId);
  }
  function check(e) {
    let tecla = (document.all) ? e.keyCode : e.which;
    //Tecla de retroceso para borrar, siempre la permite
    if (tecla == 45) {
      e.preventDefault();
      return true;
    }

    return false;
  }
  return (
    <React.Fragment>
      <h4 className="card-title title-header">{usuarioProspecto != null ? usuarioProspecto.name + " " + usuarioProspecto.name2 + " " + usuarioProspecto.lastName + " " + usuarioProspecto.lastName2 : ""}    #{dataLocation?.transactionId ?? 0}</h4>
      <h5 className="card-title">{t("Nueva LÃ­nea")}</h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmGarantiasMuebles" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Frecuebnciarevi">
                {t("Line Number or Limit")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="lineId"
                id="lineId"
                validate={{
                  number: { value: true, errorMessage: t("InvalidField") },
                }}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-2">
              <Label>{t("Coin")}</Label>
              <Select noOptionsMessage={() => ""}
                showSearch
                style={{ width: "100%" }}
                placeholder={t("SearchtoSelect")}
                optionFilterProp="children"
                // defaultValue={props.dataCapex1.back}
                onChange={(e) => { setmonedasSet(e) }}
                filterOption={(input, option) =>
                  option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                {monedas != null ?

                  monedas.map((item, index) => (
                    <Option key={index} value={item.Description}>{item.Description}</Option>
                  ))
                  : null}
              </Select>
            </div>
          </Col>
          <Col md="3">
            <div className="mb-2">
              <Label>{t("Risk Country")}</Label>
              {paisesSet != null ?
                <Select noOptionsMessage={() => ""}
                  showSearch
                  style={{ width: "100%" }}
                  placeholder={t("SearchtoSelect")}
                  optionFilterProp="children"
                  defaultValue={paisesSet}
                  onChange={(e) => { setpaisesSet(e); changeCountry(e); }}
                  filterOption={(input, option) =>
                    option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                  }
                >
                  {paises != null ?

                    paises.map((item, index) => (
                      <Option key={index} value={item.Description}>{item.Description}</Option>
                    ))
                    : null}
                </Select> : null}
            </div>
          </Col>
          <Col md="3">
            <div className="mb-2">
              <Label>{t("Province")}</Label>
              <Select noOptionsMessage={() => ""}
                showSearch
                id="province"
                style={{ width: "100%" }}
                placeholder={t("SearchtoSelect")}
                optionFilterProp="children"
                // defaultValue={props.dataCapex1.back}
                onChange={(e) => { setprovinciasSet(e) }}
                filterOption={(input, option) =>
                  option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                {provincias != null ?
                  provincias.map((item, index) => (
                    <Option key={index} value={item.Description}>{item.Description}</Option>
                  ))
                  : null}
              </Select>
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Economic Activity")}
              </Label>
              {/* {ActividadEmpresaSet} */}
              {ActividadEmpresaSet != null ?
                < Select
                  showSearch
                  style={{ width: "100%" }}
                  placeholder={t("SearchtoSelect")}
                  optionFilterProp="children"
                  defaultValue={ActividadEmpresaSet}
                  onChange={(e) => { setActividadEmpresaSet(e) }}
                  filterOption={(input, option) =>
                    option.children?.toLowerCase().indexOf(input.toLowerCase()) >= 0
                  }
                >
                  {ActividadEmpresa != null ?
                    ActividadEmpresa.map((item, index) => (
                      <Option key={index} value={item.label}>{item.label}</Option>
                    ))
                    : null}
                </Select> : null
              }
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Proposal Date")}</Label>
              <AvField
                className="form-control"
                name="proposalDate"
                type="date"
                value={requestDate}
                id="proposalDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Approval date")}</Label>
              <AvField
                className="form-control"
                name="approvalDate"
                type="date"
                value={lastRequestDate}
                id="approvalDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("DueDate")}</Label>
              <AvField
                className="form-control"
                name="dueDate"
                type="date"
                value={proposedExpirationDate}
                id="dueDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Deadline")}</Label>
              <AvField
                className="form-control"
                name="limitDate"
                type="date"
                value={nextRevisionDate}
                id="limitDate"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Review Frequency")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="revisionFrequency"
                id="revisionFrequency"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Monto">{t("Amount")}</Label>
              <AvField
                className="form-control"
                type="text"
                name="amount"
                onKeyPress={(e) => { return check(e) }}
                pattern="^[0-9,.]*$"
                onKeyUp={(e) => { let x = currencyData.getRealValue(e.target.value); e.target.value = currencyData.format(x); }}
                id="amount"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Totmaximo">{t("Total Maximum")}</Label>
              <AvField
                className="form-control"
                type="text"
                onKeyPress={(e) => { return check(e) }}
                name="maxTotal"
                pattern="^[0-9,.]*$"
                onKeyUp={(e) => { let x = currencyData.getRealValue(e.target.value); e.target.value = currencyData.format(x); }}

                id="maxTotal"
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="MontoModificado">
                {t("Modified Amount")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                onKeyPress={(e) => { return check(e) }}
                name="modifiedAmount"
                pattern="^[0-9,.]*$"
                onKeyUp={(e) => { let x = currencyData.getRealValue(e.target.value); e.target.value = currencyData.format(x); }}
                id="modifiedAmount"
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="additionalInfo">
                {t("Additional Information")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="additionalInfo"
                id="additionalInfo"
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label htmlFor="Autonomia">
                {t("Autonomy")}
              </Label>
              <AvField
                className="form-control"
                type="text"
                name="autonomy"
                id="autonomy"
                title={t("")}
              />
            </div>
          </Col>
          <Col md="3">
            <div className="mb-3">
              <Label>{t("Accounts")}</Label>
              <AvGroup check className="mb-3">
                <Switch name="isProjectLocatedProtectedNaturalArea"
                  uncheckedIcon={<Offsymbol />}
                  checkedIcon={<OnSymbol />}
                  onColor="#007943"
                  id="check1"
                  className="form-label"
                  onChange={() => {
                    setswitch1(!switch1);
                  }}
                  checked={switch1}
                />
                {'   '}
                {/* <Label className="form-check-label" htmlFor="isProjectLocatedProtectedNaturalArea"> {t("Is the project located or adjacent to a protected natural area")}</Label> */}
              </AvGroup>
            </div>
          </Col>
        </Row>
        <Row>
          <Col md={12} style={{ textAlign: "right", marginTop: "10px" }}>
            <Link
              to={{
                pathname: '/creditocomercial/garantias/',
                data: { facilityId: dataLocation?.facilityId ?? 0, transactionId: dataLocation?.transactionId ?? 0 },
              }}
            >
              <Button
                id="btnNew"
                color="danger"
                type="button"
                style={{ margin: "5px" }}
                onClick={props.toggle}
                data-dismiss="modal"
              >
                <i className="mdi mdi mdi-cancel mid-12px"></i>{" "}
                {t("Cancel")}
              </Button>
            </Link>
            <Button id="btnSearch" type="submit" color="success" style={{ margin: '5px' }}>
              <i className="mdi mdi-content-save mdi-12px"></i>
              {" "} {typeGarantia == "nuevo" ? t("Save") : t("Edit")}</Button>
          </Col>
        </Row>
      </AvForm>
      {
        successSave_dlg2 ? (
          <SweetAlert
            success
            title={t("SuccessDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              setsuccessSave_dlg2(false)
              window.location.reload();
            }}
          >
            {t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null
      }
      {
        error_dlg ? (
          <SweetAlert
            error
            title={t("ErrorDialog")}
            confirmButtonText={t("Confirm")}
            cancelButtonText={t("Cancel")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
    </React.Fragment >
  );
};

export default withTranslation()(PantallaBusqueda);
