r_dlg(true);}else{setconfirm_alert(false);setsuccessSave_dlg(true);}}).catch(function(error){setconfirm_alert(false);seterror_dlg(true);});}else if(tipo=="Propuesto"){apiBack.eliminarSowPropuestoIGR({transactId:dataLocation.transactionId,currentSowId:dataDelete.currentSowId,status:false}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);setsuccessSave_dlg(true);}}).catch(function(error){setconfirm_alert(false);seterror_dlg(true);});}else if(tipo=="DCP"){apiBack.eliminarRelacionesBancariasDeudasCP({transactId:dataLocation.transactionId,debtId:dataDelete.debtId,status:false}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);setsuccessSave_dlg(true);}}).catch(function(error){setconfirm_alert(false);seterror_dlg(true);});}else if(tipo=="DCL"){apiBack.eliminarRelacionesBancariasDeudasLP({transactId:dataLocation.transactionId,debtId:dataDelete.debtId,status:false}).then(function(resp){if(resp.statusCode=="500"){setconfirm_alert(false);seterror_dlg(true);}else{setconfirm_alert(false);setsuccessSave_dlg(true);}}).catch(function(error){setconfirm_alert(false);seterror_dlg(true);});}},onCancel:function onCancel(){return setconfirm_alert(false);},children:t("Youwontbeabletorevertthis")}):null]});});export default RelacionesBancarias;

