                </tr>
              </thead>
              <tbody>
                {shorttermdebtsRows}
                <tr style={estiloTotales}>
                  <th>{t("Total")}</th>
                  <th></th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.monto}</th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.saldo1}</th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.saldo2}</th>
                  <th>{dataReturn.sumatoriaDeudaCorto?.saldo3}</th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                </tr>
              </tbody>
            </Table>
          </Col>
        </Row>
        <div className="my-3"></div>
        <Row>
          <Col md="12">
            <h6 className="float-start my-1">{t("LongTermDebts")}</h6>
            <Link className="float-end" onClick={() => {
              setTipo("CL"); setbotonValidation(true); setdataSet({
                transactId: 0,
                bank: null,
                facilityType: null,
                amount: 0,
                date: null,
                expirationDate: null,
                debitBalance1: 0,
                debitBalance2: 0,
                debitBalance3: 0,
                rate: 0,
                fee: 0,
                bail: null,
                destinationOfFunds: null
              }); toggleShowModalDeudasLargoPlazo()
            }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link>
          </Col>
        </Row>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("Bank")}</th>
                  <th>{t("FacilityType")}</th>
                  <th>{t("Amount")}</th>
                  <th>{t("GrantDate")}</th>
                  <th>{t("DueDate")}</th>
                  <th>{t("CurrentYear")}</th>
                  <th>1 {t("YearAgo")}</th>
                  <th>2 {t("YearAgo")}</th>
                  <th>{t("Rate")}</th>
                  <th>{t("Bond")}</th>
                  <th>{t("Term") + "(" + t("days") + ")"}</th>
                  <th>{t("DestinationOfFunds")}</th>
                  <th style={{ textAlign: "right" }}></th>
                </tr>
              </thead>
              <tbody>
                {longtermdebtsRows}
                <tr style={estiloTotales}>
                  <th>{t("Total")}</th>
                  <th></th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.monto}</th>
                  <th></th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.saldo1}</th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.saldo2}</th>
                  <th>{dataReturn.sumatoriaDeudaLargo?.saldo3}</th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
                  <th></th>
