            </Row>
          </Col>
          <Col lg="6">
            <Row>
              <Col lg="12">
                <h6 className="float-start my-1">{t("ProposedSow")}</h6>
                <Link className="float-end" onClick={() => {
                  setdataSet({
                    transactId: 0,
                    otherBanks: 0,
                    banesco: 0,
                    total: 0,
