          >
            {error_msg}
          </SweetAlert>
        ) : null
      }
      {
        confirm_alert ? (
          <SweetAlert
            title={t("Areyousure")}
            warning
            showCancel
            confirmButtonText={t("Yesdeleteit")}
            confirmBtnBsStyle="success"
            cancelBtnBsStyle="danger"
            onConfirm={() => {
              const apiBack = new BackendServices();
              if (tipo == "Actual") {
                apiBack.eliminarSowActualIGR({ transactId: dataLocation.transactionId, currentSowId: dataDelete.currentSowId }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "Propuesto") {
                apiBack.eliminarSowPropuestoIGR({ transactId: dataLocation.transactionId, currentSowId: dataDelete.currentSowId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCP") {
                apiBack.eliminarRelacionesBancariasDeudasCP({ transactId: dataLocation.transactionId, debtId: dataDelete.debtId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
