                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              } else if (tipo == "DCL") {
                apiBack.eliminarRelacionesBancariasDeudasLP({ transactId: dataLocation.transactionId, debtId: dataDelete.debtId, status: false }).then(resp => {
                  if (resp.statusCode == "500") {
                    setconfirm_alert(false)
                    seterror_dlg(true)
                  } else {
                    setconfirm_alert(false)
                    setsuccessSave_dlg(true)
                  }
                }).catch(error => {
                  setconfirm_alert(false)
                  seterror_dlg(true)
                })
              }
