import React, { useState } from "react";
import { withTranslation } from "react-i18next";
import PropTypes from "prop-types";

import {
    Row,
    Col,
    Button,
    Label,
    Input,
    Modal,
    Card,
    CardBody,
    CardFooter,
} from "reactstrap";

import { AvForm, AvField } from "availity-reactstrap-validation";
import { ReactComponent as Logo } from './svgPI.svg';
import { SvgIcon } from '@material-ui/core';
import ApiServiceBpm from "../../services/BpmServices/Services";

const ModalWatchProcess = (props) => {
    const [svg, setSvg] = useState(null);

    // props.wacthprocess(props.processInstanceId).then((resp) => {
    //     console.log(resp);

    // })
    const api = new ApiServiceBpm();

    React.useEffect(() => {
        wacthprocess(props.processInstanceId);
    }, [props.processInstanceId])

    function wacthprocess(processInstanceId) {
        api.wacthprocess(processInstanceId)
            .then((data) => {

                console.log(data);

                setSvg(data);

            })
            .catch((error) => {
                console.error(error);
            });
    }
    return (
        <Modal
            size="xl"
            isOpen={props.isOpen}
            toggle={props.toggle}
            centered={true}
        >
            <div className="modal-header">
                <h5 className="modal-title mt-0">{props.t("ProcessImages")}</h5>
                <button
                    type="button"
                    onClick={props.toggle}
                    className="close"
                    data-dismiss="modal"
                    aria-label="Close"
                >
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>

            {/* <SvgIcon component={Logo} viewBox="500 500 500 500" /> */}
            {/* <Logo></Logo> */}

            <div className="m-3 d-flex justify-content-center">
                {svg}
            </div>
        </Modal>
    );
};

ModalWatchProcess.propTypes = {
    isOpen: PropTypes.bool,
    toggle: PropTypes.func,
    onSaveProcess: PropTypes.func,
};

export default withTranslation()(ModalWatchProcess);
