/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import PropTypes from 'prop-types'
import { useLocation, useHistory } from "react-router-dom";
import { Link } from "react-router-dom"

import {
  Card,CardHeader,
  CardBody,
  Row,
  Col,
  Button,
  CardFooter,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../../components/Common/Breadcrumb"

//i18n
import { useTranslation, withTranslation } from "react-i18next"

import DatosGenerales from "./DatosGenerales"
import DatosServicioFiduciario from "./DatosServicioFiduciario"
import DatosOtrosDatos from "./DatosOtrosDatos"
import ModalCloseOptions from "./ModalCloseOptions";
import ModalAdvanceOptions from "./ModalAdvanceOptions";
import { BpmServices } from "../../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import * as url from "../../../../helpers/url_helper";

import { BackendServices, CoreServices } from "../../../../services";
import React, { useEffect, useState } from "react"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"

const PantallaBusqueda = props => {

  const { t, i18n } = useTranslation();
  const history = useHistory();
  const location = useLocation()
  const { selectedData } = location;
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalAdvanceOptions, setDisplayModalAdvanceOptions] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");

  const [ServiciosFiduciario, setServiciosFiduciario] = useState(null);
  const [OtherServiciosFiduciario, setOtherServiciosFiduciario] = useState(null);
  const [BasaFid, setBasaFid] = useState(null);
  const [DataGeneral, setDataGeneral] = useState(null);
  
  const [locationData, setLocationData] = useState(null);
  //Servicios
  const [backendServices, setbackendServices] = useState(new BackendServices());
  const [bpmServices, setbpmServices] = useState(new BpmServices());
  const [coreServices, setcoreServices] = useState(new CoreServices());

  const [mainDebtor, setmainDebtor] = useState(null);

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    if (location.data !== null && location.data !== undefined) {
      if (location.data.transactionId === undefined || location.data.transactionId.length <= 0) {
        //location.data.transactionId = 0;
        //checkAndCreateProcedure(location.data);
        history.push(url.URL_DASHBOARD);
      }
      else {
        sessionStorage.setItem('locationData', JSON.stringify(location.data));
        setLocationData(location.data);
        fetchData(location.data);
      }
    }
    else {
      //chequeamos si tenemos guardado en session
      var result = sessionStorage.getItem('locationData');
      if (result !== undefined && result !== null) {
        result = JSON.parse(result);
        setLocationData(result);
        fetchData(result);
      }
    }
  }, []);

  //Caraga Inicial de datos
  function fetchData(locationData) {

    //chequear si la tarea no ha sido iniciada
    bpmServices.checkAndStartTask(locationData)
      .then((iniresult) => {
        if (iniresult === false) {
          history.push(url.URL_DASHBOARD);
        }
      })

    loadUserProspect(locationData.transactionId)
    // Read Api Service data
    LoadAll();
  }

  function loadUserProspect(transactionId) {
    // consultarDeudorPrincipal
    backendServices.consultPrincipalDebtor(transactionId)
      .then((data) => {
        if (data !== undefined) {
          setmainDebtor(data);
        }
      });
  }

  function LoadAll() {
    LoadFiduciario();
    LoadOtherFiduciario();
    LoadNumeroFideicomiso();
  }

  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
  }

  //Modal Opciones al Salir
  function toggleModalModalAdvanceOptions() {
    setDisplayModalAdvanceOptions(!displayModalAdvanceOptions);
  }

  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option) {
    switch (option) {
      case 1: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 2: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 3: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
      case 4: {
        /* ---------------------------------------------------------------------------------------------- */
        /*                    Cerramos el proceso y retornamos a la bandeja de entrada                    */
        /* ---------------------------------------------------------------------------------------------- */
        const api = new BpmServices();
        // let instanceId = location.data.instanceId
        let instanceId = 0
        api.abortProcess(instanceId).then((data) => {
          if (data !== undefined) {
            setsuccessSave_dlg(true)
          }
          else {
            //Mensaje ERROR
            seterror_msg(props.t("TheProcessCouldNotFinish"))
            seterror_dlg(true)
          }
        });
        break;
      }
    }

    // history.push("/dashboard");
  }

  function LoadFiduciario() {
    backendServices.consultarSeccionServiciosFiduciario(location.data.transactionId).then(resp => {
      let data = resp;
      (data != null || data != undefined) && setServiciosFiduciario(data);
    });
  }

  function LoadOtherFiduciario() {
    backendServices.consultarSeccionOtrosServiciosFiduciario(location.data.transactionId).then(resp => {
      let data = resp;
      (data != null || data != undefined) && setOtherServiciosFiduciario(data);
    });
  }

  function LoadNumeroFideicomiso() {
    backendServices.consultarNumeroFideicomiso(location.data.transactionId).then(resp => {
      setBasaFid(resp[0]);
      let data = resp.data;
      // (data != null || data != undefined) && setRecomendation(data);
    });
  }

  async function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }

    setDataGeneral({ transactId: location.data.transactionId, ...values });

    toggleModalModalAdvanceOptions()
  }


  /*
  // Camino Feliz ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"no"
  };
    
  //Requiere Ajustes 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si"
  };
  */



  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("TrustServices")} />

        <Card>
        <CardHeader>
              <Row>
                <Col>
                  <h4 className="card-title">{t("TrustServices")}</h4>
                  <p className="card-title-desc">{t("This form is official and must be filled out correctly")}</p>
                </Col>
              </Row> 
              <Row>
                <Col sm={6}>
                  <h5 className="card-title">{mainDebtor != null ? (mainDebtor.typePerson === "1" ? (mainDebtor.name + " " + mainDebtor.name2 + " " + mainDebtor.lastName + " " + mainDebtor.lastName2) : (mainDebtor.name)): ""} </h5>
                </Col>
                <Col sm={6}>
                  <h5 className="card-title" style={{ textAlign: "right" }}>{t("Tramit")}:{" "}#{locationData?.transactionId}</h5>
                </Col>
              </Row>                           
            </CardHeader>
        


          <AvForm className="needs-validation" onSubmit={handleSubmit}>

            <DatosGenerales BasaFid={BasaFid} />

            <DatosServicioFiduciario ServiciosFiduciario={ServiciosFiduciario} />

            <DatosOtrosDatos OtherServiciosFiduciario={OtherServiciosFiduciario} />

            <CardFooter style={{ textAlign: "right" }}>
              <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { onSaveProcess(2); }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {props.t("Exit")}</Button>
              <Button color="success" type="submit" style={{ margin: '5px' }} onClick={() => { }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Advance")}</Button>
              {/*
                  <Link className="btn btn-dark" to={ { pathname: "/creditocomercial/fideicomiso", selectedData: undefined } }>{props.t("Close")}</Link>

              <Button color="negative" type="button" style={{ margin: '5px' }}>
                { props.t("reject") }
              </Button>

              <Button  color="success" type="button" style={{ margin: '5px' }}>
                { props.t("Advance") }
              </Button>

              <Button  color="success" type="button" style={{ margin: '5px' }}>
                { props.t("GenerateDocument") }
              </Button>
                        */}
            </CardFooter>

          </AvForm>
        </Card>
        <ModalAdvanceOptions DataGeneral={DataGeneral} onSaveProcess={onSaveProcess} isOpen={displayModalAdvanceOptions} toggle={() => { toggleModalModalAdvanceOptions() }} />
        {successSave_dlg ? (
          <SweetAlert
            success
            title={props.t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)

              history.push(url.URL_DASHBOARD);
            }}
          >
            {props.t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={props.t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
      </div>

    </React.Fragment>
  )
}

PantallaBusqueda.propTypes = {
  selectedId: PropTypes.any,
  onClose: PropTypes.func.isRequired
}

export default (withTranslation()(PantallaBusqueda))
