import { useState } from 'react'
import { useTranslation } from 'react-i18next'
import {
    Card,
    Row,
    Col,
    Form,
    Container,
    Button
} from "react-bootstrap";
import Select from 'react-select'

import FormContainerQuoter from './FormContainerQuoter'
import FormQuoterTypeNatural from './FormQuoterTypeNatural';
import FormDataQuoter from './FormDataQuoter';
import FormCommonFields from './FormCommonFields';
import TableResultQuoter from './TableResultQuoter';
import { AvForm } from 'availity-reactstrap-validation';

const FormQuoterScreen = () => {
    // hooks
    const { t, i18n } = useTranslation();
    // custom hooks
    const [form, setForm] = useState({})
    const [disableSelect, setDisableSelect] = useState(true)
    const [typePerson, setTypePerson] = useState("Natural")

    // handlers
    const testHandleSubmit = () => {
        console.log("Working!", form)
    }

    const handleChange = (evt, propertyName) => {

        const name = evt?.target
            ? evt.target.name
            : propertyName.name

        const value = evt?.target
            ? evt.target.value
            : evt.value

        if (propertyName?.name) {
            handleSelect(propertyName.name, value)
        }

        setForm({
            ...form,
            [name]: value
        })

    }

    const handleSelect = (propertyName, value) => {
        switch (propertyName) {
            case 'feciPercentage':
                const disable = value == "Si"
                    ? false
                    : true
                setDisableSelect(disable)
                return
            case 'personType':
                const type = value == "Natural"
                    ? "Natural"
                    : "Juridica"
                setTypePerson(type)
                return
            default:
                break;
        }
    }

    return (
        <>
            <FormContainerQuoter>
                <Card>
                    <Card.Body>
                        <Card.Title>
                            {t("ClientData")}
                        </Card.Title>
                        {/* <AvForm onSubmit={testHandleSubmit}> */}
                        <Row>
                            <FormCommonFields
                                onChange={handleChange}
                                form={form}
                                typePerson={typePerson}
                            />
                        </Row>
                        {/* <Row>
                            <FormQuoterTypeNatural
                                onChange={handleChange}
                                form={form}
                            />
                        </Row> */}
                        <Row>
                            <FormDataQuoter
                                onChange={handleChange}
                                form={form}
                                disableSelect={disableSelect}
                            />
                        </Row>
                        <Row >
                            <Col className='d-grid mt-5'>
                                <Button
                                    size='lg'
                                    className='float-end'
                                    type='submit'
                                    onClick={testHandleSubmit}
                                >
                                    {t("Calculate")}
                                </Button>
                            </Col>
                        </Row>
                        {/* </AvForm> */}
                    </Card.Body>
                </Card>
            </FormContainerQuoter>
            <TableResultQuoter />
        </>
    )
}

export default FormQuoterScreen
