/*ReinforcedManagementReport = Lista de Informe Reforzado*/
import React, { useState } from "react"
import PropTypes from 'prop-types';
import { Link } from "react-router-dom"
import { useLocation, useHistory } from 'react-router-dom'
// import * as url from "../../../helpers/url_helper"

import {
  Card,
  CardBody,
  CardFooter,
  Button,
  Row,
  Col,
} from "reactstrap"

//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"

//i18n
import { withTranslation } from "react-i18next"

import { BackendServices, CoreServices, BpmServices, } from "../../../services";
import SweetAlert from "react-bootstrap-sweetalert";
import ModalReportManagment from "../7_SupervisorAnalisisCredito/ModalSupervisor/ModalManagmentReport";
import ModalCreditProposal from "../7_SupervisorAnalisisCredito/ModalSupervisor/ModalCreditProposal";
import ModalFinancialReport from "../7_SupervisorAnalisisCredito/ModalSupervisor/ModalFinancialReport";

const AutonomiaCredito = props => {
  const history = useHistory();
  const location = useLocation()
  const { selectedData } = location;
  const [ShowModelReportManagment, setShowModelReportManagment] = useState(false);
  const [ShowModelCreditProposal, setShowModelCreditProposal] = useState(false);
  const [ShowModelFinancialReport, setShowModelFinancialReport] = useState(false);

  function toggleShowModelReportManagment() {
    setShowModelReportManagment(!ShowModelReportManagment)
  }

  function toggleShowModelCreditProposal() {
    setShowModelCreditProposal(!ShowModelCreditProposal)
  }

  function toggleShowModelFinancialReport() {
    setShowModelFinancialReport(!ShowModelFinancialReport)
  }


    /*
  // Camino Feliz ... "dcreditoparalelo" = "sinparalelo" y "dambientalparalelo" = "sinparalelo" ... feliz
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"sinparalelo"
  };
  
  // Camino semi Feliz uno de los dos paralelos activos ... chequear (status ambiental paralelo activo no se puede avanzar) status="activo", statuscredito="activo"  ... feliz
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"no"
  };

  //Regresar por Ajustes a Analista ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si",          
    "regresar":"analista"
  };

  //Regresar por Ajustes a Ejecutivo ... 
  var values = {
    "processId": OPTs.PROCESS_SUPERVISORANALISISCREDITO.toString(),
    "activityId": OPTs.ACT_NONE.toString(),
    "transactionId": locationData.transactionId,
    "requestId":locationData.requestId,
    "decision":"si",          
    "regresar":"ejecutivo"
  };
  */



  return (
    <React.Fragment>

      <div className="page-content">

        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("CreditAnalysisSupervisor")} />

        <Card>
          <CardBody>

            <CardBody>
              <Row>
                <Col md="6">
                  <h4 className="card-title">{props.t("CreditAnalysisSupervisor")}</h4>
                </Col>
                <Col md="6" style={{ textAlign: "right" }}></Col>
              </Row>

            </CardBody>

            <Row>
              <div className="d-flex flex-row justify-content-between">
                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleShowModelReportManagment() }}>
                  {" "} {props.t("ConsultManagementReport")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>

                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleShowModelCreditProposal() }}>
                  {" "} {props.t("ConsultCreditProposal")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>

                <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { toggleShowModelFinancialReport() }}>
                  {" "} {props.t("ConsultFinancialReport")} <i className="mdi mdi-eye-outline mid-12px"></i></Button>
              </div>
            </Row>

          </CardBody>
          <CardFooter style={{ textAlign: "right" }}>
            <Button id="btnSearch" color="negative" type="button" style={{ margin: '5px' }} onClick={() => { }}><i className="mdi mdi-call-missed mid-12px"></i>
              {" "} {props.t("Refuse")}</Button>
            <Button id="btnSearch" color="success" type="button" style={{ margin: '5px' }} onClick={() => { }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i>
              {" "} {props.t("Advance")}</Button>
          </CardFooter>
        </Card>
      </div>
      <ModalReportManagment isOpen={ShowModelReportManagment} toggle={() => { toggleShowModelReportManagment() }} />
      <ModalCreditProposal isOpen={ShowModelCreditProposal} toggle={() => { toggleShowModelCreditProposal() }} />
      <ModalFinancialReport isOpen={ShowModelFinancialReport} toggle={() => { toggleShowModelFinancialReport() }} />


    </React.Fragment>
  )
}

AutonomiaCredito.propTypes = {
  //    onSelectIdPropuesta: PropTypes.func.isRequired
  selectedIdPropuesta: PropTypes.any,
  onClose: PropTypes.func.isRequired
}

export default (withTranslation()(AutonomiaCredito))
