/*ExclusionList = Lista de Exclusion*/
import React, { useState } from "react"
import PropTypes from 'prop-types';
import { useLocation, useHistory } from 'react-router-dom'
import LoadingOverlay from 'react-loading-overlay';
import ModalEndProcess from "../../../components/EndProcess/ModalEndProcess"
import * as OPTs from "../../../helpers/options_helper"
import * as url from "../../../helpers/url_helper"

import {
  Row,
  Col,
  Card,
  CardBody,
  Label,
  CardFooter,
  Button
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"
//Import Breadcrumb
import Breadcrumbs from "../../../components/Common/Breadcrumb"

//i18n
import { withTranslation } from "react-i18next"

import FormResultados from "./FormResultados"
import ModalAdvanceOptions from "./ModalAdvanceOptions"
import ModalCloseOptions from "./ModalCloseOptions"
import LogProcess from "../../../components/LogProcess/index"
import { BpmServices, BackendServices } from "../../../services";
import SweetAlert from "react-bootstrap-sweetalert";

const ListaExclusion = props => {


  const history = useHistory();
  const location = useLocation()
  //Data que recibimos en el location
  const [locationData, setLocationData] = useState(undefined);

  const [dataList, setDataList] = useState([]); //items de la lista de exclusion
  const [dataListSelected, setDataListSelected] = useState([]); //items selected de la lista de exclusion       
  const [isActiveLoading, setIsActiveLoading] = useState(false);
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [datosUsuario, setdatosUsuario] = useState(null);
  const [displayModalAdvanceOptions, setDisplayModalAdvanceOptions] = useState(false);
  const [displayModalCloseOptions, setDisplayModalCloseOptions] = useState(false);
  const [displayModalEndProcess, setDisplayModalEndProcess] = useState(false);

  //On Mounting (componentDid Mount)
  React.useEffect(() => {
    setLocationData(location.data);
    initializeData(location.data);
    iniciarProceso(location.data.status, location.data.taskId);
  }, []);
  React.useEffect(() => {
    let lista = document.getElementsByClassName("listaExclusion");
    for (var i = 0; i < lista.length; i++) {
      for (let j = 0; j < dataListSelected.length; j++) {
        if (dataListSelected[j].exclusionCatListId == dataList[i].exclusionId) {
          document.getElementById(dataList[i].exclusionId).checked = true;
        }
      }
    }
  }, [props, dataListSelected]);

  //Caraga Inicial
  function initializeData(locdata) {
    var processNumber = locdata ? locdata.transactionId : null;
    /* ---------------------------------------------------------------------------------------------- */
    /*                     Buscar en Servicio el ExclisionList de este proceso...                     */
    /* ---------------------------------------------------------------------------------------------- */
    const api = new BackendServices()
    api.consultarListaExclusion().then((data) => {
      setDataList(data.exclusionCatListDTOList);
    }).catch((error) => {
      console.error('api error: ', error);
    });
    //{ transactId: location.data.transactionId, processId: location.data.processId, activityId: location.data.activityId }
    api.consultarUsuarioProspecto(processNumber).then(resp => {      
      // dataReturn.codigoTipoPersona = resp.personType
      // dataReturn.codigoTipoIdentificacion = resp.identificationType
      // dataReturn.numeroCliente = resp.customerNumberT24
      // dataReturn.primerNombre = resp.firstName
      // dataReturn.segundoNombre = resp.secondName
      // dataReturn.primerApellido = resp.firstLastName
      // dataReturn.segundoApellido = resp.secondLastName
      // dataReturn.numeroIdentificacion = resp.customerDocumentId
      // dataReturn.nuevoDato = ""
      setdatosUsuario(resp)
    })
    /* ---------------------------------------------------------------------------------------------- */
    /*                       Obtenemos los datos de la lista de exclusion record                      */
    /* ---------------------------------------------------------------------------------------------- */
    if (processNumber != null) {

      api.consultarListaExclusionMarcada({ transactId: processNumber }).then((data) => {
        let datos = data.getExclusionApplicationListDTOList;
        setDataListSelected(datos);

      }).catch((error) => {
        console.error('api error: ', error);
      });
    }
  }
  function iniciarProceso(status, taskId) {
    if (status === "Ready") {
      const api = new BpmServices();
      api.startedStatusTask(taskId).then(resp => {
        if (resp === undefined) {
          history.push(url.URL_DASHBOARD);
        }
      });
    }
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }

  //Modal Opciones para avanzar
  function toggleModalAdvanceOptions() {
    setDisplayModalAdvanceOptions(!displayModalAdvanceOptions);
  }
  //Modal Opciones al Salir
  function toggleModalCloseOptions() {
    setDisplayModalCloseOptions(!displayModalCloseOptions);
  }
  //Modal Para mostrar Comentario de Fin de Proceso
  function toggleModalEndProcess() {
    setDisplayModalEndProcess(!displayModalEndProcess);
  }
  function onSelectOption(e) {
    console.log(e);
    
    const api = new BackendServices();
    if(document.getElementById(e.exclusionId).checked){
      api.guardarListaExclusion(locationData.transactionId, e.exclusionId).then(resp => {
        if (resp === undefined) {
          //Mensaje ERROR
          seterror_msg(props.t("ErrorSaveMessage"))
          seterror_dlg(true)
          return;
        }
      });
    }else{
      api.eliminarListaExclusionMarcada(locationData.transactionId, e.exclusionId).then(resp => {
        if (resp === undefined) {
          //Mensaje ERROR
          seterror_msg(props.t("ErrorSaveMessage"))
          seterror_dlg(true)
          return;
        }
      });
    }
  }

  //Chequear si existen opciones seleccionadas en la lista
  function checkSelectedOptions() {
    /* ---------------------------------------------------------------------------------------------- */
    /*              chequear si tiene seleccionada alguna opcion de la lista de exclusion             */
    /*              mostrar mensaje para confirmacion y finalizar proceso                             */
    /* ---------------------------------------------------------------------------------------------- */
    var checkboxes = document.getElementsByClassName("listaExclusion");
    let check = 0;
    for (var i = 0; i < checkboxes.length; i++) {
      if (checkboxes[i].checked) {
        check++;
      }
    }

    if (check > 0) {
      setDisplayModalAdvanceOptions(true);
    } else {
      //Avanzamos el Proceso
      onSaveProcess(OPTs.OPT_ENVIARIGR);
    }
  }

  //Salvando el Proceso y le pasamos una opcion: 1-Finalizar Proceso, 2- Salvarlo, 3- A Cumplimmineto, 4- Avanzar a IGR
  function onSaveProcess(option) {

    if (option === OPTs.OPT_PREFINALIZARPROCESO) {
      toggleModalEndProcess();
      return;
    }

    //Guardamos la seleccion

    var checkboxes = document.getElementsByClassName("listaExclusion");
    for (var i = 0; i < checkboxes.length; i++) {
      if (checkboxes[i].checked) {
      }
    }

    saveJBPMProcess(option);

  }

  function saveJBPMProcess(option) {

    const apiBpmServices = new BpmServices();


    switch (option) {
      case OPTs.OPT_FINALIZARPROCESO: {
        apiBpmServices.abortProcess(locationData.instanceId)
          .then((data) => {
            if (data !== undefined) {
              history.push(url.URL_DASHBOARD);
            }
            else {
              //Mensaje ERROR
              seterror_msg(props.t("TheProcessCouldNotFinish"))
              seterror_dlg(true)
            }
          });
        break;
      }
      case OPTs.OPT_SALVAPARCIAL: {
        var values = {
          "processId": OPTs.PROCESS_LISTAEXCLUSION.toString(),
          "activityId": OPTs.ACT_NONE.toString(),
          "transactionId": locationData.transactionId.toString(),
          "customerId": locationData.customerId.toString(),
          "applicationNumber": "",
          "procedureNumber": "",
          //"decision":"no"          
        };
        apiBpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {
            if (data !== undefined) {
              setsuccessSave_dlg(true);
            }
            else {
              //Mensaje ERROR
              seterror_msg(props.t("ErrorSaveMessage"))
              seterror_dlg(true)
            }
          });

        break;
      }
      case OPTs.OPT_ENVIARIGR: {
        var values = {
          "processId": OPTs.PROCESS_INFORMEGESTION.toString(),
          "activityId": OPTs.ACT_DATOSGENERALES.toString(),
          "transactionId": locationData.transactionId.toString(),
          "customerId": locationData.customerId.toString(),
          "applicationNumber": "",
          "procedureNumber": "",
          //"decision":"no"          
        };
        apiBpmServices.updatevariables(locationData.instanceId, values)
          .then((data) => {
            if (data !== undefined) {
              setsuccessSave_dlg(true);
            }
            else {
              //Mensaje ERROR
              seterror_msg(props.t("ErrorSaveMessage"))
              seterror_dlg(true)
            }
          });

        break;
      }
    }
  }

  //salvar comentario de end process
  function onSaveEndProcess(values) {

    //Salvar los comentarios en Bitacora    
    locationData["endProcessComment"] = values.txtComment;

    UpdateDataModel(locationData);

    onSaveProcess(OPTs.OPT_FINALIZARPROCESO);
  }

  //Actualizamos El Modelo de Datos segun va cambiando
  function UpdateDataModel(data) {
    setLocationData(data);
  }

  return (
    <React.Fragment>
      <div className="page-content">
        <Breadcrumbs title={props.t("CommercialCredit")} breadcrumbItem={props.t("ExclusionList")} />
        <LoadingOverlay active={isActiveLoading} spinner text={props.t("WaitingPlease")}>
          <Card>
            <CardBody>
              <Row>
                <Col lg="12">
                  <h4 className="card-title">{datosUsuario!=null?datosUsuario.firstName+" "+datosUsuario.secondName+" "+datosUsuario.firstLastName+" "+datosUsuario.secondLastName:""}</h4>
                  <h4 className="card-title">{props.t("ExclusionList")}</h4>
                  <p className="card-title-desc">
                    {props.t("Selectifyoumeetanyofthefollowingpoints")}
                  </p>
                </Col>
              </Row>
              {dataList.length > 0 && (
                <FormResultados results={dataList} dataCheck={dataListSelected} onSelectOption={onSelectOption} />
              )}
            </CardBody>
            <CardFooter style={{ textAlign: "right" }}>
              <Button color="dark" type="button" style={{ margin: '5px' }} onClick={() => { toggleModalCloseOptions() }}><i className="mdi mdi-arrow-left-bold-circle-outline mid-12px"></i> {props.t("Exit")}</Button>
              <Button color="success" type="button" style={{ margin: '5px' }} onClick={() => { checkSelectedOptions() }}><i className="mdi mdi-arrow-right-bold-circle-outline mid-12px"></i> {props.t("Advance")}</Button>
            </CardFooter>
          </Card>

          {(locationData !== undefined && locationData.customerId !== null && locationData.customerId !== undefined && locationData.customerId.length > 0) ? (<LogProcess locationData={locationData} />) : null}
        </LoadingOverlay>

        <ModalCloseOptions onSaveProcess={onSaveProcess} isOpen={displayModalCloseOptions} toggle={() => { toggleModalCloseOptions() }} />
        <ModalAdvanceOptions onSaveProcess={onSaveProcess} isOpen={displayModalAdvanceOptions} toggle={() => { toggleModalAdvanceOptions() }} />
        <ModalEndProcess onSaveEndProcess={onSaveEndProcess} isOpen={displayModalEndProcess} toggle={() => { toggleModalEndProcess() }} />

        {successSave_dlg ? (
          <SweetAlert
            success
            title={props.t("SuccessDialog")}
            onConfirm={() => {
              setsuccessSave_dlg(false)

              history.push(url.URL_DASHBOARD);
            }}
          >
            {props.t("SuccessSaveMessage")}
          </SweetAlert>
        ) : null}

        {error_dlg ? (
          <SweetAlert
            error
            title={props.t("ErrorDialog")}
            onConfirm={() => {
              seterror_dlg(false)
            }}
          >
            {error_msg}
          </SweetAlert>
        ) : null}
      </div>
    </React.Fragment>
  )
}


export default (withTranslation()(ListaExclusion))
