                        <AvGroup className="mb-3">
                          <Label htmlFor="customerid">{t("ID Number")}</Label>
                          <AvField
                            className="form-control"
                            name="customer[id]"
                            type="text"
                            value={dataSet.numero == "" || dataSet.numero == null ? datosUsuario.customerDocumentId : dataSet.numero}
                            id="customerid" />
                        </AvGroup>
                      </Col>

                      <Col md="4">
                        <AvGroup className="mb-3">
                          <Label htmlFor="balance">{t("Balance")}</Label>
                          <AvField
                            className="form-control"
                            name="balance"
                            value={dataSet.balance}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            type="text"
                            id="balance" />
                        </AvGroup>
                      </Col>

                    </Row>
                  </Col>
                </Row>
