                          <p className="message-error-parrafo">{t("Required")}</p>
                          : null}
                      </Col>
                      <Col md="4">
                        <Label htmlFor="subproposalType">{t("SubProposal Type")}</Label>
                        <Select
                          onChange={(e) => {
                            setsubPropuestaSelect(subPropuesta.find(x => x.value === e.value))
                            setsubPropuestaSet(e.value);
                            dataReturn.proposalTypeName = e.value;
                            setdataReturn(dataReturn);
                          }}
                          options={subPropuesta}
                          placeholder={t("Select")}
                          value={subPropuestaSelect}
                        />
                        {dataValidation.subPropuestaRequerido ?
                          <p className="message-error-parrafo">{t("Required")}</p>
                          : null}

                      </Col>
                      <Col md="4">
                        <Label htmlFor="facilityType">{t("Facility Type")}</Label>
                        <Select
                          onChange={(e) => {
                            settipoFacilidadSelect(tipoFacilidad.find(x => x.value === e.value))
                            settipoFacilidadSet(e.value);
                            dataReturn.facilityTypeId = e.value;
                            setdataReturn(dataReturn);
                          }}
                          options={tipoFacilidad}
                          placeholder={t("Select")}
                          value={tipoFacilidadSelect}
                        />
                        {dataValidation.tipoFacilidadRequerido ?
                          <p className="message-error-parrafo">{t("Required")}</p>
                          : null}
                      </Col>
                    </Row>
                    <Row>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="purpose">{t("Purpose")}</Label>
                          <AvField id="purpose"
                            className="form-control"
                            name="purpose"
                            id="purpose"
                            validate={{
                              required: { value: true, errorMessage: t("Required") }
                            }}
                            type="textarea"
                            value={dataSet.purpose}
                            rows="4" />
                        </AvGroup>
                      </Col>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="sublimits">{t("Sub Limits")}</Label>
                          <AvField id="sublimits"
                            className="form-control"
                            name="sublimits"
                            id="sublimits"
                            value={dataSet.sublimits}
                            validate={{
                              required: { value: true, errorMessage: t("Required") }
                            }}
                            type="textarea"
                            rows="4" />
                        </AvGroup></Col>
                    </Row>
                  </Col>
                </Row>
                {/* InterestRateSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("Proposed Interest Rate")}</h5>
                    <Row>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="proposalRate">{t("Amount")}</Label>
                          <AvField
                            className="form-control"
                            name="proposalRate"
                            type="text"
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            id="proposalRate"
                            value={dataSet.proposalRate} />
                        </AvGroup>
                      </Col>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="noSubsidyRate">{t("Rate without Subsidy")}</Label>
                          <AvField
                            className="form-control"
                            name="noSubsidyRate"
                            type="text"
                            id="noSubsidyRate"
                            value={dataSet.noSubsidyRate}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }} />
                        </AvGroup>
                      </Col>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="effectiveRate">{t("Effective Interest Rate")}</Label>
                          <AvField
                            className="form-control"
                            name="effectiveRate"
                            value={dataSet.effectiveRate}
                            type="text"
                            id="effectiveRate"
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }} />
                        </AvGroup>
                      </Col>
                      <Col md="3">
                        <AvGroup className="mb-3">
                          <Label htmlFor="feci">{t("FECI")}</Label>
                          <AvField id="feci"
                            className="form-control"
                            name="feci"
                            type="select"
                            value={dataSet.feci}
                            required>
                            <option value={true}>Si</option>
                            <option value={false}>No</option>
                          </AvField>
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* CommissionSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <ComisionSeccion MontoFacilidad={MontoFacilidad} validation={dataValidation} dataSet={dataSet} items={commissions}
                      onSaveItem={handleNewCommission}
                      onDeleteItem={handleDeleteCommission} />
                  </Col>
                </Row>
                {/* TermsSection */}
                <h5>{t("Terms")}</h5>
                <Row>
                  <Col md="12">
                    <AvGroup className="mb-3">
                      <Label htmlFor="termDays">{t("Terms in Days/Month/Year")}</Label>
                      <AvField
                        className="form-control"
                        name="termDays"
                        value={dataSet.termDays}
                        type="text"
                        validate={{
                          number: { value: true, errorMessage: t("InvalidField") },
                        }}
                        id="termDays" />
                    </AvGroup>
                    <AvGroup className="mb-3">
                      <Label htmlFor="termsDescription">{t("Terms Description")}</Label>
                      <AvField
                        className="form-control"
                        name="termDescription"
                        type="textarea"
                        id="termDescription"
                        value={dataSet.termDescription}
                        rows="4" />
                    </AvGroup>
                  </Col>
                </Row>
                {/* DisbursementSection */}
                <DisbursementSection validation={dataValidation} dataSet={dataSet} disbursementTerms={disbursementTerms}
                  onSaveDisbursementTerm={handleNewDisbursementTerm}
                  onDeleteDisbursementTerm={handleDeleteDisbursementTerm}
                  paymentPrograms={paymentPrograms}
                  onSavePaymentProgram={handleNewPaymentProgram}
                  onDeletePaymentProgram={handleDeletePaymentProgram}
                  disbursementMethods={disbursementMethods}
                  onSaveDisbursementMethod={handleNewDisbursementMethod}
                  onDeleteDisbursementMethod={handleDeleteDisbursementMethod} />
                {/* WarrantsSection */}
                <WarrantsSection validation={dataValidation} dataSet={dataSet} warrants={warrants}
                  onSaveItem={handleNewWarrant}
                  onDeleteItem={handleDeleteWarrant} />
                {/* LTV */}
                <Row>
                  <Col md="12">
                    <h5>{t("LTV")} (%)</h5>
                    <Row>
                      <Col md="4">
                        <AvGroup className="mb-3">
                          <AvField
                            className="form-control"
                            name="ltv"
                            value={dataSet.ltv}
                            type="text"
                            id="ltv" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* SuretiesSection */}
                <SuretiesSection validation={dataValidation} dataSet={dataSet} sureties={sureties}
                  onSaveItem={handleNewSurety}
                  onDeleteItem={handleDeleteSurety} />
                {/* FinancialConditionsSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("FinancialConditions")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">

                          <AvField
                            className="form-control"
                            name="finantialConditions"
                            type="textarea"
                            value={dataSet.finantialConditions}
                            id="finantialConditions"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* AmbientalRiskSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("AmbientalRisk")}</h5>
                    <Row>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="category">{t("Category")}</Label>
                          <AvField
                            className="form-control"
                            name="environmentRiskCategory"
                            type="text"
                            value={dataSet.environmentRiskCategory}
                            validate={{
                              number: { value: true, errorMessage: t("InvalidField") },
                            }}
                            id="environmentRiskCategory" />
                        </AvGroup>
                      </Col>
                      <Col md="6">
                        <AvGroup className="mb-3">
                          <Label htmlFor="covenant">{t("Covenant")}</Label>
                          <AvField
                            className="form-control"
                            name="covenant"
                            type="text"
                            value={dataSet.covenant}
                            id="covenant" />
                        </AvGroup>
                      </Col>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <Label htmlFor="opinion">{t("Opinion")}</Label>
                          <AvField
                            className="form-control"
                            name="environmentRiskOpinion"
                            type="textarea"
                            value={dataSet.environmentRiskOpinion}
                            id="environmentRiskOpinion"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* FinantialCovenantSection */}
                <Row className="mb-3">
                  <Col md="12">
                    <h5>{t("FinantialCovenant")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <AvField
                            className="form-control"
                            name="finantialCovenant"
                            value={dataSet.finantialCovenant}
                            type="textarea"
                            id="finantialCovenant"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* LegalDocumentationSection */}
                <Row>
                  <Col>
                    <h5>{t("LegalDocumentation")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">

                          <AvField
                            className="form-control"
                            name="legalDocumentation"
                            value={dataSet.legalDocumentation}
                            type="textarea"
                            id="legalDocumentation"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* OtherConditionsSection */}
                <Row>
                  <Col>
                    <h5>{t("OtherConditions")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <AvField
                            className="form-control"
                            name="otherConditions"
                            // value={props.dataSet.otherConditions}
                            type="textarea"
                            id="otherConditions"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* CreditRiskSection */}
                <Row>
                  <Col>
                    <h5>{t("CreditRisk")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">
                          <Label htmlFor="creditrisk">{t("Opinion")}</Label>
                          <AvField
                            className="form-control"
                            name="creditRiskOpinion"
                            type="textarea"
                            value={dataSet.creditRiskOpinion}
                            id="creditRiskOpinion"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                {/* ProvisionSection */}
                <Row>
                  <Col>
                    <h5>{t("Provision")}</h5>
                    <Row>
                      <Col md="12">
                        <AvGroup className="mb-3">

                          <AvField
                            className="form-control"
                            name="provision"
                            value={dataSet.provision}
                            type="textarea"
                            id="provision"
                            rows="4" />
                        </AvGroup>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                <Row className="my-2">
                  <Col xl="12 text-end">
                    <Link to="#" className="btn btn-dark"
                      onClick={(e) => {
                        setViewSeccion(!ViewSeccion); setdataSet({
                          "requestId": "XYZ892022",
                          "cr": "",
                          "amount": 0,
                          "debtor": "",
                          "clientTypeId": 0,
                          "balance": 0,
                          "proposalTypeId": "",
                          "proposalTypeName": "",
                          "facilityTypeId": "",
                          "purpose": "",
                          "sublimits": "",
                          "proposalRate": 0,
                          "noSubsidyRate": 0,
                          "effectiveRate": 0,
                          "feci": true,
                          "termDays": 0,
                          "termDescription": "",
                          "ltv": 0,
                          "finantialConditions": "",
                          "environmentRiskCategory": 0,
                          "covenant": "",
                          "environmentRiskOpinion": "",
                          "finantialCovenant": "",
                          "legalDocumentation": "",
                          "creditRiskOpinion": "",
                          "provision": "",
                          "numero": "",
                          "otherConditions": "",
                        });
                      }} style={{ margin: '5px' }}>
                      {t("Cancel")}
                    </Link>
                    <Button className="btn btn-primary" type="submit" color="success" style={{ margin: '5px' }}>
                      {t("Save")}
                    </Button>
                  </Col>
                </Row>
              </LoadingOverlay>
            </CardBody>
            : null}
        </Card>
      </AvForm>
      {/* {selectedFacility && (
        <ModalFacilityDetails isOpen={displayModalFacilityDetails} toggle={toggleFacilityDetailsModal} facility={selectedFacility} />
      )} */}
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}

      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            setViewSeccion(!ViewSeccion);
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>

  );
});

ListaFacilidad.propTypes = {
  items: PropTypes.array.isRequired,
  editMode: PropTypes.bool,
  onSaveFacility: PropTypes.func
};

ListaFacilidad.defaultProps = {
  items: [],
  editMode: false
};

export default ListaFacilidad;
