import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useTranslation } from 'react-i18next'
import Select from "react-select";
import { useLocation, useHistory } from 'react-router-dom'
import LoadingOverlay from 'react-loading-overlay';

import {
  Row,
  Col,
  Card,
  CardBody,
  Button,
  Label,
  Table,
  Pagination,
  PaginationItem,
  PaginationLink,
  CardFooter,
} from "reactstrap"

import { AvForm, AvField } from "availity-reactstrap-validation"
import { Link } from "react-router-dom"

import { CoreServices, BackendServices } from '../../services';
import Currency from '../../helpers/currency';;

const AccountMovementsHistory = (props) => {
  const currencyData = new Currency();
  const { t, i18n } = useTranslation();

  const [dataRows, setDataRows] = useState([]);
  const [isActiveLoading, setIsActiveLoading] = useState(false);

  const [apiServiceBackend, setapiServiceBackend] = useState(new BackendServices());
  const [apiCoreServices, setCoreServices] = useState(new CoreServices());

  //On Mounting (componentDidMount)
  React.useEffect(() => {
    fetchData();
  }, []);

  function fetchData() {
    loadData();
  }

  //cargar lista de facilidades
  function loadData() {
    apiCoreServices.getAccountMovements(props.customerNumberT24)
      .then((data) => {
        console.log("getAccountMovements",data);
        if (data !== null && data !== undefined) {
          var rows = data.map((dataRow, index) => (
            dataRow.dataByYear.map((dataByYear, index2) => (
              <>              
              {dataByYear.dataresult.map((dataresult, index3) => (
              <tr key={index2 + index3}>
                <th>{index2 === 0 && index3 === 0?dataRow.AcctId:""}</th>
                <td>{dataByYear.year}</td>
                <td>{t(dataresult.month)}</td>
                <td>${currencyData.format((Number(dataresult?.amount ?? 0)).toFixed(2))}</td>
                {<td>${currencyData.format((Number(dataresult?.deposits ?? 0)).toFixed(2))}</td>}
              </tr>
              ))}
              <tr>
              <th></th>
              <th>{t("Total")}</th>
              <th></th>
              <th>${currencyData.format((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.amount),0)).toFixed(2))}</th>
              <th>${currencyData.format((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.deposits),0)).toFixed(2))}</th>
            </tr>
            <tr>
              <th></th>
              <th>{t("Average")}</th>
              <th></th>
              <th>${currencyData.format((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.amount),0)/dataByYear.dataresult.length).toFixed(2))}</th>
              <th>${currencyData.format((dataByYear.dataresult.reduce((total, currentValue) => total + Number(currentValue.deposits),0)/dataByYear.dataresult.length).toFixed(2))}</th>
            </tr>
            </>                          
            ))
            )
          );
          setDataRows(rows);
        }
      }).catch((error) => { });
  }

  return (

    <LoadingOverlay active={isActiveLoading} spinner text={t("WaitingPlease")}>

      <Card>

        <Row>
          <Col md="6">
            <h5 className="card-sub-title">{t("AccountMovementsHistory")}</h5>
          </Col>
        </Row>

        <CardBody>

          <Row>
            <Col md="12" className="table-responsive styled-table-div">
              <Table className="table table-striped table-hover styled-table table">
                <thead>
                  <tr>
                    <th>{t("Account")}</th>
                    <th>{t("Year")}</th>
                    <th>{t("Month")}</th>
                    <th>{t("AverageAmount")}</th>                    
                    <th>{t("Deposits")}</th>
                  </tr>
                </thead>
                <tbody>
                  {dataRows.length > 0 ? dataRows : null}
                </tbody>
              </Table>
            </Col>
          </Row>
        </CardBody>
      </Card>

    </LoadingOverlay>

  )
}

AccountMovementsHistory.propTypes = {
  customerNumberT24: PropTypes.string
}

//export default (withTranslation()(DatosGenerales));
export default AccountMovementsHistory;
