import React, { useState } from "react"
import { useTranslation, withTranslation } from "react-i18next"
import PropTypes from 'prop-types';
import { Link, useLocation } from "react-router-dom"
import {
  Row,
  Col,
  Button,
  Table,
} from "reactstrap"

import { AvForm, AvField, AvGroup } from "availity-reactstrap-validation"
import ModalCuentasCobrar from "./ModalCuentasCobrar.js"
import AttachmentFileCore from "../../../../../components/Common/AttachmentFileCore";
import * as OPTs from "../../../../../helpers/options_helper";
import { AttachmentFileInputModel } from '../../../../../models/Common/AttachmentFileInputModel';
import { BackendServices } from "../../../../../services/index.js";
import SweetAlert from "react-bootstrap-sweetalert";
const estiloTotales = { backgroundColor: "lightgrey", color: "black" }
const CuentasCobrar = React.forwardRef((props, ref) => {
  const { t, i18n } = useTranslation();
  const { locationData } = props;
  const location = useLocation()
  /* ---------------------------------------------------------------------------------------------- */
  /*                        Variables de estados para los mensajes de alerta                        */
  /* ---------------------------------------------------------------------------------------------- */
  const [successSave_dlg, setsuccessSave_dlg] = useState(false);
  const [error_dlg, seterror_dlg] = useState(false);
  const [error_msg, seterror_msg] = useState("");
  const [confirm_alert, setconfirm_alert] = useState(false)
  const [dataDelete, setDataDelete] = useState([]);
  const [dataLocation, setData] = useState(location.data);
  const [botonValidation, setbotonValidation] = useState(true);
  const [formValid, setFormValid] = useState(false);
  const [tipo, settipo] = useState("")
  const [dataCuentas, setdataCuentas] = useState({
    "transactId": 0,
    "countryCustomer": "",
    "days30": 0,
    "days60": 0,
    "days90": 0,
    "days120": 0,
    "days150": 0,
    "days180": 0,
    "days210": 0,
    "days240": 0,
    "days270": 0,
    "days300": 0,
    "days330": 0,
    "days331": 0,
    "total": 0,
    "percentage": 0
  })
  const [dataCuentasCobrarRows, setdataCuentasCobrarRows] = useState(null);
  React.useImperativeHandle(ref, () => ({
    validateForm: () => {
      const form = document.getElementById('frmProveedores');
      form.requestSubmit();
    },
    getFormValidation: () => {
      return formValid;
    }
  }));
  const [showModalCuentasCobrar, setShowModalCuentasCobrar] = useState(false);
  React.useEffect(() => {
    // Read Api Service databhhh
    initializeData();
  }, []);
  function initializeData() {
    const api = new BackendServices();
     // consultarCuentasCobrarIGR
    api.consultAccountsReceivableIGR(dataLocation.transactionId).then(resp => {
      if (resp != undefined) {
        setdataCuentasCobrarRows(resp.map((data) => (
          <tr key={data.id}>
            <td>{data.countryCustomer}</td>
            <td>{data.days30}</td>
            <td>{data.days60}</td>
            <td>{data.days90}</td>
            <td>{data.days120}</td>
            <td>{data.days150}</td>
            <td>{data.days180}</td>
            <td>{data.days210}</td>
            <td>{data.days240}</td>
            <td>{data.days270}</td>
            <td>{data.days300}</td>
            <td>{data.days330}</td>
            <td>{data.days331}</td>
            <td>{data.total}</td>
            <td>{data.percentage}</td>
            <td style={{ textAlign: "right" }}>
            <Button type="button" color="link" onClick={(resp) => { updateDatosCxc(data) }} className="btn btn-link" ><i className="mdi mdi-border-color mdi-24px"></i></Button>
            <Button type="button" color="link" onClick={(resp) => { deleteDataCxc(data) }} className="btn btn-link" ><i className="mdi mdi-trash-can-outline mdi-24px"></i></Button>
            </td>
          </tr>)));
      } else {
        setdataCuentasCobrarRows(
          <tr key={1}>
            <td colSpan="14" style={{ textAlign: 'center' }}></td>
          </tr>);
      }
    });
  }


  //abrimos modal para adjunar archivos
  function toggleShowModalCuentasCobrar() {
    setShowModalCuentasCobrar(!showModalCuentasCobrar);
    removeBodyCss()
  }
  function dataManagament(data, tipo) {
    const api = new BackendServices()
    data.transactId = Number(dataLocation.transactionId);
    data.days30 = Number(data.days30);
    data.days60 = Number(data.days60);
    data.days90 = Number(data.days90);
    data.days120 = Number(data.days120);
    data.days150 = Number(data.days150);
    data.days180 = Number(data.days180);
    data.days210 = Number(data.days210);
    data.days240 = Number(data.days240);
    data.days270 = Number(data.days270);
    data.days300 = Number(data.days300);
    data.days330 = Number(data.days330);
    data.days331 = Number(data.days331);
    data.total = Number(data.total);
    data.percentage = Number(data.percentage);
    if (tipo == "guardar") {
      api.newAccountsReceivableIGR(data).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalCuentasCobrar();
        } else {
          toggleShowModalCuentasCobrar();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    } else {
      api.updateAccountsReceivableIGR(data).then(resp => {
        if (resp !== null && resp !== undefined) {
          setsuccessSave_dlg(true)
          toggleShowModalCuentasCobrar();
        } else {
          toggleShowModalCuentasCobrar();
          seterror_dlg(true);
        }
      }).catch(err => {
        seterror_dlg(true);
      })
    }
  }
  function verDatosCxc(data) {
    setdataCuentas(data)
    settipo("ver")
    setbotonValidation(false);
    toggleShowModalCuentasCobrar()
  }
  // Actualiza datos de cxc
  function updateDatosCxc(data) {
    setdataCuentas(data)
    settipo("editar")
    setbotonValidation(true);
    toggleShowModalCuentasCobrar()
  }
  // elimina los datos de la cxc
  function deleteDataCxc(data) {
    setDataDelete(data)
    setconfirm_alert(true);
  }
  function removeBodyCss() {
    document.body.classList.add("no_padding")
  }
  // Form Submission
  function handleSubmit(event, errors, values) {
    event.preventDefault();
    if (errors.length > 0) {
      return;
    }
  }
  return (
    <React.Fragment>
      <h5>
        {t("AccountsReceivable")}
      </h5>
      <p className="card-title-desc"></p>
      <AvForm id="frmSearch" className="needs-validation" onSubmit={handleSubmit}>
        <Row>
          <Col md="12">
            <Table className="table mt-1" responsive>
              <thead className="table-light">
                <tr>
                  <th>{t("CountryClient")}</th>
                  <th>30 {t("Days")}</th>
                  <th>60 {t("Days")}</th>
                  <th>90 {t("Days")}</th>
                  <th>120 {t("Days")}</th>
                  <th>150 {t("Days")}</th>
                  <th>180 {t("Days")}</th>
                  <th>210 {t("Days")}</th>
                  <th>240 {t("Days")}</th>
                  <th>270 {t("Days")}</th>
                  <th>300 {t("Days")}</th>
                  <th>330 {t("Days")}</th>
                  <th>+331 {t("Days")}</th>
                  <th>{t("Total")}</th>
                  <th>{t("Percent")}</th>
                  <th><Link className="float-end" onClick={() => {
                    settipo("guardar"); setdataCuentas({
                      "transactId": 0,
                      "countryCustomer": "",
                      "days30": 0,
                      "days60": 0,
                      "days90": 0,
                      "days120": 0,
                      "days150": 0,
                      "days180": 0,
                      "days210": 0,
                      "days240": 0,
                      "days270": 0,
                      "days300": 0,
                      "days330": 0,
                      "days331": 0,
                      "total": 0,
                      "percentage": 0
                    }); toggleShowModalCuentasCobrar()
                  }}><i className="mdi mdi-plus-box-multiple-outline mdi-24px"></i></Link></th>
                </tr>
              </thead>
              <tbody>
                {dataCuentasCobrarRows}
              </tbody>
            </Table>

          </Col>
        </Row>
        {locationData?
        <AttachmentFileCore attachmentFileInputModel={ new AttachmentFileInputModel(locationData.transactionId,OPTs.PROCESS_INFORMEGESTION,OPTs.ACT_CUENTASPORCOBRAR)} />
        :null}
        
      </AvForm>
      <ModalCuentasCobrar dataManagament={dataManagament} botones={botonValidation} tipo={tipo} dataCuentas={dataCuentas} isOpen={showModalCuentasCobrar} toggle={() => { toggleShowModalCuentasCobrar() }} />
      {successSave_dlg ? (
        <SweetAlert
          success
          title={t("SuccessDialog")}
          onConfirm={() => {
            setsuccessSave_dlg(false)
            initializeData();
          }}
        >
          {t("SuccessSaveMessage")}
        </SweetAlert>
      ) : null}
      {error_dlg ? (
        <SweetAlert
          error
          title={t("ErrorDialog")}
          onConfirm={() => {
            seterror_dlg(false)
            initializeData();
          }}
        >
          {error_msg}
        </SweetAlert>
      ) : null}
      {confirm_alert ? (
        <SweetAlert
          title={t("Areyousure")}
          warning
          showCancel
          confirmButtonText={t("Yesdeleteit")}
          confirmBtnBsStyle="success"
          cancelBtnBsStyle="danger"
          onConfirm={() => {
            const apiBack = new BackendServices();
            // eliminarCuentasCobrarIGR
            apiBack.deleteAccountsReceivableIGR({ transactId: dataLocation.transactionId, accountReceivableId: dataDelete.idCollectionAccount }).then(resp => {
              if (resp.statusCode == "500") {
                setconfirm_alert(false)
                seterror_dlg(true)
              } else {
                setconfirm_alert(false)
                setsuccessSave_dlg(true)
              }
            }).catch(error => {
              setconfirm_alert(false)
              seterror_dlg(true)
            })
          }}
          onCancel={() => setconfirm_alert(false)}
        >
          {t("Youwontbeabletorevertthis")}
        </SweetAlert>
      ) : null}
    </React.Fragment>
  );
});

CuentasCobrar.propTypes = {
  locationData: PropTypes.any,
}
export default CuentasCobrar;
