import PropTypes from 'prop-types';
import { formatCurrency, translationHelpers } from '../../helpers';

import {
  Table,
  Card,
  CardBody,
} from "reactstrap"
import React, { useEffect, useState } from "react"
import { BackendServices, CoreServices, BpmServices, } from "../../services";
import { useTranslation } from 'react-i18next';

const CorporateExposureClient = (props) => {
  const coreServices = new CoreServices();

  const [t, tr] = translationHelpers('commercia;l_credit', 'translation')
  const [dataRows, setdataRows] = React.useState(null);
  const [CorporateExposure, setCorporateExposure] = useState(null);

  const customerNumberT24 = props.prospect?.customerNumberT24;
  // const customerNumberT24 = "600133774";

  // React.useEffect(() => {
  //   setdataRows(props.items.map((item, index) => (
  //     <tr key={'row-' + index}>
  //       <th scope="row" width="30%">{item.description}</th>
  //       <td className="text-end">{formatCurrency('$', item.approved, 0)}</td>
  //       <td className="text-end">{formatCurrency('$', item.current, 0)}</td>
  //       <td className="text-end">{formatCurrency('$', item.proposed, 0)}</td>
  //       <td className="text-end">{item.difference === 0 ? '-' : formatCurrency('$', item.difference, 0)}</td>
  //       <td className="text-end">{item.ltv + '%'}</td>
  //     </tr>)
  //   ));
  // }, [props]);

  useEffect(() => {
    getCoporateExhibitionClient();
  }, []);

  function getCoporateExhibitionClient() {
    coreServices.getCorporateExhibitionByClients(customerNumberT24).then(resp => {
      console.log(resp);
      resp = [
        {
          clinteId: "Julio Barreto",
          data: [
            {
              name: "Facilidades Largo Plazo0001", approvalRisk: 0, proposalRisk: 0, variationRisk: 0
            },
            {
              name: "Facilidades Largo Plazo0002", approvalRisk: 100, proposalRisk: 1, variationRisk: 144444
            }
          ]
        },
        {
          clinteId: "Julio Barreto",
          data: [
            {
              name: "Facilidades Largo Plazo0001", approvalRisk: 0, proposalRisk: 0, variationRisk: 0
            },
            {
              name: "Facilidades Largo Plazo0002", approvalRisk: 11111, proposalRisk: 12222, variationRisk: 133333
            }
          ]
        }
      ];
      setCorporateExposure(resp);
      setdataRows(resp.map((item, index) => (
        <>
          <tr key={'row-' + index}>
            <th scope="row" width="30%">{item.clinteId}</th>
            <th scope="row" className="text-end"></th>
            <th scope="row" className="text-end"></th>
            <th scope="row" className="text-end"></th>
          </tr>
          {item.data.map((item2, index2) => (
            <tr key={'subrow-' + index2}>
              <th scope="row" width="30%"></th>
              <th scope="row" className="text-start">{formatCurrency('$', item2.approvalRisk, 0)}</th>
              <th scope="row" className="text-start">{formatCurrency('$', item2.variationRisk, 0)}</th>
              <th scope="row" className="text-start">{formatCurrency('$', item2.proposalRisk, 0)}</th>
            </tr>
          ))}
        </>
      )
      ));
    });
  }

  function getTotal(item) {
    return formatCurrency('$', CorporateExposure?.reduce((acu, crr) => {
      let data1 = (acu.data?.reduce((acu2, crr2) => ((acu2[item] ?? acu2) + crr2[item])) ?? acu);
      let data2 = crr.data?.reduce((acu2, crr2) => ((acu2[item] ?? acu2) + crr2[item]));
      return (data1 + data2)
    }), 0)
  }

  return (
    <React.Fragment>
      <Card>
        <CardBody>
          <h5>{props.title}</h5>
          <p className="card-title-desc">
          </p>
          <div className="table-responsive">
            <Table className="table mb-0">
              <thead className="table-light">
                <tr>
                  <th></th>
                  <th className="table-right">{tr("ApprovedRisk")}</th>
                  <th className="table-right">{tr("ProposedRisk")}</th>
                  <th className="table-right">{tr("Variation")}</th>
                </tr>
              </thead>
              <tbody>
                {dataRows}
                <tr>
                  <th scope="row" width="30%">{tr("Total")}</th>
                  <th scope="row" className="table-right">{getTotal('approvalRisk')}</th>
                  <th scope="row" className="table-right">{getTotal('variationRisk')}</th>
                  <th scope="row" className="table-right">{getTotal('proposalRisk')}</th>
                </tr>
              </tbody>
            </Table>
          </div>
        </CardBody>
      </Card>
    </React.Fragment>
  );

};

CorporateExposureClient.propTypes = {
  title: PropTypes.string,
  items: PropTypes.array.isRequired
};


export default CorporateExposureClient;
